vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/vec2(iResolution.y, iResolution.y);
    vec2 m = iMouse.xy/vec2(iResolution.y, iResolution.y);
    vec2 v = st - m;
    v = v / 0.01;
    float tau = 6.283185;
    float hue = atan(v.x, v.y)/tau;
    hue = hue + length(v)/80. - iTime/2.;
    vec3 rgb = hsl2rgb ( vec3(hue, 1., 
                              cos(2.0-length(v)*3.5)));
    fragColor = vec4(rgb.r,rgb.g,rgb.b,1.0);
}

//Try changing these parameters to get more crazy patterns!
#define s1 2.
#define scale 2.1
#define scale1 1.14

#define ITERS 12
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=0.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/20.0;
    uv.y += (iTime)/25.0;
    for(int c=0;c<ITERS;c++){
        col_prev = col;

        for(int i=0;i<ITERS;i++)
        {
            uv = fract(uv.yx);
            uv= (uv+((vec2(uv.x/scale-uv.y/scale1,uv.y/scale-uv.x/scale1)/(scale))))/scale1;
            uv.x *= scale1/s1;

            uv.y /= -scale1/s1;

        }
        col[2] = abs((uv.y)-(uv.x));
        col = ((col+col_prev.yzx))/2.2;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

// Meta CRT - Single Pass - @P_Malin
// A single pass version of Meta CRT : https://www.shadertoy.com/view/4dlyWX

// This can be used as a webcam fiter in Memix
// See github here : https://github.com/pmalin/pmalin-memix-shaders

