#define AA  1   // (= 0/1) optional antialiasing

float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrConCylDf (vec3 p, vec2 cs, float r, float h);
float PrAnConCylDf (vec3 p, vec2 cs, float r, float w, float h);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float BumpFbm3 (vec3 p);
float Hashfv3 (vec3 p);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, qHit;
vec2 rollCs;
float tCur, dstFar, szFac, flRad, flLen;
int idObj;
bool invFly;
const int idFus = 1, idWng = 2, idEng = 3;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, w, s, dy;
  dMin = dstFar / szFac;
  p /= szFac;
  p.xy = Rot2Cs (p.xy, rollCs);
  q = p;
  w = 1.2;
  dy = 0.;
  if (q.z < -1.5) {
    s = (q.z + 1.5) * (q.z + 1.5);
    w *= 1. - 0.025 * s;
  } else if (q.z > 2.) {
    s = (q.z - 2.) * (q.z - 2.);
    w *= 1. - 0.025 * s;
    dy = -0.01 * s;
  }
  d = max (PrCapsDf (q - vec3 (0., dy, 0.), w, 7.5), - q.z - 6.);
  DMINQ (idFus);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (1.7, -4.2);
  d = max (max (- q.z - 1.6, dot (vec2 (q.y, dot (q.xz, sin (0.15 * pi + vec2 (0., 0.5 * pi)))),
     sin (0.1 * pi + vec2 (0., 0.5 * pi))) - 1.6), q.y - 0.6);
  d = max (d, - min (abs (PrBox2Df (q.xz - vec2 (2.6, -1.5), vec2 (1., 0.2))),
     abs (PrBox2Df (q.yz - vec2 (-0.2, -1.5), vec2 (0.5, 0.2)))));
  d = SmoothMax (d, abs (PrRoundBox2Df (q.xy, vec2 (4., 1.), 0.7)) - 0.04, 0.05);
  DMINQ (idWng);
  q = p;
  q.z += 6.05;
  d = PrCylDf (q, 0.4, 0.15);
  q.z += 0.5;
  d = min (d, PrAnConCylDf (q, sin (0.07 * pi + vec2 (0.5 * pi, 0.)), 0.45, 0.03, 0.35));
  DMINQ (idEng);
  return 0.5 * szFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 240; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec2 CapsHit (vec3 ro, vec3 rd, float cRad, float cLen)
{
  float dIn, dOut, d, a, b, bs, w, s, ws, roz;
  dIn = dstFar;
  dOut = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - cRad * cRad);
  if (w > 0. && a > 0.) {
    ws = sqrt (w);
    d = - (b + ws) / a;
    s = ro.z + d * rd.z;
    if (d > 0. && abs (s) < cLen) dIn = d;
    else {
      roz = ro.z;
      ro.z -= cLen * sign (s);
      bs = dot (rd, ro);
      w = bs * bs - (dot (ro, ro) - cRad * cRad);
      if (w > 0.) {
        d = - bs - sqrt (w);
        if (d > 0.) dIn = d;
      }
      ro.z = roz;
    }
  }
  if (dIn < dstFar) {
    d = (- b + ws) / a;
    s = ro.z + d * rd.z;
    if (abs (s) < cLen) dOut = d;
    else {
      ro.z -= cLen * sign (s);
      bs = dot (rd, ro);
      w = bs * bs - (dot (ro, ro) - cRad * cRad);
      if (w > 0.) dOut = - bs + sqrt (w);
    }
  }
  return vec2 (dIn, dOut);
}

float FlmDens (vec3 p)
{ // (from "Flaming Asteroids")
  float c;
  c = max (0., 1.3 * BumpFbm3 (0.2 * vec3 (p.xy / flRad, 0.5 * p.z + 16. * tCur)) - 0.2 +
     0.4 * smoothstep (-0.5 * flRad, 0.1 * flRad, p.z));
  return c * (1. - smoothstep (-0.3 * flRad, 0., length (vec3 (p.xy,
     p.z - clamp (p.z, - 2. * flLen, 0.))) - flRad * (1. + 0.8 * p.z / (2. * flLen)))) *
     smoothstep (0., flRad, p.z + 2. * flLen);
}

vec4 FlmCol (vec3 ro, vec3 rd, vec2 dCyl2, float dstObj)
{
  vec4 col4;
  vec3 q, c;
  float s, ds, f, fn, r;
  const float sMax = 50.;
  ds = min (2. * flRad, dCyl2.y - dCyl2.x) / sMax;
  s = dCyl2.x;
  ro.z -= flLen;
  q = ro + s * rd;
  r = length (q.xy) / flRad;
  q.xy = Rot2D (q.xy, 0.8 * r * (2. * smoothstep (0.4, 0.8, r) - 1.) * q.z);
  fn = FlmDens (q);
  col4 = vec4 (0.);
  for (float j = float (VAR_ZERO); j < sMax; j ++) {
    f = fn;
    s += ds;
    q = ro + (s + ds * Hashfv3 (16. * rd)) * rd;
    r = length (q.xy) / flRad;
    q.xy = Rot2D (q.xy, 0.8 * r * (2. * smoothstep (0.4, 0.8, r) - 1.) * q.z);
    fn = FlmDens (q);
    c = mix (mix (vec3 (1., 0.3, 0.1), vec3 (1., 1., 0.2), clamp ((0.3 / ds) * (f - fn), 0., 1.)),
       vec3 (0.9, 0.8, 0.3), smoothstep (-0.5 * flRad, 0.2 * flRad, q.z)) *
       (1. - smoothstep (-0.1, 0.1, s - dstObj));
    col4 = col4 + (1. - col4.a) * vec4 (c, 1.) * f / sMax;
    if (s > dstObj || col4.a > 1.) break;
  }
  return clamp (col4, 0., 1.);
}

vec4 SphHit (vec3 ro, vec3 rd, float rad)
{
  vec3 vn;
  float b, d, w;
  b = dot (rd, ro);
  w = b * b + rad * rad - dot (ro, ro);
  d = dstFar;
  if (w > 0.) {
    d = - b - sqrt (w);
    vn = (ro + d * rd) / rad;
  }
  return vec4 (d, vn);
}

vec3 StarPat (vec3 rd, float scl) 
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 8. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, dSph4, c;
  vec3 col, vn, vnh, roo, rdo, q, stCol;
  vec2 dCyl2;
  float dstObj, dstGrnd, dstCld, s, spRad, h, clDens;
  bool doRefl;
  szFac = 0.5;
  roo = ro;
  rdo = rd;
  doRefl = false;
  dstGrnd = dstFar;
  col = vec3 (0.);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = vec4 (0.84, 0.84, 0.85, 0.3);
    if (idObj == idFus) {
      q = qHit;
      q.z -= 1.;
      s = 0.;
      if (abs (q.z) < 3.6) {
        q *= 0.6;
        q.z = fract (q.z) - 0.5;
        s = max (step (length (q.yz + vec2 (-0.2, 0.)), 0.22), step (0., q.y) *
           step (length (q.xz), 0.22));
      }
      s = max (s, step (SmoothMax (PrRoundBox2Df (vec2 (abs (qHit.x) - 0.55, qHit.z) - vec2 (0., 5.6),
         vec2 (0.3, 0.6), 0.1), - qHit.y + 0.2, 0.1), 0.));
      if (s > 0.) {
        doRefl = true;
        col4 = vec4 (0.);
      }
      if (qHit.z < -5.9) col4 *= 0.5 + 0.5 * smoothstep (0.4, 0.45,
         mod (16. * (atan (qHit.y, qHit.x) / (2. * pi) + 0.5), 1.));
      if (qHit.y < 0.) {
        col4 *= 0.7 + 0.3 * smoothstep (0., 0.02,
           min (abs (PrBox2Df (vec2 (abs (qHit.x), qHit.z) - vec2 (0.35, -0.5), vec2 (0.35, 1.5))),
           abs (PrBox2Df (vec2 (abs (qHit.x), qHit.z) - vec2 (0.15, 4.5), vec2 (0.15, 0.4)))) - 0.01);
      }
      col4.rgb = mix (col4.rgb, vec3 (0.3, 0.8, 0.8), smoothstep (0., 0.02, qHit.z - 7.5));
    } else if (idObj == idWng) {
      col4.rgb = mix (vec3 (0.3, 0.8, 0.8), col4.rgb, smoothstep (0., 0.02,
         abs (length (qHit.xz - vec2 (2.5, -0.2)) - 0.5) - 0.1));
    } else if (idObj == idEng) {
      col4 = (PrConCylDf (qHit, sin (0.07 * pi + vec2 (0.5 * pi, 0.)), 0.45, 0.35) > 0.) ?
         vec4 (0.7, 0.3, 0.3, 0.) * (0.8 + 0.2 * sin (16. * pi * qHit.z)) : vec4 (1., 0., 0., -1.);
    }
    col = col4.rgb;
    if (col4.a >= 0.) col = col * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
       col4.a * vec3 (1., 1., 0.9) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 64.);
    else col *= 0.5 + 0.5 * max (- dot (rd, vn), 0.);
  }
  if (doRefl) {
    rd = reflect (rd, vn);
    col = vec3 (0.);
    dstObj = dstFar;
  }
  if (dstObj >= dstFar) {
    ro = roo;
    spRad = 200.;
    dSph4 = SphHit (ro - vec3 (0., - spRad - 5., 0.), rd, spRad);
    dstGrnd = dSph4.x;
    if (dstGrnd < dstFar && dstGrnd > 0.) {
      ro += dstGrnd * rd;
      vnh = dSph4.yzw;
      ro.xz += vec2 (0., 0.5 * tCur);
      h = Fbm2 (vec2 (0.23, 0.2) * ro.xz);
      vn = VaryNf ((4. - 2. * step (0.45, h)) * ro, vnh, (0.1 + 2.9 * max (h - 0.45, 0.)) *
         smoothstep (0.03, 0.1, - dot (vnh, rd)));
      s = Noisefv2 (32. * ro.xz);
      col4 = vec4 (0., 0.2, 1., 0.3) * (1. - 0.2 * s);
      col4 = mix (col4, vec4 (0.8, 0.5, 0.2, 0.1) * (1. - 0.1 * s), step (0.45, h));
      col4 = mix (col4, vec4 (0.4, 0.8, 0.4, 0.1) * (1. - 0.1 * s), smoothstep (0.46, 0.54, h));
      col4 = mix (col4, vec4 (0.5, 0.7, 0.5, 0.1) * (1. - 0.2 * s), smoothstep (0.6, 0.7, h));
      col4 = mix (col4, vec4 (1., 1., 1., 0.3) * (1. - 0.2 * s), smoothstep (0.75, 0.8, h));
      col4.rgb = mix (col4.rgb, vec3 (1., 1., 0.8), 0.2);
      col4 = mix (vec4 (0.6, 0.6, 0.9, 0.), col4, smoothstep (0.03, 0.1, - dot (vnh, rd)));
      col4 = mix (vec4 (0.05, 0.05, 0.05, 0.), col4, smoothstep (0., 0.03, - dot (vnh, rd)));
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
         col4.a * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 8.);
      ro.xz += 0.1 * atan (sunDir.xz, sunDir.yy);
      clDens = clamp (1.6 * Fbm2 (vec2 (0.4, 0.7) * ro.xz) - 0.4, 0., 1.);
      col *= 1. - 0.3 * clDens;
    }
    ro = roo;
    spRad = 250.;
    dSph4 = SphHit (ro - vec3 (0., - spRad - 5., 0.), rd, spRad);
    dstCld = dSph4.x;
    if (dstCld < dstFar && dstCld > 0.) {
      ro += dstCld * rd;
      vnh = dSph4.yzw;
      ro.xz += vec2 (0., 0.5 * tCur);
      clDens = clamp (1.6 * Fbm2 (vec2 (0.4, 0.7) * ro.xz) - 0.4, 0., 1.);
      col = mix (col, vec3 (1.) * (0.2 + 0.8 * max (dot (vnh, sunDir), 0.)), clDens *
         smoothstep (0.11, 0.14, - dot (vnh, rd)));
    }
  }
  ro = roo;
  rd = rdo;
  if (! doRefl) {
    flRad = 0.4;
    flLen = 20.;
    ro.z += flLen + 7.4 * szFac;
    dCyl2 = CapsHit (ro, rd, flRad, flLen);
    if (dCyl2.x < min (min (dstObj, dstGrnd), dstFar)) {
      c = FlmCol (ro, rd, dCyl2, dstObj);
      col = mix (col, 3. * c.rgb, clamp (8. * c.a * c.a, 0., 1.));
    }
  }
  stCol = StarPat (rd, 16.);
  if (length (col) < 0.03) col = mix (col, stCol, step (Maxv3 (col), 0.4 * Maxv3 (stCol)));
  if (doRefl && length (col) < 0.03) col += vec3 (0.15, 0.1, 0.);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  invFly = (mod (0.025 * tCur, 1.) > 0.5);
  if (mPtr.z > 0. && mPtr.x > 0.4 && mPtr.y < -0.4) invFly = ! invFly;
  rollCs = sin (0.1 * pi * sin (0.1 * pi * tCur) + (invFly ? pi : 0.) + vec2 (0.5 * pi, 0.));
  if (invFly) uv *= -1.;
  az = pi;
  el = -0.1 * pi;
  if (mPtr.z > 0. && ! (mPtr.x > 0.4 && mPtr.y < -0.4)) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.9 * pi * sin (0.01 * pi * tCur);
    el -= 0.05 * pi * sin (0.012 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, 0.01 * pi);
  az = clamp (az, 0.1 * pi, 1.9 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -15.);
  zmFac = 4.;
  dstFar = 300.;
  sunDir = normalize (vec3 (1., 1., -1.));
  if (invFly) sunDir.xz *= -1.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrConCylDf (vec3 p, vec2 cs, float r, float h)
{
  return max (dot (vec2 (length (p.xy) - r, p.z), cs), abs (p.z) - h);
}

float PrAnConCylDf (vec3 p, vec2 cs, float r, float w, float h)
{
  return max (abs (dot (vec2 (length (p.xy) - r, p.z), cs)) - w, abs (p.z) - h);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float PerBumpf (float p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

vec3 PerBumpv3 (vec3 p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

float BumpFbm3 (vec3 p)
{
  vec3 q;
  float a, f;
  a = 1.;
  f = 0.;
  q = p;
  for (int j = 0; j < 4; j ++) {
    p += PerBumpv3 (q + PerBumpv3 (q).yzx);
    p *= 1.5;
    f += a * (PerBumpf (p.z + PerBumpf (p.x + PerBumpf (p.y))));
    q = 2. * q + 1.;
    a *= 0.75;
  }
  return f;
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

