// I've not seen anybody out there computing correct cell interior distances for Voronoi
// patterns yet. That's why they cannot shade the cell interior correctly, and why you've
// never seen cell boundaries rendered correctly. 

// However, here's how you do mathematically correct distances (note the equidistant and non
// degenerated grey isolines inside the cells) and hence edges (in yellow):

// https://iquilezles.org/articles/voronoilines


vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 hash2( vec2 p )
{
    // procedural white noise	
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec2 animatedO( in vec2 n, in vec2 g)
{
    vec2 o = hash2( n + g );
    o = 0.5 + 0.5*sin( iTime/10. + 6.2831 * o );
    return o;
}


vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mr;
    vec2 mo;
    float md = 8.;

    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = animatedO(n, g);

        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mo = o;
        }

    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------

    md = 10.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
        vec2 o = animatedO(n, g);

        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        {
            md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
        }
    }

    //return vec3( md, mr );
    return vec3( md, mo );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xx;
    vec3 c = voronoi( 8.0*p );

    vec3 col = texture( iChannel0, p + c.yz / 8. ).rgb;

    // highlights
    col += c.x*vec3(0.8);

    // colorize some cells
    vec3 cell_color = 0.5 + 0.5 * cos( vec3(c.y, 0,c.z) * 12. );	
    float dd = length( c.yz );
	col = mix( cell_color, col, smoothstep( 0.2, 0.4, dd + 0.1) );

    // borders	
    col = mix( vec3(0.), col, smoothstep( 0.04, 0.07, c.x ) );

    // color adjust
    col = rgb2hsv(col);
    col.y *= 1.5;
    col.z *= 1.25;
    col = hsv2rgb(col);


	fragColor = vec4(col,1.0);
}

#define ZOOM_ITER 40
#define ZOOM_CENTER vec2(0.5,0.5)
#define ZOOM_AMP 0.02
#define ZOOM_OUTSIDE_AMP 0.15
#define ZOOM_OUTSIDE_OFFSET 0.2
#define CHROMA_MIX 0.2
#define HUGE 9E16
#define PI 3.14159265
#define saturate(i) clamp(i,0.,1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xy;
  float len = length( uv - ZOOM_CENTER );

  vec3 tex = vec3( 0.0 );

  for ( int i = 0; i < ZOOM_ITER; i ++ ) {
    float fi = ( float( i ) + 0.5 ) / float( ZOOM_ITER );
    vec3 blurA = saturate( vec3(
      1.0 - 4.0 * abs( 1.0 / 4.0 - fi ),
      1.0 - 4.0 * abs( 2.0 / 4.0 - fi ),
      1.0 - 4.0 * abs( 3.0 / 4.0 - fi )
    ) ) * 4.0;
    vec3 blurB = vec3(
        1.0 - 2.0 * abs( 1.0 / 2.0 - fi )
    ) * 2.0;
    vec3 blur = mix( blurA, blurB, CHROMA_MIX ) / float( ZOOM_ITER );
    float scaleAmp = ( ZOOM_OUTSIDE_AMP * len + ZOOM_AMP ) * fi + ZOOM_OUTSIDE_OFFSET * len;
    vec2 uvt = ( 1.0 - scaleAmp ) * ( uv - ZOOM_CENTER ) + ZOOM_CENTER;
    tex += blur * texture( iChannel0, uvt ).xyz;
  }

  // fragColor = vec4( tex, 1.0 );
  fragColor = texture(iChannel0, uv);
}

