#define undo(uv)	(((uv * iResolution.y) + 0.5 * iResolution.xy) / iResolution.xy)

//Change this to see the projected screens that create the fractal;
#define viewScreens	false

//iq palette
vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) { return a + b * cos(6.28318 * (c * t + d)); }

vec3 texCam(vec2 uv, vec2 p, float rot, float scale) {
	uv -= p;
	uv *= rot(rot);
	uv /= scale;
	uv = undo(uv);
	float m = 1.0;
	if (viewScreens) m = 0.0;
	return mix(texture(iChannel1, uv).rgb, texture(iChannel0, uv).rgb, m) * step(uv.x, 1.0) * step(uv.y, 1.0) * step(0.0, uv.x) * step(0.0, uv.y);
}

