	col += texCam(uv, vec2(0.0 + sin(t * 0.6 + 3.0) * 0.4, 0.0 + sin(t * 0.8 + 6.0) * 0.2), 0.3 + t, 0.3);

	//Something on screen to get it going
	vec3 ballcol = pal(floor(t * 0.5) * 0.25, vec3(0.5), vec3(0.5), vec3(1), vec3(0, 0.33, 0.66));
	vec2 ballpos = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;
	if (iMouse.z > 0.0) col += ballcol * step(length(vec2(uv - ballpos)), 0.1);
	else col += ballcol * step(length(vec2(uv.x + sin(t) * 0.2, uv.y + cos(t) * 0.2)), 0.1);

	//Reduce the color to prevent it from getting too bright
	col *= 0.85;
	col = clamp(col, 0.0, 1.0);
	fragColor = vec4(col, 1.0);
}

