float PI = radians(180.);

float makeCircle(vec3 p, vec3 rd, float r){
    /**
    Find inside of circle based on position vector and ray direction
    Remember each point on screen gets its own ray direction
    We use the normal vector of the point to control its brightness on screen.
    **/ 
    float n = length(cross(p, rd));
    n = smoothstep(r-0.02, r, n);
    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv = fract(5.*uv);
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);


    // Camera Source - Ray Source
    vec3 ro = vec3(0., 0., -1.);

    // Ray Direction
    vec3 rd = vec3(uv.x, uv.y, 0);
    rd -= ro; // Vector
    rd /= length(rd); // Normalize

    int numC = 5;

    vec3 p = vec3(0.);
    float n = 0.;
    vec3 cCol = vec3(1., 0., 0.);

    vec3 sceneCenter = vec3(0., 0., 5.);
    sceneCenter -= ro;

    p = sceneCenter;
    //p -= ro; // position vector with respect to camera
    cCol = vec3(1., 1., 0.);
    float nScene = makeCircle(p, rd, 0.5);
    n = nScene;
    col = mix(cCol, col, n);
    float theta = PI/4.;
    mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));

    for(int i=0; i < numC; i++){
        float id = smoothstep(0., float(numC), float(i+1));
        p = vec3(cos(id*1.*iTime), 0.0*(id), 5.*sin(id*1.*iTime));
        // Define a new plane of rotation around the sun for the planet
        theta = (id-.5)*PI/4.;
        rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
        p.xy *= rot;
        p += sceneCenter;
        //p *= 3.;
        //p -= ro; // position vector with respect to camera
        cCol = vec3(id, 1.-id, id*id);
        //cCol = vec3((id+0.5));
        n = makeCircle(p, rd, 0.2*(0.5+id));
        //n = min(nScene, n);;
        //if(n>0. && nScene > 0. && dot(p - sceneCenter, rd) > 0.){
        //    n = nScene;
