        p.z-=15.;
        p=R(p,normalize(vec3(3,2,1)),iTime*.5);        
        e=s=3.;
        for(int j=0;j++<10;)
            p.xz=abs(p.zx),
            e=min(
                e,
                (length(p.zx+length(p)-3.)-.3)/s
            ),
            q=R(normalize(vec3(1,2,3)),vec3(.577),iTime*.7),
            p=R(p,q,iTime*.3),
            p-=vec3(1,1.2,1.+sin(iTime)*.5),
            p*=1.2,
            s*=1.2;
        g+=e;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1);
    vec2 uv = fragCoord / iResolution.yy * 10.0;


    vec2 rayOrigin = vec2(6.2, 7.6);
    vec2 mousePos = iMouse.xy / iResolution.yy * 10.0;
    vec2 rayDirection = normalize(mousePos - rayOrigin);

    ivec2 blockPos = ivec2(uv);

    vec2 pos = rayOrigin;
    for (int i = 0; i < 50; i++) {
        ivec2 currentBlock = ivec2(floor(pos));
        // If we are inside the block the pixel is in, draw grey
        if (blockPos.x == currentBlock.x && blockPos.y == currentBlock.y) {
            fragColor.rgb = vec3(0.5);
            break;
        }

        vec2 fPos = fract(pos);
        vec2 distances = ceil(sign(rayDirection) * 0.5 + 0.5) - fPos;
        vec2 steps = abs(distances / rayDirection);
        float closestStep = min(steps.x, steps.y);
        pos += rayDirection * (closestStep + 0.001);
    }

    // Drawing grid lines
    fragColor.rgb = mix(vec3(0, 0, 0), fragColor.rgb, step(0.05, fract(uv.x)) * step(0.05, fract(uv.y)));
    // Drawing ray
    vec2 normal = rayDirection.yx * vec2(-1, 1);
    float closest = max(dot(uv - rayOrigin, rayDirection), 0.0);
    float d = length(uv - rayOrigin - rayDirection * closest);
    fragColor.rgb = mix(vec3(0, 1, 0), fragColor.rgb, step(0.05, d));
    // Drawing ray origin
    fragColor.rgb = mix(vec3(0, 0, 1), fragColor.rgb, step(0.15, length(rayOrigin - uv)));
    // Drawing mouse pos
    fragColor.rgb = mix(vec3(1, 0, 0), fragColor.rgb, step(0.15, length(mousePos - uv)));
}

/*

Please view shader in full screen. It works better at up-scaling
than down-scaling, so needs a viewing window at least 1024x1024 for
the default texture.

AMD FidelityFX Contrast Adaptive Sharpening
https://gpuopen.com/fidelityfx-cas/
Potentially useful to upscale under-resolution renders.

This is a slimmed down reimplementation based on the paper.

Best viewed in fullscreen.
Drag left right to change switchover position. CAS on left.
Drag up and down to set CAS level.

Thanks Txoka for the fixes.
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 muv;
    if (iMouse.z > 0.) {
        muv = iMouse.xy/iResolution.xy;
    } else {
        muv = vec2(.5+.3*sin(iTime*.3),.5);
    }

    vec3 col = texture(iChannel0, uv).xyz;

    // CAS algorithm
    float max_g = col.y;
    float min_g = col.y;
    vec4 uvoff = vec4(1,0,1,-1)/iChannelResolution[0].xxyy;
    vec3 colw;
    vec3 col1 = texture(iChannel0, uv+uvoff.yw).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw = col1;
    col1 = texture(iChannel0, uv+uvoff.xy).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;
    col1 = texture(iChannel0, uv+uvoff.yz).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;
    col1 = texture(iChannel0, uv-uvoff.xy).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;
    float d_min_g = min_g;
    float d_max_g = 1.-max_g;
    float A;
    max_g = max(0., max_g);
    if (d_max_g < d_min_g) {
        A = d_max_g / max_g;
    } else {
        A = d_min_g / max_g;
    }
    A = sqrt(max(0., A));
    A *= mix(-.125, -.2, muv.y);
    vec3 col_out = (col + colw * A) / (1.+4.*A);
    if (uv.x > (muv.x-1./iResolution.x)) {
        if (uv.x > (muv.x+1./iResolution.x)) {
            // 'Control' texture
            col_out = texture(iChannel1, uv).xyz;
        } else {
            // Black line
            col_out = vec3(0);
        }
    }


    // Output to screen
    fragColor = vec4(col_out,1);
}

