float getDistMinimal(vec3 p, out int id) {
    return bootstrapGetDist(iTime, p, id);
}


float getDist(vec3 p, out int id) {
    float d = getDistMinimal(p, id);

    float td = MAX_DIST;
    int tid;
    float fi = -1.;

    for (int i = 0; i < MAX_LASER_BOUNCES; ++i) {
        fi += 1.;
        vec3 start = sampleCell(iChannel0, vec2(0., fi)).xyz;
        if (start.x < -9.) break;
        vec3 end = sampleCell(iChannel0, vec2(1., fi)).xyz;
        td = sdSegment(p, start, end);
        if (td < d) { d = td; id = ID_LASER; }
    }

    return d;
}

vec3 getNormal(vec3 p) {
    int id;
    float dist = getDistMinimal(p, id);
    vec2 e = vec2(MIN_DIST, 0.);
    vec3 n = dist - vec3(
        getDistMinimal(p - e.xyy, id),
        getDistMinimal(p - e.yxy, id),
        getDistMinimal(p - e.yyx, id));
    return normalize(n);
}

float softShadow(vec3 ro, vec3 rd, float k, float mind, float maxd) {
    int id;
    float totalDist = 0.1;
    float res = 1.;
    for (int i = 0; i < MAX_STEPS; ++i) {
        vec3 next = ro + totalDist * rd;
        float d = getDistMinimal(next, id);
        if (id != ID_LASER && id != ID_REFR) {
            res = min(res, k * d / totalDist);
            if (abs(d) < mind) return 0.;
        } else {
            return 1.1;
        }
        totalDist += d;
        if (totalDist > maxd) break;
    }
    return clamp(res, 0., 1.);
}

float castRay(vec3 ro, vec3 rd, out int id) {
    float totalDist = 0.;
    for (int i = 0; i < MAX_STEPS; ++i) {
        vec3 next = ro + totalDist * rd;
        float d = getDist(next, id);
        totalDist += (d);
        if (abs(d) < MIN_DIST || totalDist > MAX_DIST) break;
        if (i == MAX_STEPS - 1) totalDist = MAX_DIST + 1.;
    }

    return totalDist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.y;
    float vpWindow = 2. / iResolution.y;
    vec2 vpShift = vec2(iResolution.x / iResolution.y, 1.);
    vec2 vp = fragCoord * vpWindow - vpShift;

    vec3 camOrigin = vec3(0.2, 2.2, -2.) + sin(iTime * 1.) * vec3(0.1, 0., 0.);
    vec3 camTarget = vec3(0.1, -0.2, 0.5);
    vec3 camForward = normalize(camTarget - camOrigin);
    vec3 camRight = cross(vec3(0., 1., 0.), camForward);
    vec3 camUp = cross(camForward, camRight);

    vec3 ro = camOrigin;
    vec3 rd = normalize(camForward * 2.5 + camRight * vp.x + camUp * vp.y);

    int id;
    float d;
    vec3 col = vec3(0.);
    vec3 mul = vec3(1.);
    float flip = 0.;

    for (int i = 0; i < 20; ++i) {
        d = castRay(ro, rd, id);

        if (d < MAX_DIST) {

            if (id == ID_LASER) {
                col += mul * vec3(0., 2., 0.);
                break;
            }

            vec3 hit = ro + d * rd;
            vec3 n = getNormal(hit);

            vec3 tl = normalize((LIGHT_POS - hit));

            float diffuse = max(0., dot(tl, n));
            float ambient = 0.1;
            float specular = max(0., dot(reflect(normalize(hit - LIGHT_POS), n), -rd));
            float shadow = softShadow(hit + n * MIN_DIST * 2., tl, 10., 0.001, 2.);

            if (id == ID_FLOOR) {
                col += mul * diffuse * vec3(0.5);
                col += mul * ambient * vec3(0.1);
                col += mul * pow(specular, 2.) * vec3(0.2);
                col *= mix(0.7, 1., shadow);
                break;
            } else

            if (id == ID_TARGET) {
                col += mul * diffuse * vec3(1.);
                col += mul * vec3(0.3);
                col += mul * pow(specular, 20.) * vec3(0.2);
                col *= mix(0.7, 1., shadow);

                mul *= 0.2;
                ro = hit + n * MIN_DIST * 2.;
                rd = reflect(rd, n);
                continue;
            } else

            if (id == ID_REFR) {
                col += mul * pow(specular, 20.) * vec3(5.);

                float h = REFR;
                float idx = mix(1. / h, h / 1., flip);
                rd = refract(rd, n, idx);
                ro = hit + rd * WALL * 4.;
                flip = 1. - flip;

                continue;
            }

            if (id == ID_REFL) {
                col += mul * diffuse * vec3(0.1);
                col += mul * pow(specular, 3.) * vec3(1.);
                col *= mix(0.7, 1., shadow);

                ro = hit + n * MIN_DIST * 2.;
                rd = reflect(rd, n);
                rd = normalize(rd + texture(iChannel1, hit.xy * 3.).xyz * rd * 0.4);
                continue;
            }

            break;

        } else {
            break;
        }
    }


    float gamma = 2.2;
    col = pow(col, vec3(1. / gamma));


    float frame = 1. - length(fragCoord.xy / iResolution.xy - 0.5);
    col *= frame * frame * 0.9 + 0.1;


    fragColor = vec4(col,1.0);
}

common_code:
#define MIN_DIST (0.001)
#define MAX_DIST (10.)
#define MAX_STEPS (100)

#define WALL (0.002)
#define MAX_LASER_BOUNCES (20)
#define LASER_ORIGIN (vec3(-1.93, 0., -2.))

const vec3 LASER_DIR = normalize(vec3(0.7, 0., 2.));

#define ID_FLOOR (0)
#define ID_REFL (1)
#define ID_REFR (2)
#define ID_LASER (3)
#define ID_TARGET (4)
#define ID_ROOF (5)

#define LIGHT_POS (vec3(0., 3., -0.5))

#define REFR (1.5)

// distance funcs

vec3 rotate(vec3 v, vec3 axis, float angle) {
    float ah = angle / 2.;
    vec4 q = vec4(axis.xyz * sin(ah), cos(ah));
    vec3 temp = cross(q.xyz, v) + q.w * v;
    return v + 2. * cross(q.xyz, temp);
}

float sdSegment(vec3 p, vec3 s, vec3 e) {
    p -= s;
    e -= s;
    float len = length(e);
    float v = clamp(dot(p, e) / (len * len), 0., 1.);
    vec3 proj = e * v;
    return distance(proj, p) - 0.003;
}

float sdCylinder(vec3 p, float h, float r) {
  vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(h, r);
  return min(max(d.x, d.y),0.0) + length(max(d, 0.0));
}

float sdHollowCylinder(vec3 p, float h, float r) {
    return max(sdCylinder(p, h, r), -sdCylinder(p, h - WALL, r - WALL));
}

// bootstraps

#define ANIM_DURATION (3.5)

float animate(float iTime, float start, float end, float from, float to) {
    iTime += ANIM_DURATION;
    iTime = mod(iTime, ANIM_DURATION + 0.5);
    float dur = (end - start);
    float idx = clamp((iTime - start) / dur, 0., 1.);

    idx = min(idx, 1. - clamp((iTime - ANIM_DURATION) / 0.5, 0., 1.));

    idx = smoothstep(0., 1., idx);
    return mix(from, to, idx);
}

float bootstrapGetDist(float iTime, vec3 p, out int id) {
    id = ID_FLOOR;
    float d = p.y + 0.5, td, shift;
    vec3 tp;

    //d = min(d, 6. - length(p));

    shift = animate(iTime, 0., 0.5, 0.3, 0.5);
    tp = p;
    tp -= vec3(-1., 0., 0.7);
    tp = rotate(tp, vec3(0., 1., 0.), shift);
    tp = rotate(tp, vec3(1., 0., 0.), 1.57);
    td = sdCylinder(tp, 0.2, 0.03);
    if (td < d) { d = td; id = ID_REFL; }

    shift = animate(iTime, 0.5, 1., -0.3, 0.);
    tp = p;
    tp -= vec3(0.15, shift, 0.7);
    td = sdHollowCylinder(tp, 0.5, 0.08);
    if (td < d) { d = td; id = ID_REFR; }

    shift = animate(iTime, 1., 1.5, -1.9, -1.8);
    tp = p;
    tp -= vec3(1., 0., 0.6);
    tp = rotate(tp, vec3(0., 1., 0.), shift);
    tp = rotate(tp, vec3(1., 0., 0.), 1.57);
    td = sdCylinder(tp, 0.2, 0.03);
    if (td < d) { d = td; id = ID_REFL; }

    shift = animate(iTime, 1.5, 2., 2., 1.8);
    tp = p;
    tp -= vec3(-0.7, 0., 1.);
    tp = rotate(tp, vec3(0., 1., 0.), shift);
    tp = rotate(tp, vec3(1., 0., 0.), 1.57);
    td = sdCylinder(tp, 0.2, 0.03);
    if (td < d) { d = td; id = ID_REFL; }

    shift = animate(iTime, 2., 2.5, -0.45, -0.535);
    tp = p;
    tp -= vec3(0.5, 0., 1.47);
    tp = rotate(tp, vec3(0., 1., 0.), shift);
    tp = rotate(tp, vec3(1., 0., 0.), 1.57);
    td = sdCylinder(tp, 0.2, 0.03);
    if (td < d) { d = td; id = ID_REFL; }

    shift = animate(iTime, 2.5, 3., -1.3, -1.1);
    tp = p;
    tp -= vec3(-0.5, 0., -0.5);
    tp = rotate(tp, vec3(0., 1., 0.), shift);
    tp = rotate(tp, vec3(1., 0., 0.), 1.57);
    td = sdCylinder(tp, 0.2, 0.03);
    if (td < d) { d = td; id = ID_REFL; }

    shift = animate(iTime, 3., 3.5, 0., 1.);
    tp = p;
    tp -= vec3(0.7, 0., -0.25);
    td = length(tp) - 0.2;
    if (td < d) { d = td; id = ID_TARGET; }

    return d;
}

// State

#define GRID_SIZE (100.)
#define GRID_CELL (1. / GRID_SIZE)
#define GRID_HALF vec2(GRID_CELL * 0.5)
#define CLOSE(x, y) (abs(x - y) < 0.001)
#define IS_CELL(c, a) (CLOSE(c.x, a.x) && CLOSE(c.y, a.y))

vec2 getGridCell(vec2 uv) {
    vec2 scaled = uv * GRID_SIZE;
    vec2 vertex = floor(scaled);
    vec2 center = vertex + 0.5;
    return distance(scaled, center) < 0.5 ? vertex : vec2(-1.);
}

vec4 sampleCell(sampler2D tex, vec2 index) {
    return texture(tex, index / GRID_SIZE + GRID_HALF);
}

