float getDist(vec3 p, out int id) {
    return bootstrapGetDist(iTime, p, id);
}

vec3 getNormal(vec3 p) {
    int id;
    float dist = getDist(p, id);
    vec2 e = vec2(MIN_DIST, 0.);
    vec3 n = dist - vec3(
        getDist(p - e.xyy, id),
        getDist(p - e.yxy, id),
        getDist(p - e.yyx, id));
    return normalize(n);
}

float castRay(vec3 ro, vec3 rd, out int id) {
    float totalDist = 0.;
    for (int i = 0; i < MAX_STEPS; ++i) {
        vec3 next = ro + totalDist * rd;
        float d = getDist(next, id);
        totalDist += (d);
        if (abs(d) < MIN_DIST || totalDist > MAX_DIST) break;
        if (i == MAX_STEPS - 1) totalDist = MAX_DIST + 1.;
    }

    return totalDist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 cell = getGridCell(uv);

    if (
        cell.x >= 2. || 
        cell.y >= float(MAX_LASER_BOUNCES) || 
        cell.x < 0. || 
        cell.y < 0.
    ) {
        fragColor = vec4(0.);
        return;
    }


    vec3 lo = LASER_ORIGIN, ld = LASER_DIR;
    int id;
    float flip = 0.;

    float fi = -1.;

    for (int i = 0; i < MAX_LASER_BOUNCES; ++i) {
        fi += 1.;

        float d = castRay(lo, ld, id);

        if (IS_CELL(cell, vec2(0., fi))) {
            fragColor = vec4(lo, 0.);
            return;
        }

        vec3 hit = lo + d * ld;

        if (IS_CELL(cell, vec2(1., fi))) {
            fragColor = vec4(hit, 0.);
            return;
        }

        if (d > MAX_DIST) {
            break;
        }

        if (id == ID_REFL) {
            vec3 n = getNormal(hit);
            lo = hit + n * MIN_DIST;
            ld = reflect(ld, n);
            continue;
        }

        if (id == ID_REFR) {
            vec3 n = getNormal(hit);
            lo = hit - n * WALL * 3.;
            ld = refract(ld, n, mix(1. / REFR, REFR / 1., flip));
            flip = 1. - flip;
            continue;
        }

        break;
    }


    fragColor = vec4(vec3(-10.), 1.);
}

