// Fun fact: shear + opposite shear to produce a rotation (e.g. https://link.springer.com/content/pdf/10.1007/3-540-62005-2_26.pdf )
//           Press s & v alternatingly


// Acknowledgement: First Person hyperbolic disk suggested by Manfred.


const int sample_range = 5; 
const float point_size = 0.1;
const float line_thickness = 0.01;
const float infty = 10000.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/pixel_per_unit;

    vec3 color = vec3(0);
    vec2 p1,p2;
    float d1 = infty;
    float d2 = infty;

    mat2 lattice;
    lattice[0] = texelFetch( iChannel0, ivec2(0), 0 ).xy; // Lattice after Reduction from Buffer A
    lattice[1] = texelFetch( iChannel0, ivec2(0), 0 ).zw; // 

    vec2 z = texelFetch( iChannel1, ivec2(0), 0 ).zw; // Height from Buffer B
    float cusp_zoom = min(z.y < 4.6 ? 1. : z.y*0.3, 11.);       // Used to zoom out in upper half plane, increase copies in the disk model, 
    int range = sample_range + 2*int(cusp_zoom);      // and increase the copies of the voronoi cell

    //Voronoi Window
    if(fragCoord.x >= iResolution.x/2. && fragCoord.y >= iResolution.y/2.){ 
        vec2 uv = (fragCoord-vec2(0.75*iResolution.x,0.75*iResolution.y))/pixel_per_unit;
        vec3 color_in;
        for(int x = -range; x<=range; ++x){
            for(int y = -range; y<=range; ++y){
                if(x == 0 && y == 0) 
                    color_in = vec3(1,0,0);
                else if(x == 0 && y == 1)
                    color_in = vec3(0,1,0);
                else if(x == 1 && y == 0)
                    color_in = vec3(0,0,1);
                else
                    color_in = vec3(1);               
                vec2 point = lattice[0]*float(x) + lattice[1]*float(y);

                DrawPoint(color, color_in, uv, point, point_size);
                ClosestPoints(p1, p2, d1, d2, uv, point);
        }}
        if(abs(dot(uv-0.5*(p1+p2), normalize(p2-p1)))<line_thickness) color = vec3(1);   // https://iquilezles.org/articles/voronoilines
    } 
    //Disk Model Window
    else if(fragCoord.x >= iResolution.x/2. && fragCoord.y <= iResolution.y/2.)
    {
        vec2 resolution = iResolution.xy/2.;
        vec2 offset = vec2(0.75 * iResolution.x, 0.25*iResolution.y);
        vec2 w = (fragCoord-offset)/resolution.x*4.; 

        float arrow = sdArrow( w*40., vec2(0,0)*40., vec2(0,0)*50.-.3*vec2(0,-1)*40., 0.2, 1. );
        arrow = arrow <= 0.1 ? 1. : 0.;
        color = vec3(arrow); 

        w = vec2(-w.y,w.x);

        if(length(w)<=0.03){
            color += vec3(1);
        } else if(length(w)<1.){ 
            int number_of_max_translates = 10+int(cusp_zoom)*30; 
            ToHalfplane(w);
            Moebius(inverse(lattice), w);


            bool flip = false; // https://www.shadertoy.com/view/wdjyzh
            int j=0;
            for (;j<number_of_max_translates; ++j)
            {
                flip = !flip;
                if(w.x > 0.5) w.x -= 1.;
                else if(w.x < -0.5) w.x += 1.;
                else if(length(w)<1.) CircleInversion(w);
                else { break; }
            }
            color += j==0 || j==1 || j==2 ? vec3((j+1)%2,j,j%2) :vec3(flip);
         } else if(length(w)<1.02){
             color += vec3(1.);
         }

    } 
    //Upper Half Plane Window
    else{
        vec2 dir = texelFetch( iChannel1, ivec2(0), 0 ).xy;    
        vec2 resolution = vec2(iResolution.x*0.5, iResolution.y);
        vec2 uv = cusp_zoom * 4.*fragCoord/resolution.x; uv.x -= 2.*cusp_zoom;



        if(length(z-uv) < 0.3){
            if(length(z-uv) < 0.05)
                color = vec3(1);
            else{
                //mat2 rot = mat2(0,1,-1,0);
                float arrow = sdArrow( uv*40., z*40., z*40.-.3*dir*40., 0.1, 1. );
                arrow = arrow <= 0.1 ? 1. : 0.;
                color = vec3(arrow);     
            }
        }


        float line_width = max(0.01, cusp_zoom*0.01);
        int num_translates = 0;
        for(int j = -num_translates; j<=num_translates; ++j){
        vec2 st = uv + vec2(j,0);
            if( abs(abs(st.x)-0.5) < line_width && length(st) >= 1.) 
                color = vec3(1);
            else if(abs(length(st)-1.) < line_width) 
                color = vec3(1);
        }

    }


    fragColor = vec4(color,1.0);
}

