const float pixel_per_unit = 100.;


//Transformations
mat2 e(){
    return mat2(1,0,0,1);
}

void Rotate(inout mat2 A, in float theta){
    mat2 k = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
    A = k*A;
}

void Shear(inout mat2 A, in float s){
    mat2 u = mat2(1,s,0,1);
    A= u*A;
}
void ShearOpposite(inout mat2 A, in float s){
    mat2 u = mat2(1,0,s,1);
    A= u*A;
}

void Dilate(inout mat2 A, in float t){
    mat2 a = mat2(t,0,0,1./t);
    A= a*A;
}


void kan(inout mat2 k, inout mat2 A, inout mat2 n, in mat2 g)
{
    float a=g[0][0], b=g[1][0], c=g[0][1], d=g[1][1];
    float t= sqrt(a*a+c*c);

    k[0] = vec2(a,c); k[1] = vec2(-c,a); k = k / t;
    A=mat2(t,0,0,1./t);
    n=mat2(1,(a*b+c*d)/(t*t),0,1);
}


void sl2inverse(inout mat2 g){
    mat2 h = g;
    g[0][0] = h[1][1];
    g[1][1] = h[0][0];
    g[1][0] = -h[1][0];
    g[0][1] = -h[0][1];
}

void MoebiusAti(in mat2 g, inout vec2 z){//z=i -> di-b / -ci+a
    sl2inverse(g);
    float a=g[0][0], b=g[1][0], c=g[0][1], d=g[1][1];
    z = vec2(a*c+b*d,1) / (c*c+d*d);
}

void Moebius(in mat2 g, inout vec2 z){// z -> az+b / cz+d  for a,b,c,d real
    float a=g[0][0], b=g[1][0], c=g[0][1], d=g[1][1];
    float x = a*c*dot(z,z)+b*d + (a*d+b*c)*z.x;
    float y = z.y;
    float denom = (c*c*dot(z,z)+d*d+2.*d*c*z.x);
    z = vec2(x,y) / denom;
    }


void ToHalfplane(inout vec2 z){// z -> -iz-i / z-1
    float x=z[0], y=z[1];
    float denom = x*x + y*y + 1. - 2.*x;
    z = vec2(-2.*y, -x*x-y*y+1.) / denom;
}

void CircleInversion(inout vec2 z)
{
	z.x = -z.x;
	z /= dot(z,z);
}

void HorizontalTranslation(inout vec2 z)
{
	z.x = mod(z.x+0.5, 1.)-0.5;
}




//Lattice Reduction

float ClosestInteger(float f)
{
    return floor(f+0.5);
}

void WeakReduce(inout mat2 A, inout mat2 gamma){
    float mu = round( dot(A[0], A[1]) / dot(A[0],A[0]) );
    A[1] -= mu * A[0];
    mat2 u = mat2(1,-mu,0,1);
    gamma = gamma*u;
}

void GramSchmidt(inout mat2 A){
    float mu = dot(A[0], A[1]) / dot(A[0],A[0]);
    A[1] -= mu * A[0];
}

void SwapColumns(inout mat2 A, inout mat2 gamma){
     mat2 B = A;
     A[0] = -B[1]; A[1] = B[0];
     mat2 w = mat2(0,-1,1,0);
     gamma = gamma*w;
}

bool IsReduced(in mat2 A){
    GramSchmidt(A);
    return dot(A[0],A[0]) <  4./3. * dot(A[1],A[1]);
}


void LLL(inout mat2 A, inout mat2 gamma){ //Lattice Reduction Algorithm
    WeakReduce(A, gamma);
    while(!IsReduced(A)){
        SwapColumns(A, gamma);
        WeakReduce(A, gamma);
    }   
}

#define bug_fix 0.00001 
void MakeShorter(inout mat2 A, inout mat2 gamma){ // Tries to find a shorter basis using at most one step.
    if(dot(A[0],A[0]) > dot(A[1],A[1])+bug_fix) {
        SwapColumns(A, gamma); 
        return;}    
    mat2 B = A, C = A;
    B[1] -=  B[0];
    if(dot(A[1],A[1]) > dot(B[1],B[1])){ 
        A = B; 
        gamma = gamma*mat2(1,-1,0,1);
        return;}
    C[1] +=  C[0];
    if(dot(A[1],A[1]) > dot(C[1],C[1])){
        A = C;
        gamma = gamma*mat2(1,1,0,1);
        return;}
}




float DrawCircle(in vec2 uv, float radius){
    return abs(length(uv)-radius) < 0.01 ? 1. : 0.;
}









void DrawPoint(inout vec3 color_out, in vec3 color_in, in vec2 pixel, in vec2 center, in float radius){
    vec2 point = pixel-center;
    if( dot(point, point) <= radius*radius){
        color_out = color_in;
    }
}

//Checks if "point" is closer to "pixel" compared to the currently closest points p1,p2.
void ClosestPoints(inout vec2 p1, inout vec2 p2, inout float d1, inout float d2, in vec2 pixel, in vec2 point){
    float d = distance(pixel, point);
    if(d < d2){
        if(d<d1){
            d1 = d;
            p1 = point;
        }else{
            d2 = d;
            p2 = point;
        }
    }
}







