#define SWAY 0.01
#define BLURSTRENGTH 1.0

const int samples = 35,
          LOD = 2,         // gaussian done on MIPmap at scale LOD
          sLOD = 1 << LOD; // tile size = 2^LOD
const float sigma = float(samples) * .2;

float gaussian(vec2 i) {
    return exp( -.5* dot(i/=sigma,i) ) / ( 6.28 * sigma*sigma );
}

vec4 blur(sampler2D sp, vec2 U, vec2 scale) {
    vec4 O = vec4(0);  
    int s = samples/sLOD;

    for ( int i = 0; i < s*s; i++ ) {
        vec2 d = vec2(i%s, i/s)*float(sLOD) - float(samples)/2.;
        O += gaussian(d) * textureLod( sp, U + scale * d , float(LOD) );
    }

    return O / O.a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 swayuv = uv * 1.0 + sin(iTime * 0.5) * SWAY;
    vec3 col = texture(iChannel0,swayuv).rgb;
    vec3 blur = blur(iChannel0,uv, 1./iChannelResolution[0].xy ).rgb;
    blur *= BLURSTRENGTH;
    // col += blur * 0.5;
    bool chroma_shift = false;
    if (chroma_shift) {
        swayuv.x += 0.01;
        col.r *= texture(iChannel0,swayuv).r;
        swayuv.x -= 0.02;
        col.b *= texture(iChannel0,swayuv).b;
    }
    col = max(col,blur);
    fragColor = vec4(col,1.0);
}

