// Fork of "Donut factory" by anclin. https://shadertoy.com/view/MsffDS
// 2021-07-05 22:22:34

// I was going to make things much more complex with crepuscular rays, textures, curving bamboo
// trunks, and moving leaves (among other things), but I wanted to have a "good enough" version to
// wrap things up and just get this thing out the door!

const float EPSILON = 0.001;

const vec3 leafColorDark = vec3(0x58, 0x66, 0x0f)/255.;
const vec3 leafColorLight = vec3(0xc0, 0xc0, 0x6e)/255.;
const vec3 trunkColor = vec3(0xb1, 0xb5, 0xb1)/255.;

// credit to iq for making these SDF transforms/shapes
float opExtrussion(in vec3 p, in float sdf, in float h) {
    vec2 w = vec2( sdf, abs(p.y) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float sdCircle( vec2 p, float r ) {
    return length(p) - r;
}

float bamboo(vec3 origin, float height, float radius) {
  float radiusBumpy = max(-.02*abs(sin(10.*origin.y))+radius,radius-.002);
  origin.y -= clamp(origin.y, 0.0, height);
  return length(origin) - radiusBumpy;
}

float leaves(vec3 origin, float h, float distortion) {
    // don't look too closely at these crazy, distorted "leaves"!
    origin.y -= h;
    float c = opExtrussion( origin, sdCircle( vec2(origin.x,origin.z), .5 ), 0.6 );
    return c-.7*sin(distortion*origin.x)*sin(distortion*origin.y)*sin(distortion*origin.z);
}

vec2 N22(vec2 p) {
    vec3 a = fract(p.xyx*vec3(123.34,234.34,345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y,a.y*a.z));
}

float SceneSDF(vec3 p){
    //p = rotateY(iTime * 0.2) * p;
    float smallest = 5.;
    float d = 100.;
    vec3 xyz = fract(p);  // point within a grid cell
    xyz.y = p.y;  // we want the absolute y position in the grid cell for correctly calculating notches
    vec2 id = floor(p.xz);  // the grid cell we are in

    for(float y=-1.; y <= 1.; y++) {  // look at the neighboring cells
        for(float x=-1.; x <= 1.; x++) {  // to see which cell has a point that is the closest to our pixel
            vec2 off = vec2(x, y);
            vec2 coord = id+off;
            vec2 n = N22(id+off);

            // calculate the point, then add the offset to it
            // so that the point is moved into its own grid cell
            // this is the origin for the purposes of this grid cell (i.e. the place where the bamboo trunk is centered)
            // and xyz is the pixel where we are, so we measure from xyz->point since we want to know dist from point->origin
            vec2 point = off+n;

            // dist between Voronoi point and current pixel RELATIVE TO GRID
            // because the point is created relative to the grid
            // so there won't be any Voronois if uv goes too far from the origin
            vec3 gridCenter = vec3(xyz.x-point.x,xyz.y,xyz.z-point.y);

            d = bamboo(vec3(gridCenter),20.,.05);
            d = min(d,leaves(gridCenter,21.9,20.));

            smallest = (d < smallest) ? d : smallest;
        }
    }
    return smallest;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - SceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        SceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - SceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        SceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - SceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 RayDirection(float fieldOfView, vec2 fragCoord, vec2 size){
	vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec4 ShortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end){
	float depth = start;
	vec3 pos = eye;
    for(int i = 0; i < 255; i++){
        vec3 pos = eye + depth * marchingDirection;
        //if(pos.y >= 30.) break;
    	float dist = SceneSDF(pos);
        if( dist <= EPSILON){
            return vec4(pos,depth);
        }
        depth += dist;
        if(depth >= end){
            pos = eye + depth * marchingDirection;
        	return vec4(pos,end);
        }
    }
    return vec4(pos,end);
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 rayDir = RayDirection(45.0, fragCoord.xy, iResolution.xy);

    float t = iTime*0.05;
    vec3 eye = vec3(30.0+10.0*sin(t),3.,10.*cos(t));

    vec3 lookAtPos = vec3(1.0,40.,1.0);

    mat4 viewToWorld = viewMatrix(eye, lookAtPos, vec3(0,1,0) );
    vec3 worldDir = (viewToWorld*vec4(rayDir,0)).xyz;

    vec4 shortDist = ShortestDistanceToSurface(eye,worldDir,0.,100.0);
    vec3 pos = shortDist.xyz;
    float dist = shortDist.w;

    vec3 lightPos = vec3(.5,1.5,.5);
    vec3 lightDirection = normalize(-lightPos);

    vec3 p = eye + dist * worldDir;
    vec3 normal = estimateNormal(p);
    float diffuse = dot(normal, -lightDirection);
    diffuse = max(diffuse,0.);

    vec3 finalColor = vec3(0);

    if(shortDist.y >= 21.35) {
        finalColor = leafColorLight;
    } else if(shortDist.y >= 20.) {
        finalColor = leafColorDark;
    } else {
        finalColor = trunkColor;
    }

    vec3 ambientColor = vec3(.4);
    finalColor = finalColor*(diffuse+ambientColor);

	fragColor = vec4(finalColor,1.);
}

float time = 0.1;
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float opS( float d1, float d2 )
{
    return max(-d1,d2);
}

 mat3 rotationMatrix(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

float map(vec3 p)
{

    vec3 q = p;

    vec3 c = vec3(0.2);
    p.z = mod(p.z,c.z)-0.5*c.z;


    vec3 p_s;

    p = p * rotationMatrix(vec3(0.0, 0.0, 1.0), sin(floor(q.z * 10.0) * 10.0) * 4.0 + 0.1 * (time));

    float bars = 1000.0;
    int sides = 25; // not really sides
    float angle = 3.1415 * 1.0 / float(sides) + sin(iTime/10.0)+ 1.0;

    for ( int i = 0; i < sides; i ++)
    {

        p_s = p * rotationMatrix(vec3(0.0, 0.0, 1.0), angle * float(i));

       	p_s += vec3(
            sin(30.0 * floor(q.z))* 0.5 + 1.0, 
            cos(iTime + sin(q.z* 9.0)), 
            0.0);

        vec3 boxdim = vec3(
        	0.05 + 0.05 * sin(q.z*5.0 + iTime* 2.0), 
            sin(q.z * 10.0) * 0.5  + 0.5, 
            0.01 + pow(sin(iTime), 0.5)
        );


        bars = min(bars, sdBox(p_s, boxdim));  
    }



    float result = bars;   
    return result;
}


