    vec2 xy = fragCoord.xy; //We obtain our coordinates for the current pixel


    xy.x = xy.x / (iResolution.x-iMouse.x); //We divide the coordinates by the screen size 
    xy.y = xy.y / (iResolution.y-iMouse.y);

