    //uv -= 0.5;

    //uv *= rotation_matrix;

    //uv -= 0.5*cos(1.*iTime);

    uv.x = fract(num_flags*uv.x);
    uv.y = fract(num_flags*uv.y);

    float t = uv.x*7. - 2.*iTime + uv.y*3.;

