// from https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec3 getc(vec2 uv, int frame) {
    vec2 o = hash22(vec2(frame, 0.0));
    vec2 r = hash22(vec2(frame, 1.0)) * 6.283185;
    vec2 cs = vec2(cos(r.x), sin(r.x));
    uv = vec2(dot(uv, cs), dot(uv, cs.yx * vec2(1.0, -1.0)));
    vec3 col = texture(iChannel0, uv / vec2(1.0, 1.0) - o).rgb;
    vec3 hsl = rgb2hsl(col);
    hsl.x += float(frame) * 2.0 / (sqrt(5.0) - 1.0) + uv.x * 0.01;
    return hsl2rgb(hsl);
}

vec3 shepard_noise (vec2 uv, float loglevel) {
    int it = int(loglevel);
    float m = fract(loglevel);

    const float O = 2.0;
    const int N = 32;

    uv *= exp(mix(log(1.0),log(O),m));
    uv *= 2.0;

    vec3 w = vec3(0.0);
    w += mix(getc(uv, it - N), vec3(0.0), m);
    uv /= O;
    for (int i = 1; i < N; ++i) {
        w += getc(uv, it - N + i);
        uv /= O;
    }
    w += mix(vec3(0.0), getc(uv, it), m);

    w /= float(N);

    w = pow(vec3(2.0), (w - 0.3)*log2(float(N))*32.0);
    return w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.xy)*2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime / 4.0;


    int it = int(t / 8.0);
    float m = smoothstep(0.0, 1.0, fract(t / 8.0));
    //float m = fract(t / 8.0);

    vec2 p0 = hash22(vec2(it, 0))*2.0 - 1.0;
    float s0 = hash22(vec2(it, 1)).x * 64.0;
    vec2 p1 = hash22(vec2(it + 1, 0))*2.0 - 1.0;
    float s1 = hash22(vec2(it + 1, 1)).x * 64.0;
    vec2 p = mix(p0, p1, m);
    float s = mix(s0, s1, m);

    vec3 w = shepard_noise(uv - p, s);

    fragColor = vec4(pow(ACESFitted(w),vec3(0.4545)),1.0);
}

