// Email: countfrolic@gmail.com
// Twitter: @The_ArtOfCode
// YouTube: youtube.com/TheArtOfCodeIsCool
// Facebook: https://www.facebook.com/groups/theartofcode/
//
// this shader simulates volume ray casting
// 1) ray march a box to find entry and exit points of the volume
// 2) ray cast into the volume and use several different compositing techniques
//    - iso surface with binary search refinement
//    - maximum/minimum intensity projection
//    - front to back emission absorption
//    - switch between them using defines below
// misc:
//    - have a look at VolumeIntensity(...)

#define ISO_SURFACE_RAYCASTING true
#define MAXIMUM_INTENSITY_PROJECTION false
#define MINIMUM_INTENSITY_PROJECTION false
#define FRONT_TO_BACK_EMISSION_ABSORPTION false

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S(a, b, x) smoothstep(a, b, x)
#define T iTime

#define BOX_SIZE 1.

mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p) - s;
	return length(max(p, 0.)) + min(max(p.x, max(p.y, p.z)), 0.);
}

float GetDist(vec3 p) {
    return sdBox(p, vec3(BOX_SIZE));
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO = 0.;

    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += abs(dS);
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0.);

    vec3 n = d - vec3(
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l - p),
        r = normalize(cross(vec3(0., 1., 0.), f)),
        u = cross(f, r),
        c = f * z,
        i = c + uv.x * r + uv.y * u,
        d = normalize(i);
    return d;
}

// here you can simulate whatever volume intensies you want
// usually CT or MRI scans or simulations from the real world are used here
float VolumeIntensity(vec3 p) {
    // p : [0, 1]
    // try to have no hard edges in intensity here -> would produce "ugly" normals

    // simulate sphere in center of volume with soft fall off at the edge of sphere to avoid "ugly" normals
    float i = S(.6, .4, distance(p, vec3(.5)));
    // float i = distance(p, vec3(.5)) < .5 ? 1. : 0.; // sphere without soft edge

    return i;
}

vec3 CentralDiffGradient(vec3 p, float dt)
{
    vec3 a0 = vec3(p.x+dt,p.y,p.z);
    vec3 b0 = vec3(p.x,p.y+dt,p.z);
    vec3 c0 = vec3(p.x,p.y,p.z+dt);

    vec3 a1 = vec3(p.x-dt,p.y,p.z);
    vec3 b1 = vec3(p.x,p.y-dt,p.z);
    vec3 c1 = vec3(p.x,p.y,p.z-dt);

    vec3 gradient = vec3(
        VolumeIntensity(a0) - VolumeIntensity(a1),
        VolumeIntensity(b0) - VolumeIntensity(b1),
        VolumeIntensity(c0) - VolumeIntensity(c1));

    return normalize(gradient);
}

vec3 phong(vec3 N, vec3 L, vec3 V, vec3 color, float shininess)
{
    // calc. lighting
    vec3 R = normalize(reflect(-L, N));
    float diffuse_intensity = max(dot(N, L) * .5 + .5, 0.); // wrapped lambert
    float specular_intensity = pow(max(dot(V, R), 0.), shininess);

    // calc. final color
    return (diffuse_intensity + specular_intensity) * color;
}

vec3 WorldToVolumeCoord(vec3 p, vec3 min_box, vec3 max_box)
{
    return (p - min_box) / (max_box - min_box);
}

struct EntryExitPoints {
    vec3 p_front;
    vec3 p_back;
    bool volume_hit;
};

EntryExitPoints FindEntryExitPoints(vec3 ro, vec3 rd) {
    EntryExitPoints result;
    result.p_front = vec3(0.);
    result.p_front = vec3(0.);
    result.volume_hit = false;

    float d = RayMarch(ro, rd);

    // check if front face was hit
    if(d < MAX_DIST) {
        // front face
        vec3 p_front = ro + d * rd;

        // start again at front face with small offset inside the surface
        // step inside the volume along normal as an alternative instead of just along the ray
        // might be better at small angles
        ro = p_front + 5e-2 * rd;
        d = RayMarch(ro, rd);

        // check if back face was hit
        if (d < MAX_DIST) {
            // back face
            vec3 p_back = ro + d * rd;

            // return valid result if front and back was found
            result.p_front = p_front;
            result.p_front = p_front;
            result.volume_hit = true;
        }
    }

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
	vec2 m = iMouse.xy / iResolution.xy;

    vec3 ro = vec3(0., 3., -3.);
    ro.yz *= Rot(-m.y * 3.14 + 1.);
    ro.xz *= Rot(-m.x * 6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0.), 1.);
    vec3 col = vec3(0.);

    // use ray-box intersection instead of ray marching for better performance here
    EntryExitPoints entry_exit = FindEntryExitPoints(ro, rd);

    if (entry_exit.volume_hit) {
        vec3 p_front = entry_exit.p_front;
        vec3 p_back = entry_exit.p_back;

        // parameters
        vec3 surface_color = vec3(1., .4, .1);
        vec3 light_color = vec3(1.);
        vec3 L = normalize(vec3(1., -1., -1.));
        vec3 V = normalize(-ro);
        float shininess = 32.0;
        float iso_value = .5;
        int refinement_steps = 10;
        float step_size = 1e-3;

        // ray cast from p_front to p_back
        float t = 0.;
        float t_end = distance(p_front, p_back);

        // compositing options:
        // - iso surface ray cast
        // - maximum/minimum intensity projection
        // - front to back emission absorption model

        if (ISO_SURFACE_RAYCASTING) {

            while (t < t_end) {
                // position in world space
                vec3 p = p_front + t * rd;

                // position in volume space with volume at origin
                vec3 coord = WorldToVolumeCoord(p, -vec3(BOX_SIZE), vec3(BOX_SIZE));

                float s = VolumeIntensity(coord);

                // iso surface ray casting with boundary refinement
                if (s > iso_value) {
                    // refine position with binary search of iso surface
                    float t_a = t - step_size;
                    float t_b = t;
                    vec3 coord_r = coord;
                    for (int i = 0; i < refinement_steps; ++i)
                    {
                        // binary search position
                        float t_r = 0.5f * (t_a + t_b);
                        vec3 p_r = p_front + rd * t_r;
                        coord_r = WorldToVolumeCoord(p_r, -vec3(BOX_SIZE), vec3(BOX_SIZE));

                        // sample new intensity
                        float s_r = VolumeIntensity(coord_r);

                        // move left or right boundary for binary search
                        if (s_r < iso_value)
                            t_a = t_r;
                        else
                            t_b = t_r;
                    }

                    // get gradient
                    vec3 N = CentralDiffGradient(coord_r, .5 * step_size);

                    // phong with wrapped lambert
                    col = phong(N, L, V, surface_color, shininess) * light_color;

                    // debug
                    //col = coord_r;
                    //if (uv.x > .0) col = N;

                    // stop, nothing can be seen behind the iso surface if it is fully opaque
                    break;
                }

                // step along ray
                t += step_size;
            }

        } else if (MAXIMUM_INTENSITY_PROJECTION) {

            float max_s = .0;
            while (t < t_end && max_s < 0.99) { // early ray termination
                // position in world space
                vec3 p = p_front + t * rd;

                // position in volume space with volume at origin
                vec3 coord = WorldToVolumeCoord(p, -vec3(BOX_SIZE), vec3(BOX_SIZE));

                float s = VolumeIntensity(coord);

                // store maximum intensity
                max_s = max(max_s, s);

                // step along ray
                t += step_size;
            }
            col = vec3(max_s);

        } else if (MINIMUM_INTENSITY_PROJECTION) {

            float min_s = 1.;
            while (t < t_end && min_s > .0) { // early ray termination
                // position in world space
                vec3 p = p_front + t * rd;

                // position in volume space with volume at origin
                vec3 coord = WorldToVolumeCoord(p, -vec3(BOX_SIZE), vec3(BOX_SIZE));

                float s = VolumeIntensity(coord);

                // store minimum intensity
                min_s = min(min_s, s);

                // step along ray
                t += step_size;
            }
            col = vec3(min_s);

        } else if (FRONT_TO_BACK_EMISSION_ABSORPTION) {

            vec3 C = vec3(.0);
            float A = .0;
            while (t < t_end && A < .99) { // early ray termination
                // position in world space
                vec3 p = p_front + t * rd;

                // position in volume space with volume at origin
                vec3 coord = WorldToVolumeCoord(p, -vec3(BOX_SIZE), vec3(BOX_SIZE));

                float s = VolumeIntensity(coord);

                // classification using a transfer function
                //vec4 color = TransferFunction(s);
                vec4 color = vec4(s);
                color.rgb = surface_color;
                color.a *= .05;

                // get gradient
                vec3 N = CentralDiffGradient(coord, .5 * step_size);

                // shade
                color.rgb = phong(N, L, V, color.rgb, shininess);
                color.a *= step_size * 100.;

                // front to back emission absorption
                C += (1. - A) * color.a * color.rgb;
                A += (1. - A) * color.a;

                // step along ray
                t += step_size;
            }
            col = C.rgb;

        } else {
            // fallback error color as red
            col = vec3(1., 0., 0.);
        }
    }

    fragColor = vec4(col, 1.);
}

vec3 WHITE = vec3(1);
vec3 BLACK = vec3(0);
vec3 SAFFRON = vec3(255,153,51)/255.;
vec3 GREEN = vec3(19,136,8)/255.;
vec3 BLUE = vec3(0,0,128)/255.;
float PI = radians(180.);


float get_band(float x, float width,  float smoothing_d){
return smoothstep(0., smoothing_d, x)*smoothstep(0., smoothing_d, -x + width);

}

vec3 Flag(vec2 uv, float ratio){

// Normalized pixel coordinates (from 0 to 1)



    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    float band = floor(uv.y*3.);

    col = BLACK;

    //float flag_region = smoothstep(0., 0.01, band+1.)*smoothstep(0., 0.01, -band + 3.);
    float flag_region = get_band(band+1., 4., 0.001);

    float top = flag_region*smoothstep(0., 0.01, band + 3.);
    col = mix(col, GREEN, top);

    top = top*smoothstep(0., 0.01, band);
    col = mix(col, WHITE, top);

    top = top*smoothstep(0., 0.01, band-1.);
    col = mix(col, SAFFRON, top);


    /**
    if(band < 3. && band > -1.){
        if (band > 1.){
            col = SAFFRON;
        } else if (band > 0.) {
            col = WHITE;
        } else if (band > -1.){
            col = GREEN; 
        }
    }
    **/


    vec2 center = vec2(0.5);
    vec2 cuv = uv - center;
    cuv.y = cuv.y*ratio;
    float d = length(cuv);
    //float angle = (PI + atan(cuv.y, cuv.x))/2.*PI;
    float angle = atan(cuv.y, cuv.x)/PI;
    angle = 12.*angle;
    angle = fract(angle);

    float circle_r = 0.09;
    float circle_w = 0.01;

    //float circle_rim = smoothstep(0., 0.01, d-circle_r + circle_w)*smoothstep(0., 0.01, d-circle_r);
    float circle_rim = get_band(d-(circle_r - circle_w), circle_w, 0.001);
    col = mix(col, BLUE, circle_rim);

    /**
    if(d > circle_r - circle_w && d < circle_r){
        col = BLUE;
    }
    **/

    col = mix(col, BLUE, smoothstep(0., 0.001, -d+0.01));
    /**
    if(d < 0.01){
        col = BLUE;
    }
    **/


    col = mix(col, BLUE, smoothstep(0., 0.001, -d+circle_r)*get_band(angle-0.8, 0.2, 0.01));

    /**
    if(d < circle_r && angle > 0.8){
        col = BLUE;
    }
    **/

    col = mix(BLACK, col, flag_region);

    return col;


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*7. - 2.*iTime + uv.y*3.;

    uv.y += sin(t)*0.03;

    float ratio = iResolution.y/iResolution.x;
    vec3 col = Flag(uv, ratio);

    col *= .9 + cos(t)*0.2;
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
