#define skyboxVisible true
#define INFINITY 1e9
#define PI 3.1415926
#define NUM_SAMPLES 16
#define NUM_BOUNCES 4

vec2 _Pixel;
float _Seed = 0.0;
float rand() {
    float result = sin(_Seed / 100.0 * dot(_Pixel * cos(iTime * 53.35342), vec2(12.9898f, 78.233f))) * 43758.5453;
    _Seed += 1.0;
    return fract(result);
}
float rand2(vec2 v) {
    float result = fract(sin(fract(iTime + v.x + v.y) * 54538.4375 * dot(v, vec2(12.9898, 78.233))) * 43758.5453);
    return result;
}
float sdot(vec3 x, vec3 y) {
    return clamp(dot(x, y), 0.0, 1.0);
}
vec3 randPointOnSphere(vec2 seed) {
    vec2 uv = vec2(fract(rand2(seed)), fract(rand2(seed * 59.24234)));
    float theta = 2.0 * PI * uv.x;
    float psi = acos(2.0 * uv.y - 1.0);
    float x = cos(theta) * sin(psi);
    float y = sin(theta) * sin(psi);
    float z = cos(psi);
    return vec3(x, y, z);
}
float smoothnessToPhongAlpha(float s)
{
    return pow(1000.0, s * s);
}
float energy(vec3 color)
{
    return (color.x + color.y + color.z) / 3.0;
}

struct Sphere {
    vec3 pos;
    float radius;
    int materialID;
};
struct Box {
    vec3 size;
    vec3 pos;
    int materialID;
};
struct AABB {
    vec3 min;
    vec3 max;
    vec3 pos;
};
struct Plane {
    float y;
    vec3 normal;
    int materialID;
};
struct Material {
    vec3 color;
    bool emission;
    float intensity;
    float ior;
};
struct Ray {
    vec3 origin;
    vec3 direction;
    vec3 energy;
};
struct HitInfo {
    vec3 pos;
    vec3 direction;
    vec3 normal;
    float distance;
    int materialID;
};
Material materials[4] = Material[4](
    Material(vec3(0.5, 0.6, 0.8), false, 0.0),
    Material(vec3(0.7, 0.9, 0.5), false, 0.0),
    Material(vec3(1.0, 0.7, 0.8), false, 0.0),
    Material(vec3(1.0, 1.0, 1.0), true, 5.0)
);
Plane plane = Plane(-1.0, vec3(0.0, 1.0, 0.0), 0);
const int numSpheres = 1;
const int numBoxes = 17;
Sphere spheres[numSpheres] = Sphere[numSpheres](
    Sphere(vec3(0., 5.15, 0.), 1., 1)
);
Box boxes[numBoxes] = Box[numBoxes](
    Box(vec3(.15, 2., .15), vec3(2., 2., 2.), 2),
    Box(vec3(.15, 2., .15), vec3(-2., 2., 2.), 2),
    Box(vec3(.15, 2., .15), vec3(-2., 2., -2.), 2),
    Box(vec3(.15, 2., .15), vec3(2., 2., -2.), 2),
    Box(vec3(2.15, .15, 2.15), vec3(0., 4., 0.), 2),
    Box(vec3(.15, 1., .15), vec3(3.5, 1., 1.), 2),
    Box(vec3(.15, 1., .15), vec3(1.5, 1., 1.), 2),
    Box(vec3(.15, 1., .15), vec3(1.5, 1., -1.), 2),
    Box(vec3(.15, 1., .15), vec3(3.5, 1., -1.), 2),
    Box(vec3(1.15, .15, 1.15), vec3(2.5, 2., 0.), 2),
    Box(vec3(.15, 1.5, .15), vec3(3.5, 3.5, 0.), 2),
    Box(vec3(.15, 1.5, .15), vec3(3.5, 3.5, -.5), 2),
    Box(vec3(.15, 1.5, .15), vec3(3.5, 3.5, .5), 2),
    Box(vec3(.15, 1.5, .15), vec3(3.5, 3.5, 1.), 2),
    Box(vec3(.15, 1.5, .15), vec3(3.5, 3.5, -1.), 2),
    Box(vec3(.15, .15, 1.15), vec3(3.5, 5., 0.), 2),
    Box(vec3(5.0, .001, 5.0), vec3(0., 9.0, 0.), 3)
);

bool intersectAABB(in Ray ray, in AABB box, inout float t_min, inout float t_max)
{
	vec3 div = 1.0 / ray.direction;
	vec3 t_1 = (box.min - ray.origin);
	vec3 t_2 = (box.max - ray.origin);
    t_1 += box.pos;
    t_2 += box.pos;
    t_1 *= div;
    t_2 *= div;
	vec3 t_min2 = min(t_1, t_2);
	vec3 t_max2 = max(t_1, t_2);

	t_min = max(max(t_min2.x, t_min2.y), max(t_min2.z, t_min));
	t_max = min(min(t_max2.x, t_max2.y), min(t_max2.z, t_max));

	return t_min < t_max;
}
void intersectBox(Ray ray, inout HitInfo rayHit, int boxID)
{
    Box box = boxes[boxID];
    AABB boxAABB = AABB(-box.size, box.size, box.pos);
	float t_min = 0.0;
	float t_max = INFINITY;
	if(intersectAABB(ray, boxAABB, t_min, t_max)) {
        if (t_min < rayHit.distance) {
            vec3 p = ray.origin + ray.direction * t_min;
            p -= box.pos;
            p /= box.size;
            if(abs(p.x) > abs(p.y)) {
                if(abs(p.x) > abs(p.z)) {
                    rayHit.normal = vec3(p.x > 0.0 ? 1.0 : -1.0, 0, 0);
                }
                else {
                    rayHit.normal = vec3(0, 0, p.z > 0.0 ? 1.0 : -1.0);
                }
            }
            else if(abs(p.y) > abs(p.z)) {
                rayHit.normal = vec3(0, p.y > 0.0 ? 1.0 : -1.0, 0);
            }
            else {
                rayHit.normal = vec3(0, 0, p.z > 0.0 ? 1.0 : -1.0);
            }
            rayHit.distance = t_min;
            rayHit.pos = ray.origin + ray.direction * t_min;
            rayHit.materialID = box.materialID;
        }
	}
}
void intersectSphere(Ray ray, inout HitInfo rayHit, int sphereID)
{
    Sphere sphere = spheres[sphereID];
    vec3 d = ray.origin - sphere.pos;
    float p1 = -dot(ray.direction, d);
    float p2sqr = p1 * p1 - dot(d, d) + sphere.radius * sphere.radius;
    if (p2sqr < 0.0)
        return;
    float p2 = sqrt(p2sqr);
    float t = p1 - p2 > 0.0 ? p1 - p2 : p1 + p2;
    if (t > 0.0 && t < rayHit.distance)
    {
        if (t < rayHit.distance) {
            rayHit.distance = t;
            rayHit.pos = ray.origin + ray.direction * t;
            rayHit.normal = normalize(rayHit.pos - sphere.pos);
            rayHit.materialID = sphere.materialID;
        }
    }
}
void intersectGroundPlane(Ray ray, inout HitInfo rayHit) {
    float t = -ray.origin.y / ray.direction.y;
    if (t > 0.0 && t < rayHit.distance)
    {
        rayHit.distance = t;
        rayHit.pos = ray.origin + t * ray.direction;
        rayHit.normal = plane.normal;
        rayHit.materialID = plane.materialID;
    }
}
HitInfo trace(Ray ray) {
    HitInfo rayHit = HitInfo(vec3(0.), ray.direction, vec3(0.), INFINITY, 0);
    intersectGroundPlane(ray, rayHit);
    for (int i = 0; i < numSpheres; i++) {
        intersectSphere(ray, rayHit, i);
    }
    for (int i = 0; i < numBoxes; i++) {
        intersectBox(ray, rayHit, i);
    }
    return rayHit;
}
vec3 shade(inout Ray ray, HitInfo hitInfo) {
    vec3 pos = hitInfo.pos;
    vec3 dir = normalize(hitInfo.direction);
    vec3 normal = normalize(hitInfo.normal);
    if (hitInfo.distance < INFINITY) {
        Material material = materials[hitInfo.materialID];
        vec3 diffuse = material.color;
        ray.origin = pos + normal * 0.001;
        vec3 reflectionAngle = reflect(ray.direction, normal);
        float alpha = smoothnessToPhongAlpha(material.smoothness);
        vec3 rand = randPointOnSphere(dir.xy / iResolution.xy);
        ray.direction = rand * sign(dot(normal, rand));
        vec3 specular = vec3(1.0) * (alpha + 2.0) * pow(sdot(ray.direction, reflectionAngle), alpha);
