    } else {
        ray.energy *= 0.0;
        return skyboxVisible ? texture(iChannel1, dir).xyz : vec3(0.0);
    }
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    _Pixel = fragCoord;
