    vec2 translatedUV = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    translatedUV /= 1.7;

    vec4 data = texture(iChannel0, uv);
    if (iMouse.z > 0.0 || iMouse.w > 0.0) data = vec4(0.0);

    vec3 camUp = vec3(0, 1, 0);
	vec3 camLookat = vec3(0, 2, 0);

    float mx = iMouse.x / iResolution.x * PI * 2.0 - 0.7;
	float my = -iMouse.y / iResolution.y * 1.45 + 1.55;
	vec3 camPos = vec3(cos(my) * cos(mx), sin(my), cos(my) * sin(mx)) * (18.2);

    if ((dot(iMouse.xy, vec2(1.0)) <= 8.0)) camPos = vec3(12.0, 5.0, 6.0);

	vec3 camVec=normalize(camLookat - camPos);
	vec3 sideNorm=normalize(cross(camUp, camVec));
	vec3 upNorm=cross(camVec, sideNorm);
	vec3 worldFacing=(camPos + camVec);
	vec3 worldPix = worldFacing + translatedUV.x * sideNorm * (iResolution.x/iResolution.y) + translatedUV.y * upNorm;
	vec3 rayVec = normalize(worldPix - camPos);

    float aspectRatio = iResolution.y / iResolution.x;
    for (int i = 0; i < NUM_SAMPLES; i++) {
        float offset = rand2(vec2(float(i) + iTime, float(i) * iTime)) / iResolution.x;
        vec3 rayDirection = rayVec + offset;
        rayDirection = normalize(rayDirection);
        vec3 color;
        Ray ray = Ray(camPos, rayDirection, vec3(1.0, 1.0, 1.0));
        for (int j = 0; j < NUM_BOUNCES; j++) {
            HitInfo tracedRay = trace(ray);
            color += ray.energy * shade(ray, tracedRay);
            if (ray.energy == vec3(0.0))
                break;
        }
        data += vec4(color, 1.0);
    }
    fragColor = data;
}

