const int CONWAY = 1;
const int ELEMENTARY = 2;


//Adjust here
//--------------------------
const float timeStep = 0.01;
const int ruleset = ELEMENTARY;
const float rule = 30.;
//--------------------------



bool fetch(vec2 coord) {
    vec4 val = texelFetch(iChannel0, ivec2(coord), 0);
    return val.r > .5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Place pixel below mouse
    if (length(floor(iMouse.zw) - floor(fragCoord.xy)) < 1.) {
        fragColor = vec4(1.);
    } else {
        // Run ruleset if timestep has been reached
        if (mod(iTime - iTimeDelta, timeStep) > mod(iTime, timeStep)) { 

            // Get neighbour states
            bool tl = fetch(fragCoord + vec2(-1.,  1.));
            bool tm = fetch(fragCoord + vec2( 0.,  1.));
            bool tr = fetch(fragCoord + vec2( 1.,  1.));
            bool ml = fetch(fragCoord + vec2(-1.,  0.));
            bool mm = fetch(fragCoord + vec2( 0.,  0.));
            bool mr = fetch(fragCoord + vec2( 1.,  0.));
            bool bl = fetch(fragCoord + vec2(-1., -1.));
            bool bm = fetch(fragCoord + vec2( 0., -1.));
            bool br = fetch(fragCoord + vec2( 1., -1.));

            int sum = int(tl) + int(tm) + int(tr)
                    + int(ml) +           int(mr)
                    + int(bl) + int(bm) + int(br);

            // Apply rule(s)
            bool alive;

            if (ruleset == CONWAY) {
                alive = sum == 3 || (alive && sum == 2);
            } else if (ruleset == ELEMENTARY) {
                alive = (( tl &&  tm &&  tr) && (mod(rule, 256.) >= 128.))
                          || (( tl &&  tm && !tr) && (mod(rule, 128.) >=  64.))
                          || (( tl && !tm &&  tr) && (mod(rule,  64.) >=  32.))
                          || (( tl && !tm && !tr) && (mod(rule,  32.) >=  16.))
                          || ((!tl &&  tm &&  tr) && (mod(rule,  16.) >=   8.))
                          || ((!tl &&  tm && !tr) && (mod(rule,   8.) >=   4.))
                          || ((!tl && !tm &&  tr) && (mod(rule,   4.) >=   2.))
                          || ((!tl && !tm && !tr) && (mod(rule,   2.) >=   1.))
                          //Keep "seed" ie. singe pixel at the top
                          ||(!(tl || tm || tr) && mm);
            }

            // Store and display result
            if (alive) {
                fragColor = vec4(1.);
            } else {
                fragColor = vec4(0.);
            }
        } else {
            // Keep previous state
            fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
        }
    }
}

