// if you're having major framerate issues, uncomment the following
// line to enable low quality mode
// #define LOW_QUALITY

//
#ifdef LOW_QUALITY
    #define MAX_ITER 128
    #define MAX_BOUNCE 4
    #define MIN_DIST .001
    #define MAX_DIST 20.
#else
    #define MAX_ITER 256
    #define MIN_DIST .000001
    #define MAX_DIST 20.
    #define MAX_BOUNCE 8
#endif


const vec3 lightDir = normalize(vec3(-.8,.6,0.));

vec2 scene(vec3 p) {
    // table
    vec2 t = vec2(sdCappedCylinder(p, 3., .1), 0.);

    // paint can
    vec3 q = p;
    q.yz = rot2D(q.yz, PI / 2.);
    q.xy = rot2D(q.xy, .6);
    q += vec3(.7, .3, -.58);

    float can = sdCappedCylinder(q, .5, .5);
    float hole = sdCappedCylinder(q+vec3(0.,-.01,0.), .45, .5);
    float disp = noise(p*10.) * .003;
    t = depthTest(t, max(can,-hole)+disp, 1.);

    // paint
    q = p-vec3(.2,.1,-.6);
    float displace = noise(vec3(normalize(q.xz)*4.,0.));
    float paint = sdRoundedCylinder(q, .6 + .1 * displace, .03, .002 * p.y / .06);
    t = depthTest(t, paint, 2.);

    return t;
}

vec2 raymarch(vec3 ro, vec3 rd) {
    float t = 0.;
    float id = -1.;
    for(int i = 0; i < MAX_ITER; i++) {
        vec3 p = ro + rd * t;
        vec2 d = scene(p);
        if(d.x < MIN_DIST || t > MAX_DIST) break;
        t += d.x;
        id = d.y;
    }
    return vec2(t,id);
}

vec3 normal(vec3 p, float t) {
    vec2 e = vec2(t * .00001, 0.);
    vec3 n = vec3(
        scene(p+e.xyy).x-scene(p-e.xyy).x,
        scene(p+e.yxy).x-scene(p-e.yxy).x,
        scene(p+e.yyx).x-scene(p-e.yyx).x
    );
    return normalize(n);
}

// Source: https://iquilezles.org/articles/rmshadows
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = scene(ro + rd*t).x;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

void material(inout Material mat, float id, vec3 p, vec3 n) {
    if(id == 0.) {
        mat.diffuse  = tex3D(p * .5, n, iChannel0).rgb;
        mat.specular = vec3(1.);
        mat.shine    = 10.;
        mat.reflec   = .2;
    } else if(id == 1.) {
        mat.diffuse  = n * .5 + .5;
        mat.specular = vec3(1.);
        mat.shine    = 70.;
        mat.reflec   = .8;
    } else if(id == 2.) {
        float rnd = noise(p*3.5);
        vec3 col = mix(vec3(.2,.7,.4),vec3(.8,.2,.5),rnd);
        mat.diffuse  = col * .9;
        mat.specular = col * 1.5;
        mat.shine    = 50.;
        mat.reflec   = .5;
    }
}

vec3 shade(vec3 ro, vec3 rd) {
    vec3 col = vec3(0.);
    vec3 mask = vec3(1.);

    for(int i = 0; i < MAX_BOUNCE; i++) {
        vec2 t = raymarch(ro, rd);

        // did the ray hit anything?
        if(t.x < MAX_DIST) {
            // calculate position and normal
            vec3 p = ro + rd * t.x;
            vec3 n = normal(p, t.x);
            vec3 r = normalize(reflect(rd, n));

            // get material data
            Material mat;
            material(mat, t.y, p, n);

            // output the color
            float shad = softshadow(p, lightDir, .02, MAX_DIST, 20.);
            float diff = max(0.,dot(lightDir,n)) * shad;
            float spec = pow(max(0.,dot(lightDir,r)),mat.shine) * shad;

            vec3 light = mat.diffuse * diff + mat.specular * spec;
            col += light * mask * (1. - mat.reflec);

            // if this surface is reflective, change
            // the ray for the next bounce
            if(mat.reflec > 0.) {
                mask *= mat.reflec * mat.specular;
                rd = r;
                ro = p + rd * .001;
            } else {
                break;
            }
        } else {
            // ray hit nothing, output the background and
            // stop further bounces
            if(i < 1) col += texture(iChannel2, rd, 1.7).rgb * mask;
            else col += texture(iChannel1, rd).rgb * mask;
            break;
        }
    }

    return col;  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    vec3 ro = vec3(0.,1.,-5.);
    vec3 rd = vec3(uv, 1.);

    float a = iTime * .2;
    if(iMouse.xy != vec2(0.)) {
        a = PI * ((2. * iMouse.x / iResolution.x) - 1.);
        ro *= .2 + .8 * (iMouse.y / iResolution.y);
    }
    ro.xz = rot2D(ro.xz, a);
    rd.xz = rot2D(rd.xz, a);

    fragColor.rgb = shade(ro, normalize(rd));
    fragColor.w   = 1.;
}

