#define PI 3.14159265359

struct Material {
    vec3 diffuse;
    vec3 specular;
    float shine;
    float reflec;
};

vec2 rot2D(vec2 p, float a) {
    float s = sin(a), c = cos(a);
    mat2 m  = mat2(c,s,-s,c);
    return m * p;
}

vec2 depthTest(vec2 a, float bt, float bi) {
    if(a.x < bt) return a;
    else return vec2(bt, bi);
}

// 3d texture sampler by reinder (thanks!)
// Source: https://www.shadertoy.com/view/4dj3Dw
vec4 tex3D( in vec3 pos, in vec3 normal, sampler2D sampler )
{
	return 	texture( sampler, pos.yz )*abs(normal.x)+ 
			texture( sampler, pos.xz )*abs(normal.y)+ 
            texture( sampler, pos.xy )*abs(normal.z);
}

// Source: https://iquilezles.org/articles/distfunctions
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

// Source: https://www.shadertoy.com/view/4sfGzS
float hash(vec3 p)  // replace this by something better
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}
float noise( in vec3 x )
{
    vec3 i = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(i+vec3(0,0,0)), 
                        hash(i+vec3(1,0,0)),f.x),
                   mix( hash(i+vec3(0,1,0)), 
                        hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(i+vec3(0,0,1)), 
                        hash(i+vec3(1,0,1)),f.x),
                   mix( hash(i+vec3(0,1,1)), 
                        hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

