    - texture(iChannel0, uv.xy-vec2(0., th)).r;

    float gxg = texture(iChannel0, uv.xy+vec2(th, 0.)).g
    - texture(iChannel0, uv.xy-vec2(th, 0.)).g ;

    float gyg = texture(iChannel0, uv.xy+vec2(0., th)).g
    - texture(iChannel0, uv.xy-vec2(0., th)).g;

    float gxb = texture(iChannel0, uv.xy+vec2(th, 0.)).b
    - texture(iChannel0, uv.xy-vec2(th, 0.)).b;

    float gyb = texture(iChannel0, uv.xy+vec2(0., th)).b
    - texture(iChannel0, uv.xy-vec2(0., th)).b;

    //hack to concea noise from: https://www.shadertoy.com/view/Mdf3zr
    float gr = gxr*gxr + gyr*gyr;
    float gg = gxg*gxg + gyg*gyg;
    float gb = gxb*gxb + gyb*gyb;

    //more noise control
    float g = pow((gr+gg+gb)/1.,1.9);


    vec3 col = texture(iChannel0, p / iResolution.xy).xyz;
    col = mix(col,  0.5 + 0.5*cos(iTime*4.+uv.y*6.+vec3(0,2,4)),g*100.);

