// All the distance functions from:https://iquilezles.org/articles/distfunctions
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 256
#define MAX_DIST 200.
#define SURF_DIST .0001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define EDGE_WIDTH 0.01

#define MATERIAL_DIFFUSE0 0
#define MATERIAL_REFLECT 10

float edge;
float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

float sdBox( vec3 p, vec3 b )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// https://iquilezles.org/articles/fbmsdf
// https://www.shadertoy.com/view/3dGSWR
float sph( vec3 i, vec3 f, vec3 c )
{
    // random radius at grid vertex i+c (please replace this hash by
    // something better if you plan to use this for a real application)
    vec3  p = 17.0*fract( (i+c)*0.3183099+vec3(0.11,0.17,0.13) );
    float w = fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
    float r = 0.8*w*w;
    // distance to sphere at grid vertex i+c
    return sdBox((f-c),vec3(r));
}

// https://iquilezles.org/articles/fbmsdf
float sdBase( in vec3 p , bool animate)
{
    float t = iTime*2.0;
    //if(animate)p.y-=t;
    vec3 i = floor(p);
    vec3 f = fract(p);
    return min(min(min(sph(i,f,vec3(0,0,0)),
                       sph(i,f,vec3(0,0,1))),
                   min(sph(i,f,vec3(0,1,0)),
                       sph(i,f,vec3(0,1,1)))),
               min(min(sph(i,f,vec3(1,0,0)),
                       sph(i,f,vec3(1,0,1))),
                   min(sph(i,f,vec3(1,1,0)),
                       sph(i,f,vec3(1,1,1)))));
}


vec2 GetDist(vec3 p) {
    float t = iTime*6.0;
    p.z+=t;

    vec3 prevP = p;
    float d = sdBase(p,true);
    float d2 = sdBase(p,false);

    p.xz = mod(p.xz,20.0)-10.0;
    float b = sdBox(p,vec3(3.0,10.0,3.0));

    d = max(b,-d);
    d2 = min(max(-p.y+10.0,-d2),max(p.y+10.0,-d2));

    vec2 res = vec2(min(d,d2)*0.5, MATERIAL_DIFFUSE0);

    vec2 model = res;
    return model;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side) {
    vec2 dO = vec2(0.0);
    float lastDistEval = 1e10;
    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if (lastDistEval < EDGE_WIDTH && dS.x > lastDistEval + 0.001) {
			edge = 1.0;
		}
        if (dS.x < lastDistEval) lastDistEval = dS.x;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 diffuseMaterial(vec3 n, vec3 col) {
    float dif = dot(n,normalize(vec3(1,2,3)))*0.5+0.5;
    return vec3(dif)*col;
}

vec3 reflectMaterial(vec3 p, vec3 rd, vec3 n) {
    vec3 r = reflect(rd,n);
    vec3 col = r;

    float k = r.y*2.5+2.5;
    col = mix(col*vec3(0.5),vec3(0.5),k);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 2, -3.5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    //ro.xz *= Rot(radians(180.0));

    if(mod(iTime,20.0)<17.0){
        ro.xz *= Rot(-iTime*.1+1.0);
    } else {
        ro.xz *= Rot(radians(180.0));
    }

    #endif

    vec3 rd = R(uv, ro, vec3(0,0,0), 3.0);
    vec2 d = RayMarch(ro, rd, 1.);
    vec3 col = vec3((map(uv.y,0.0,1.0,1.0,0.5))-0.6);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd,n);

        int mat = int(d.y);
        if(mat == MATERIAL_DIFFUSE0){
            col = diffuseMaterial(n,vec3(1.0));
        } else if(mat == MATERIAL_REFLECT) {
            col = reflectMaterial(p,rd,n);
        }

    }

    // gamma correction
    col = pow( col, vec3(0.6545) );
    col = mix(col,vec3(0.3,0.3,0.3),edge);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = vec2(0.5, 0.5);
    float tr = 0.2;
    vec2 time_offset = vec2(tr*cos(iTime), tr*sin(iTime));

    vec2 tcenter = center + time_offset;

    vec2 tuv = uv - tcenter;


    float dist = length(tuv);
    float tlen = length(time_offset);
    float tproj = dot(tuv, time_offset)/(dist*tlen);


    vec3 col = vec3(0.8, 0.5, 0.1);
    float alpha = 1.0;

    if(dist > 0.1*abs(cos(iTime))){
        //col = vec3(abs(time_offset/tr)*0.5, 0.2*abs(cos(iTime)));
        col = vec3(0.2*abs(cos(iTime)), 0.5*abs(cos(iTime)), 0.5*abs(cos(iTime)));
    }

    if(tproj < (-0.99 -0.01*sin(iTime/2.0)) && tproj >= -1.0){
        col = vec3(0.9, 0.9, 0.5);
        alpha = 0.2;
    }


    if(dist < 0.01){
        col = vec3(1, 1, 0.2);
        alpha = 1.0;
    }

    if(distance(uv, center) < 0.01){
        col = vec3(0.2, 0.2, 0.2);
        alpha = 1.0;
    }




    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,alpha);
}

