    float sdOjoIzq= sdEllipsoid( p-vec3(-2.5,-0.5,-4.0), vec3(1.8,1.4,0.9) );
    float sdOjoDer= sdEllipsoid( p-vec3( 2.5,-0.5,-4.0), vec3(1.8,1.4,0.9) );

    //restar cilindro a ojos y poner en negro uno nuevo
    float sdPupilaIzq=sdCylinderXY(p-vec3(-2.5,-0.65,-3.5), vec2(0.2,2.0) );
    float sdPupilaDer=sdCylinderXY(p-vec3( 2.5,-0.65,-3.5), vec2(0.2,2.0) );
    float sdOjoIzqInter=intersectSDF( sdOjoIzq,sdPupilaIzq);
    float sdOjoDerInter=intersectSDF( sdOjoDer,sdPupilaDer);

    float sdOjoIzqDif=differenceSDF( sdOjoIzq,sdPupilaIzq);
    float sdOjoDerDif=differenceSDF( sdOjoDer,sdPupilaDer);
     sdOjoIzq=sdOjoIzqDif;
     sdOjoDer=sdOjoDerDif;


    res =opU3(res, vec3(sdOjoIzqInter,0.0,MATERIAL_NO));
    res =opU3(res, vec3(sdOjoIzq,1.0,MATERIAL_NO));

    res =opU3(res, vec3(sdOjoDerInter,0.0,MATERIAL_NO));
    res =opU3(res, vec3(sdOjoDer,1.0,MATERIAL_NO));



    //Nariz
    vec3 prot0=p;
    prot0.yz= rotatev2(p.yz, radians(350.0));  //inclinado hacia adelante
    float sNariz1= sdCylinderXZ(prot0-vec3(0.0,-1.5,-5.3), vec2(0.2,1.5) );
    vec3 pn=p-vec3(0.0,1.0,0.0);
    vec3 prot1=pn;
    vec3 prot2=pn;
     prot1.xy= rotatev2(pn.xy, radians(15.0)); //lado izq
     prot2.xy= rotatev2(pn.xy, radians(345.)); //lado derecho


    float sNariz1Izq= sdCylinderXZ(prot1-vec3( 0.1,-1.5,-5.2), vec2(0.18,1.5) );
    float sNariz1Der= sdCylinderXZ(prot2-vec3(-0.1,-1.5,-5.2), vec2(0.18,1.5) );

    res =opU3(res, vec3(sNariz1,2.0,MATERIAL_NO));

    //suavizar frente
    sdCabeza= smin( sdCabeza, sdFrente, 2.0);
    sdCabeza= smin( sdCabeza, sNariz1, 1.5);


    sdCabeza= smin( sdCabeza, sNariz1Izq, 0.8);
    sdCabeza= smin( sdCabeza, sNariz1Der, 0.8);

    p=p-vec3(0.0,0.5,0.0);  //subir labios
    //Labio Izquierdo
    float r1; float r2; float h;
    r1=0.05; r2=0.3; h=1.7;
    pn=p-vec3(0.15,0.0,0.5); //unir mas cerca labios
    prot1=rotate_z(pn-vec3(-2.0+0.1,-4.5,-4.5), radians(70.0));
    prot2=rotate_z(pn-vec3(-2.0,    -4.5,-4.5), radians(86.0));

    //rotar hacia un lado de afuera
    prot1=rotate_x(prot1, radians(20.0));
    prot2=rotate_x(prot2, radians(20.0));
    float sdLabioIzqA= sdRoundCone( prot1-vec3(0.0,0.0,0.0), r1, r2, h );
    float sdLabioIzqB= sdRoundCone( prot2-vec3(0.0,0.0,0.0), r1, r2, h );



    //----labio derecho
    pn=p-vec3(-0.15,0.0,0.5);
    prot1=rotate_z(pn-vec3(2.0-0.2,-4.5,-4.5), radians(270.0+2.0));
    prot2=rotate_z(pn-vec3(2.0-0.2,-4.5,-4.5), radians(286.0+2.0));

    //rotar hacia un lado de afuera
    prot1=rotate_x(prot1, radians(20.0));
    prot2=rotate_x(prot2, radians(20.0));
    float sdLabioDerA= sdRoundCone( prot1-vec3(0.0,0.0,0.0), r1, r2, h );
    float sdLabioDerB= sdRoundCone( prot2-vec3(0.0,0.0,0.0), r1, r2, h );

    res =opU3(res, vec3(sdCabeza,16.0,MATERIAL_NO));
    res =opU3(res, vec3(sdLabioDerA,2.0,MATERIAL_NO));
    res =opU3(res, vec3(sdLabioDerB,2.0,MATERIAL_NO));
    res =opU3(res, vec3(sdLabioIzqA,2.0,MATERIAL_NO));
    res =opU3(res, vec3(sdLabioIzqB,2.0,MATERIAL_NO));

    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}

//------------------------------------------------
vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

