vec3 amb(vec3 c, float k)
{  return c * k; }

float diff(vec3 p,vec3 lp,vec3 n )
{   vec3 l = normalize(lp - p);
    float dif = clamp(dot(l, n), 0.0, 1.0);
    return dif;
}
float spec( vec3 p, vec3 lp,vec3 rd,vec3 n )
{ vec3 l = normalize(lp - p);
  vec3 r = reflect(-l, n);
  float spe =pow(clamp(dot(r, -rd), 0.0, 1.0), 20.0);
  return spe;
}



vec3 LightShadingV2(vec3 normal,vec3 p, vec3 ro,vec3 rd, vec3 plight_pos,vec3 color)
{   
    vec3 final = vec3( 0.0 );
    float shininess = 64.;
	vec3 l = normalize( p-plight_pos);
    vec3 v = normalize( p-ro);
    vec3 h = normalize(v + l);

    vec3 lightDir = normalize(plight_pos);
    vec3 viewDir = normalize(-rd);  
    vec3 halfV = normalize(viewDir + lightDir);
    float diff = dot(normal, l);

    vec3 vl = normalize( plight_pos - p );
    vec3 ref_v = reflect( v, normal );
    float diffuse  = max( 0.0, dot( vl, normal ) );
    float specular = max( 0.0, dot( vl, ref_v ) );
		specular = pow( specular, shininess );
		final += color * ( diffuse + specular);
    return  final;


}   


