//https://www.shadertoy.com/view/4lcSRn   ///IQ
vec3 pattern( in vec2 uv )
{   vec3 col = vec3(0.4);
    col += 0.4*smoothstep(-0.01,0.02,cos(uv.x*0.5)*cos(uv.y*0.5)); 
    col *= smoothstep(-1.0,-0.98,cos(uv.x))*smoothstep(-1.0,-0.98,cos(uv.y));
    return col;
}

// Convert polar coordinates -> rectangular.
vec2 polar2rect(vec2 polar)
{
    float x = polar.x * cos(polar.y);
    float y = polar.x * sin(polar.y);
    return vec2(x, y);
}


//https://www.shadertoy.com/view/stXXWn
///http://glslsandbox.com/e#73908.0
vec3 espiral( vec2 uv ) 
{
    float size= 10.0;
    vec2 cuv = vec2(2.0*length(uv), atan(uv.x, uv.y));
    cuv.x = mix(cuv.x, 2.0/cuv.x, 0.7);
    float td = (pow(0.3/length(cuv.x),1.0));
    float strip1 = step(-sin(size*(cuv.x-cuv.y)), -0.5);
    float strip2 = step(-sin(size*(cuv.x+cuv.y)), -0.1)*step(sin(size*(cuv.x-cuv.y)), -0.0);
    return vec3(5.0*(strip1*4.+2.*strip2)*td);

}
