#define AA  0   // (= 0/1) optional antialiasing

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrEVCapsDf (vec3 p, vec4 u, float r);
float PrEECapsDf (vec3 p, vec3 v1, vec3 v2, float r);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec2 Noisev2v4 (vec4 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec4 vum[4], vur[5], shipConf;
vec3 qHit, ltDirEx, sunDir, vim[4], vir[5], bDeck, sailVel;
float tCur, dstFar;
int idObj;
bool monoImg;
const int idHull = 1, idRud = 2, idStruc = 3, idMast = 4, idSparT = 5, idSparL = 6, idSailT = 7,
   idSailA = 8, idSailF = 9, idFlag = 10, idRig = 11;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float WaveHt (vec2 p, float d)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  h = 0.;
  if (d < 0.3 * dstFar) {
    q = p;
    q += sailVel.xz * tCur;
    wFreq = 0.5;
    wAmp = 0.1;
    tw = 0.1 * tCur * vec2 (1., -1.);
    for (int j = 0; j < 4; j ++) {
      q *= qRot;
      t4 = abs (sin (wFreq * (q.xyxy + tw.xxyy) + 2. * Noisev2v4 (t4).xxyy - 1.));
      v4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
      t = 1. - sqrt (v4.xz * v4.yw);
      t *= t;
      t *= t;
      h += wAmp * (t.x + t.y);
      wFreq *= 2.;
      wAmp *= 0.5;
    }
    h *= 1. - smoothstep (0.1, 0.3, d / dstFar);
  }
  return h;
}

vec4 WaveNf (vec3 p, float d)
{
  vec2 e;
  float h;
  e = vec2 (max (0.1, 1e-4 * d * d), 0.);
  h = WaveHt (p.xz, d);
  return vec4 (normalize (vec3 (h - vec2 (WaveHt (p.xz + e.xy, d),
     WaveHt (p.xz + e.yx, d)), e.x)).xzy, h);
}

float ShipHullDf (vec3 p, float dMin)
{
  vec3 q;
  float d, fy, fz, gz;
  q = p;
  d = abs (p.z) - 4.5;
  q.z = mod (q.z + 1.4, 2.8) - 1.4;
  q.yz -= vec2 (-3.4, -0.4);
  d = max (d, PrBoxDf (q, vec3 (0.3, 0.1, 0.5)));
  DMINQ (idStruc);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (-3.8, 0.5);
  fz = q.z / 5. + 0.3;
  fz *= fz;
  fy = 1. - smoothstep (-1.3, -0.1, q.y);
  gz = smoothstep (2., 5., q.z);
  bDeck = vec3 ((1. - 0.45 * fz * fz) * (1.1 - 0.5 * fy * fy) *
     (1. - 0.5 * smoothstep (-5., -2., q.y) * smoothstep (2., 5., q.z)),
     0.78 - 0.8 * gz * gz - 0.2 * (1. - smoothstep (-5.2, -4., q.z)), 5. * (1. + 0. * 0.02 * q.y));
  d = min (PrBoxDf (vec3 (q.x, q.y + bDeck.y - 0.6, q.z), bDeck),
     max (PrBoxDf (q - vec3 (0., 0.72, -4.6), vec3 (bDeck.x, 0.12, 0.4)),
     - PrBox2Df (vec2 (abs (q.x) - 0.4, q.y - 0.65), vec2 (0.2, 0.08))));
  d = max (d, - PrBoxDf (vec3 (q.x, q.y - 0.58 - 0.1 * fz, q.z),
     vec3 (bDeck.x - 0.07, 0.3, bDeck.z - 0.1)));
  q = p;
  d = max (d, - max (PrBox2Df (vec2 (q.y + 3.35, mod (q.z + 0.25, 0.5) - 0.25), vec2 (0.08, 0.1)),
     abs (q.z + 0.5) - 3.75));
  DMINQ (idHull);
  q = p;
  d = PrBoxDf (q + vec3 (0., 4.4, 4.05), vec3 (0.03, 0.35, 0.5));
  DMINQ (idRud);
  return dMin;
}

float ShipMastDf (vec3 p, float dMin)
{
  vec3 q, qq;
  float d, fy, fz, s, rSpar, yLim, zLim;
  rSpar = 0.05;
  fy = 1. - 0.07 * p.y;
  fz = 1. - 0.14 * step (1., abs (p.z));
  zLim = abs (p.z) - 4.5;
  q = p;
  d = zLim;
  q.z = mod (q.z + 1.4, 2.8) - 1.2;
  d = max (d, PrCapsDf ((q - vec3 (0., 3.7 * (fz - 1.), 0.)).xzy, 0.1 * fy, 3.7 * fz));
  DMINQ (idMast);
  q = p;
  yLim = abs (q.y - 0.2 * fz) - 3. * fz;
  qq = q;
  qq.y = mod (qq.y - 3.3 * (fz - 1.), 2. * fz) - fz;
  qq.z = mod (qq.z + 1.4, 2.8) - 1.4 + 0.1 * fz;
  d = max (max (min (d, PrCylDf (vec3 (qq - vec3 (0., 0.05 * fy * fz, 0.1 * fz - 0.23)).xzy,
     0.15 * fy, 0.11 * fy * fz)), yLim), zLim);
  DMINQ (idMast);
  d = max (max (PrCapsDf (qq.yzx, 0.05, 1.23 * fy * fz), yLim), zLim);
  DMINQ (idSparT);
  q = p;
  d = dstFar;
  for (int k = 0; k < 4; k ++) d = min (d, PrEVCapsDf (q - vim[k], vum[k], rSpar));
  DMINQ (idSparL);
  return dMin;
}

float ShipSailDf (vec3 p, float dMin)
{
  vec3 q, qq, w;
  float d, fy, fz;
  fy = 1. - 0.07 * p.y;
  fz = 1. - 0.14 * step (1., abs (p.z));
  q = p;
  qq = q;
  qq.y = mod (qq.y - 3.1 * (fz - 1.), 2. * fz) - fz;
  qq.z = mod (qq.z + 1.4, 2.8) - 1.4 + 0.2 * (fz - abs (qq.y)) * (fz - abs (qq.y)) - 0.1 * fz;
  d = max (max (max (PrBoxDf (qq, vec3 ((1.2 - 0.07 * q.y) * fz, fz, 0.01)),
     min (qq.y, 1.5 * fy * fz - length (vec2 (qq.x, qq.y + 0.9 * fy * fz)))),
     abs (q.y - 3. * (fz - 1.)) - 2.95 * fz), - PrBox2Df (qq.yz, vec2 (0.01 * fz)));
  d = max (d, abs (p.z) - 4.5);
  DMINQ (idSailT);
  q = p;
  q.z -= -3.8;
  q.y -= -1.75 - 0.2 * q.z;
  d = PrBoxDf (q, vec3 (0.01, 0.9 - 0.2 * q.z, 0.6));
  DMINQ (idSailA);
  q = p;
  q.yz -= vec2 (-1., 4.5);
  w = vec3 (1., q.yz);
  d = max (max (max (abs (q.x) - 0.01, - dot (w, vec3 (2.3, 1., -0.35))),
     - dot (w, vec3 (0.68, -0.74, -1.))), - dot (w, vec3 (0.41, 0.4, 1.)));
  DMINQ (idSailF);
  q = p;
  q.yz -= vec2 (3.4, 0.18);
  d = PrBoxDf (q, vec3 (0.01, 0.2, 0.3));
  DMINQ (idFlag);
  return dMin;
}

float ShipRigDf (vec3 p, float dMin)
{
  vec3 q;
  float rRig, d, fz, gz, s;
  rRig = 0.02;
  fz = 1. - 0.14 * step (1., abs (p.z));
  q = p;
  d = abs (p.z) - 4.5;
  gz = (q.z - 0.5) / 5. + 0.3;
  gz *= gz;
  gz = 1.05 * (1. - 0.45 * gz * gz);
  q.x = abs (q.x);
  q.z = mod (q.z + 1.4, 2.8) - 1.4;
  d = max (d, min (PrEECapsDf (q, vec3 (1.05 * gz, -3.25, -0.5),
     vec3 (1.4 * fz, -2.95, -0.05), 0.7 * rRig),
     PrEECapsDf (vec3 (q.xy, abs (q.z + 0.2) - 0.01 * (0.3 - 2. * q.y)), vec3 (gz, -3.2, 0.),
     vec3 (0.05, -0.9 + 2. * (fz - 1.), 0.), rRig)));
  q = p;
  d = min (d, PrEVCapsDf (q - vir[0], vur[0], 0.8 * rRig));
  d = min (min (d, min (PrEVCapsDf (q - vir[1], vur[1], rRig),
     PrEVCapsDf (q - vir[2], vur[2], rRig))), PrEVCapsDf (q - vir[3], vur[3], rRig));
  q.x = abs (q.x);
  d = min (d, PrEVCapsDf (q - vir[4], vur[4], rRig));
  s = step (1.8, q.y) - step (q.y, -0.2);
  d = min (min (d, min (PrEECapsDf (q, vec3 (0.95, 0.4, 2.7) + vec3 (-0.1, 1.7, 0.) * s,
     vec3 (0.05, 1.1, -0.15) + vec3 (0., 2., 0.) * s, rRig),
     PrEECapsDf (q, vec3 (1.05, 1., -0.1) + vec3 (-0.1, 2., 0.) * s,
     vec3 (0.05, 0.5, -2.95) + vec3 (0., 1.7, 0.) * s, rRig))),
     PrEECapsDf (q, vec3 (0.95, 0.4, -2.9) + vec3 (-0.1, 1.7, 0.) * s,
     vec3 (0.05, 0.9, -0.25) + vec3 (0., 2., 0.) * s, rRig));
  DMINQ (idRig);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin;
  p.xz = Rot2D (p.xz, shipConf.w);
  p.yz = Rot2D (p.yz, -1. * shipConf.z);
  p.xy = Rot2D (p.xy, 6. * shipConf.y);
  p.y -= shipConf.x + 4.1;
  q = p;
  dMin = dstFar;
  dMin = ShipHullDf (q, dMin);
  dMin = ShipMastDf (q, dMin);
  dMin = ShipSailDf (q, dMin);
  dMin = ShipRigDf (q, dMin);
  return 0.7 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

void EvalShipConf ()
{
  vec4 h;
  vec3 vd;
  vec2 e;
  e = vec2 (4., 0.);
  h = vec4 (WaveHt (- e, 0.), WaveHt (e, 0.), WaveHt (- e.yx, 0.), WaveHt (e.yx, 0.));
  shipConf = vec4 (0.25 * dot (h, vec4 (1.)), vec2 (h.xz - h.yw) / (2. * e.x), 0.15 * pi * sin (0.05 * tCur));
  vim[0] = vec3 (0., -3.5, 4.3);   vd = vec3 (0., -2.6, 6.7) - vim[0];   vum[0] = vec4 (normalize (vd), length (vd));
  vim[1] = vec3 (0., -4., 4.1);    vd = vec3 (0., -2.9, 6.) - vim[1];    vum[1] = vec4 (normalize (vd), length (vd));
  vim[2] = vec3 (0., -1.2, -3.);   vd = vec3 (0., -0.5, -4.5) - vim[2];  vum[2] = vec4 (normalize (vd), length (vd));
  vim[3] = vec3 (0., -2.7, -3.);   vd = vec3 (0., -2.7, -4.5) - vim[3];  vum[3] = vec4 (normalize (vd), length (vd));
  vir[0] = vec3 (0., -3., -4.45);  vd = vec3 (0., -2.7, -4.5) - vir[0];  vur[0] = vec4 (normalize (vd), length (vd));
  vir[1] = vec3 (0., 2.45, 2.65);  vd = vec3 (0., -2.7, 6.5) - vir[1];   vur[1] = vec4 (normalize (vd), length (vd));
  vir[2] = vec3 (0., 2.5, 2.65);   vd = vec3 (0., -3.2, 4.9) - vir[2];   vur[2] = vec4 (normalize (vd), length (vd));
  vir[3] = vec3 (0., 2.6, -3.);    vd = vec3 (0., -0.5, -4.5) - vir[3];  vur[3] = vec4 (normalize (vd), length (vd));
  vir[4] = vec3 (0.65, -3.5, 3.5); vd = vec3 (0.05, -2.7, 6.4) - vir[4]; vur[4] = vec4 (normalize (vd), length (vd));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += clamp (h, 0.02, 0.3);
    if (sh < 0.05) break;
  }
  return 0.6  + 0.4 * sh;
}

void ShipCol (out vec4 col4, out vec2 vf)
{
  vec2 cg;
  vf = vec2 (0.);
  if (idObj == idHull) {
    if (abs (qHit.x) < bDeck.x - 0.08 && qHit.y > -3.6 && qHit.z > - bDeck.z + 0.62) {
      col4 = vec4 (0.5, 0.3, 0., 0.1) * (0.5 +
         0.5 * SmoothBump (0.05, 0.95, 0.02, mod (8. * qHit.x, 1.)));
    } else if (qHit.y > -4.) {
      col4 = vec4 (0.7, 0.5, 0.1, 0.1);
      if (abs (qHit.z - 4.) < 0.25 && abs (qHit.y + 3.55) < 0.05) col4 *= 1.2;
      else if (qHit.z < -4. && abs (qHit.x) < 0.84 && abs (qHit.y + 3.62) < 0.125) {
        cg = step (0.1, abs (fract (vec2 (6. * qHit.x, 8. * (qHit.y + 3.62)) + 0.5) - 0.5));
        if (cg.x * cg.y == 1.) col4 = vec4 (0.8, 0.8, 0.2, 0.3);
        else col4 *= 0.8;
      } else {
        col4 *= 0.7 + 0.3 * SmoothBump (0.06, 0.94, 0.03, mod (8. * qHit.y, 1.));
        vf = vec2 (64., 0.3);
      } 
    } else if (qHit.y > -4.05) {
      col4 = vec4 (0.8, 0.8, 0.8, 0.1);
    } else if (qHit.y < -4.7) {
      col4 = vec4 (0.8, 0., 0., 0.1);
      vf = vec2 (64., 2.);
    } else {
      col4 = vec4 (0.3, 0.2, 0.1, 0.);
      vf = vec2 (64., 2.);
    }
  } else if (idObj == idRud) {
    col4 = vec4 (0.5, 0.3, 0., 0.);
  } else if (idObj == idStruc) {
    col4 = vec4 (0.4, 0.3, 0.1, 0.1);
    if (max (abs (qHit.x), abs (qHit.z + 0.22)) < 0.2) {
      cg = step (0.1, abs (fract (vec2 (10. * vec2 (qHit.x, qHit.z + 0.22)) + 0.5) - 0.5));
      if (cg.x * cg.y == 1.) col4 = vec4 (0.8, 0.8, 0.2, 0.3);
    }
  } else if (idObj == idSailT) {
    qHit.x *= (1. + 0.07 * qHit.y) * (1. + 0.14 * step (1., abs (qHit.z)));
    col4 = vec4 (1., 1., 1., 0.) * (0.7 + 0.3 * SmoothBump (0.06, 0.94, 0.02, mod (4. * qHit.x, 1.)));
  } else if (idObj == idSailA) {
    col4 = vec4 (1., 1., 1., 0.) * (0.7 + 0.3 * SmoothBump (0.06, 0.94, 0.02, mod (5. * qHit.z, 1.)));
  } else if (idObj == idSailF) {
    col4 = vec4 (1., 1., 1., 0.) * (0.7 + 0.3 * SmoothBump (0.06, 0.94, 0.02,
       mod (2.95 * qHit.y + 4. * qHit.z - 0.5, 1.)));
  } else if (idObj == idFlag) {
    col4 = vec4 (1., 1., 0.5, 0.1);
    if (abs (abs (length (qHit.yz) - 0.1) - 0.04) < 0.02) col4 = vec4 (0.8, 0.2, 0.2, 0.1);
  } else if (idObj == idMast) {
    col4 = vec4 (0.7, 0.4, 0., 0.1);
    if (length (qHit.xz) < 0.16 * (1. - 0.07 * qHit.y)) col4 *= 0.6 + 0.4 * SmoothBump (0.04, 0.96, 0.01,
       mod (2. * qHit.y / (1. + 0.14 * step (1., abs (qHit.z))), 1.));
  } else if (idObj == idSparT) {
    qHit.x *= (1. + 0.07 * qHit.y) * (1. + 0.14 * step (1., abs (qHit.z)));
    col4 = vec4 (0.7, 0.4, 0., 0.1) *  (0.6 + 0.4 * SmoothBump (0.08, 0.92, 0.01,
       mod (4. * qHit.x, 1.)));
  } else if (idObj == idSparL) {
    col4 = vec4 (0.7, 0.4, 0., 0.1);
  } else if (idObj == idRig) {
    col4 = vec4 (0.25, 0.2, 0.15, 0.);
    vf = vec2 (32., 0.5);
  }
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y < 0.001) {
    rd.y = 0.001;
    rd = normalize (rd);
  }
  q = 0.01 * (ro.xz + 0.5 * tCur + ((100. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.2, 0.8, ff);
  fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  skCol = vec3 (0.4, 0.5, 0.8);
  sd = max (dot (rd, sunDir), 0.);
  skCol = mix (skCol, vec3 (1., 1., 0.8), step (0.1, sd) *
     0.2 * pow (sd, 128.) + 0.8 * pow (sd, 2048.));
  col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  return col;
}

vec3 InShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, vh4;
  vec3 vn, col, watCol, row, rdw, rw, vnw;
  vec2 vf;
  float dstObj, dstWat, sh, hw;
  bool waterRefl;
  sailVel = vec3 (0., 0., 1.);
  dstWat = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  waterRefl = false;
  for (int k = VAR_ZERO; k < 2; k ++) {
    if (k == 0 || waterRefl) dstObj = ObjRay (ro, rd);
    if (k == 0) {
      waterRefl = (dstWat < min (dstFar, dstObj));
      if (waterRefl) {
        ro += dstWat * rd;
        vh4 = WaveNf (ro, dstWat);
        vnw = vh4.xyz;
        hw = vh4.w;
        row = ro;
        rdw = rd;
        rd = reflect (rd, vnw);
        ro += 0.01 * rd;
      }
    }
  }
  if (dstObj < min (dstWat, dstFar)) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    ShipCol (col4, vf);
    sh = waterRefl ? 1. : ObjSShadow (ro + 0.01 * vn, sunDir);
    if (vf.x > 0.) vn = VaryNf (vf.x * qHit, vn, vf.y);
    col = col4.rgb * (0.2 + 0.1 * max (dot (vn, normalize (vec3 (- sunDir.xz, 0.)).xzy), 0.) +
       0.8 * sh * max (dot (vn, sunDir), 0.)) + col4.a * step (0.95, sh) * pow (max (0.,
       dot (sunDir, reflect (rd, vn))), 32.);
  } else {
    col = SkyCol (ro, rd);
  }
  if (waterRefl) {
    rw = row + sailVel * tCur;
    vnw = VaryNf (rw, vnw, 1. - smoothstep (0.1, 0.4, dstWat / dstFar));
    watCol = mix (vec3 (0., 0.2, 0.4), vec3 (0., 0.15, 0.3),
       smoothstep (0.3, 0.7, Fbm2 (0.25 * rw.xz))) *
       (0.3 + 0.7 * (max (vnw.y, 0.) + 0.1 * pow (max (0., dot (sunDir, reflect (rdw, vnw))), 32.)));
    col = mix (watCol, 0.7 * col, pow (1. - abs (dot (rdw, vnw)), 5.));
    col = mix (col, SkyCol (row, rdw), smoothstep (0.7, 0.95, dstWat / dstFar));
  }
  if (monoImg) col = vec3 (1., 0.3, 0.7) * pow (Maxv3 (col), 1.2);
  return clamp (col, 0., 1.);
}

vec3 InMain (vec2 fCoord)
{
  mat3 vuMat;
  vec3 ro, rd;
  float el, az;
  az = 0.9 * pi;
  el = -0.05 * pi;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (-1., 3., -25.);
  rd = vuMat * normalize (vec3 (fCoord, 4.));
  sunDir = vuMat * normalize (vec3 (0.5, 1., -1.));
  return clamp (InShowScene (ro, rd), 0., 1.);
}

float ExObjDf (vec3 p)
{
  vec4 t;
  t = vec4 (mod (p.xy + 0.1, 0.2) - 0.1, abs (p.yx) - 1.21);
  return SmoothMax (PrBoxDf (p, vec3 (1.2, 1.2, 0.02)), 0.08 -
     min (length (t.xz), length (t.yw)), 0.01);
 }

float ExObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    d = ExObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ExObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 sz)
{
  vec3 v, tm, tp, u, vn;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    vn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return vec4 (dMin, vn);
}

vec3 ExShowScene (vec3 ro, vec3 rd)
{
  vec4 db4;
  vec3 col, vn;
  vec2 q;
  float dstExObj, s;
  col = vec3 (0.1, 0.12, 0.1);
  db4 = BlkHit (ro, rd, vec3 (1.55, 1.35, 0.15));
  if (db4.x < dstFar) {
    dstExObj = ExObjRay (ro, rd);
    if (dstExObj < dstFar) {
      ro += dstExObj * rd;
      vn = ExObjNf (ro);
      if (max (abs (ro.x), abs (ro.y)) < 1. && ro.z < -0.01) col = InMain (ro.xy);
      else col = vec3 (0.8, 0.2, 0.6) * (0.8 + 0.2 * Fbm2 (128. * ro.xy)) *
         (0.2 + 0.8 * max (dot (vn, ltDirEx), 0.));
      if (ro.z < -0.01) {
        q = ro.xy - vec2 (0.95, -0.95);
        s = abs (length (q) - 0.25) - 0.02;
        col = mix (vec3 (0.1), col, min (smoothstep (0.002, 0.005, abs (s)), 1. - step (s, 0.) *
           smoothstep (0.85, 0.9, mod (30. * (atan (q.y, q.x) / (2. * pi) + 0.5), 1.))));
      }
    }
  }
  if (db4.x < min (dstExObj, dstFar)) {
    vn = db4.yzw;
    col = mix (col, vec3 (1.) * (0.2 + 0.8 * max (dot (vn, ltDirEx), 0.)) +
       0.1 * pow (max (0., dot (ltDirEx, reflect (rd, vn))), 32.),
       0.1 + 0.9 * pow (1. - abs (dot (rd, vn)), 4.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  monoImg = false;
  if (mPtr.z > 0. && mPtr.x > 0.4 && mPtr.y < -0.4) monoImg = true;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0. && ! monoImg) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -9.);
  dstFar = 100.;
  ltDirEx = vuMat * normalize (vec3 (1., 1., -1.));
  EvalShipConf ();
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 6.));
    col += (1. / naa) * ExShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrEVCapsDf (vec3 p, vec4 u, float r)
{
  return length (p - clamp (dot (p, u.xyz), 0., u.w) * u.xyz) - r;
}

float PrEECapsDf (vec3 p, vec3 v1, vec3 v2, float r)
{
  return PrEVCapsDf (p - v1, vec4 (normalize (v2 - v1), length (v2 - v1)), r);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4f (float p)
{
  return fract (sin (p + vec4 (0., 1., 57., 58.)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

vec2 Noisev2v4 (vec4 p)
{
  vec4 ip, fp, t1, t2;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t1 = Hashv4f (dot (ip.xy, vec2 (1., 57.)));
  t2 = Hashv4f (dot (ip.zw, vec2 (1., 57.)));
  return vec2 (mix (mix (t1.x, t1.y, fp.x), mix (t1.z, t1.w, fp.x), fp.y),
               mix (mix (t2.x, t2.y, fp.z), mix (t2.z, t2.w, fp.z), fp.w));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

/*
    Code date: 2021-06-23
*/


#define SCREEN_D -800.0
#define AMOUNT_OF_PLASMA 15

float hash(float n) {
    float n2 = fract(n*5.18261);
    return fract(n2*n);
}

float hash(int n) {
    return hash(float(n));
}


float sdist(vec2 p, vec2 l0, vec2 l1) {
    vec2 v1 = l1 - l0;
    vec2 v2 = l0 - p;
    vec2 v3 = vec2(v1.y,-v1.x);
    return dot(v2,normalize(v3));
}


float dist(vec2 p, vec2 l0, vec2 l1) {
    return abs(sdist(p, l0, l1));
}




vec2 rotate(vec2 p, float a) {

    return vec2 (
        cos(a)*p.x - sin(a)*p.y,
        sin(a)*p.x + cos(a)*p.y);
}

vec2 irotate(vec2 p, float a) {
    return rotate(p, -a);
}

vec3 closestPoint(vec3 A, vec3 B, vec3 C) {
    vec3 d = (C - B) / distance(C,B);
    vec3 v = A - B;
    float t = dot(v,d);
    vec3 P = B + t * d;
    return P;
}

vec3 iproject(vec3 p)
{


    p.x -= iResolution.x/2.0;
    p.y -= iResolution.y/2.0;

    p.z -= 2000.0;
    p.x /= iResolution.y/675.0;
    p.y /= iResolution.y/675.0;

    p.x *= (SCREEN_D+p.z);
    p.y *= (SCREEN_D+p.z);

    p.x /= (SCREEN_D);
    p.y /= (SCREEN_D);


    p.z += 2000.0;
    p.z += 400.0;

    p.y += sin(iTime*.5)*200.0;
    p.x += cos(iTime*.35)*200.0;
    p.z += cos(iTime*.4)*300.0;
    p.yz = irotate(p.yz, sin(iTime*.3)*.04);
    p.xz = irotate(p.xz, cos(iTime*.3)*.02);
    p.yx = irotate(p.yx, cos(iTime*.5)*.02);

    p.yz =  irotate(p.yz,- 2.3+2.7+6.28*sin((iMouse.y)/iResolution.y));
    p.xz =  irotate(p.xz, 4.1+6.28*sin((iMouse.x)/iResolution.x));
    return p;

}

vec4 calcLight(vec3 p, vec3 ps) {
    float wx = max((5000.0 - p.x)/ps.x,(-5000.0 - p.x)/ps.x);
    float wy = max((5000.0 - p.y)/ps.y,(-5000.0 - p.y)/ps.y);
    float wz = max((5000.0 - p.z)/ps.z,(-5000.0 - p.z)/ps.z);
    float w = min(min(wx,wy),wz);
    p += ps*w;
    vec3 light = vec3(500,3000,2000);
    vec3 l = light-p;
    float ll = length(l);
    l = normalize(l);
    //box norm X
    if(abs(p.x) > max(abs(p.y),abs(p.z))) {
        vec3 norm = vec3(-p.x/abs(p.x),0,0);
        norm.x += (texture(iChannel0,.4*p.yz/iResolution.xy).b - texture(iChannel0,.4*vec2(p.y+.1,p.z)/iResolution.xy).b)*50.0;
        norm.y += (texture(iChannel0,.4*p.yz/iResolution.xy).b - texture(iChannel0,.4*vec2(p.y,p.z+.1)/iResolution.xy).b)*50.0;
        float bri = 2.0*dot(l,norm) / (ll*ll*.0000005);
        return clamp(texture(iChannel0,.4*p.zy/iResolution.xy)*bri,0.0,1.0);
    //box norm Y
    } else if (abs(p.y) > max(abs(p.x),abs(p.z))) {
        vec3 norm = vec3(0,-p.y/abs(p.y),0);
        norm.x += (texture(iChannel0,.4*p.xz/iResolution.xy).b - texture(iChannel0,.4*vec2(p.x+.1,p.z)/iResolution.xy).b)*50.0;
        norm.y += (texture(iChannel0,.4*p.xz/iResolution.xy).b - texture(iChannel0,.4*vec2(p.x,p.z+.1)/iResolution.xy).b)*50.0;
        float bri = 2.0*dot(l,norm) / (ll*ll*.0000005);
        return clamp(texture(iChannel0,.4*p.xz/iResolution.xy)*bri,0.0,1.0);
    //box norm Z
    } else {
        vec3 norm = vec3(0,0,-p.z/abs(p.z));
        norm.x += (texture(iChannel0,.4*p.xy/iResolution.xy).b - texture(iChannel0,.4*vec2(p.x+.1,p.y)/iResolution.xy).b)*50.0;
        norm.y += (texture(iChannel0,.4*p.xy/iResolution.xy).b - texture(iChannel0,.4*vec2(p.x,p.y+.1)/iResolution.xy).b)*50.0;
        float bri = 2.0*dot(l,norm) / (ll*ll*.0000005);
        return clamp(texture(iChannel0,.4*p.xy/iResolution.xy)*bri,0.0,1.0);
    }
}
float rmod(vec3 cp) {
    float asdf = sin(atan(cp.y,cp.z)*36.0);
    float asdf2 = sin(atan(cp.x,cp.z)*36.0);
    return (asdf+asdf2)*3.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float spec = 0.0;
    vec2 coord = fragCoord-.5;
    //coord = rotate(coord, sin(coord.x*.01)*.1);
    vec3 p = iproject(vec3(coord, 800));
    vec3 p2 = iproject(vec3(coord, -1000));
    vec3 ps = normalize(p2-p);
    float old_d = length(p);
    vec3 pg = vec3(0,0,0); //glass reflection
    vec3 psg = vec3(0,0,0);
    bool glass_reflect = false;

    vec3 pg2 = vec3(0,0,0); //glass reflection
    vec3 psg2 = vec3(0,0,0);
    bool glass_reflect2 = false;

        //glass sphere
    vec3 cp = closestPoint(vec3(0,0,0), p, p2);

    if( length(cp) > 800.0 && old_d > 800.0) {} else
    {
        float A = length(cp);
        float C = 800.0;
        float B = sqrt(C*C-A*A);
        p = cp - ps*B;
        //p+=rmod(normalize(p));
        spec += dot(reflect(ps, normalize(p)), normalize(vec3(500,8000,2000)));
        pg = p;
        glass_reflect = true;
        psg = reflect(ps,normalize(p));
        ps = refract(ps, normalize(p), 1.0/1.52);
        p+=ps*10.0;
        ps = refract(ps, normalize(p), 1.52/1.0);
    }


    bool glass_out = false;


    float val = 0.0;
    float r = sin(iTime)*700.0;
    //cast rays
    for (int i = 0; i <60; i++) {
        float d = length(p);

        //glass sphere
        if(!glass_out && d > 800.0 && old_d < 800.0) {
            vec3 cp = closestPoint(vec3(0,0,0), p, p+ps);
            float A = length(cp);
            float C = 800.0;
            float B = -sqrt(C*C-A*A);
            p = cp - ps*B; 
        //p+=rmod(normalize(p));
        pg2 = p;
        glass_reflect2 = true;
        psg2 = reflect(ps,normalize(-p));
            d=length(p);
            ps = refract(ps, normalize(-p), 1.0/1.52);
            p+=ps*10.0;
            ps = refract(ps, normalize(-p), 1.52/1.03); 
            glass_out = true;
            //i=50;
            break;
        }


        float dist = 100.0;
        //check ray against plasma
        for (int j = 0; j < AMOUNT_OF_PLASMA; j++) {

            float more = clamp(100.0 - (800.0 - d), 0.0, 100.0);
            more /= 100.0;
            more *= more;
            more *= more;
            more *= 50.0;

            vec3 lps = vec3(0,1,0);
            float h = hash(j);
            float time = iTime+float(j)*100.0;
            //float h2 = fract(h*8.1731);
            lps.xy = rotate(lps.xy,(h-.5)*6.28*time*.087+cos((d+more)/150.0-h*time*3.5)*.15);
            lps.yz = rotate(lps.yz,(h-.5)*6.28*time*.14+cos((d+more)/150.0-h*time*2.0)*.15);

            lps = normalize(lps);
            vec3 a1 = d*lps;
            if(d <= 801.0) {
                dist = min(dist,distance(p,a1));
                float v = clamp((35.0+more)-distance(p,a1), 0.0, 35.0);
                v += clamp(5.0 - (d - 60.0),0.0,20.0)*.05;
                //v+= v*clamp(100.0 - (800.0 - d), 0.0, 100.0)*.05;
                if(d < 100.0) v -= v*(100.0-d)/100.0;
                val += v;
            } else {
                break;
            }
        }
        old_d = d;
        if(d < 50.0) { //small sphere in the middle
            ps = vec3(0,0,0);
            break;
        }
        p+=ps*max(min(dist*.5,clamp(808.0-d,0.0,1000.0)),5.0);
    }

    //val += clamp(50.0-distance(p,vec3(0,0,0)), 0.0, 50.0)*100.0;
    val /= 85.0;
    vec3 col = vec3((val-1.0)*.5,(val-2.0)*.15,val);
    col = clamp(col, 0.0, 1.0);



    fragColor = calcLight(p,ps)*vec4(1,.8,.7,1);
    if(glass_reflect) fragColor += calcLight(pg,psg)*.3*vec4(1,.8,.7,1); //outer reflection
    if(glass_reflect2) fragColor += calcLight(pg2,psg2)*.1*vec4(1,.8,.7,1); //inner reflection

    p.x+=iTime*100.0;
    fragColor += vec4(col,1.0);
    spec=clamp(spec,0.0,1.0);
    spec*=spec;
    spec*=spec;
    spec*=spec;
    spec*=spec;
    spec*=spec;
    spec*=spec;
    spec*=spec;
    spec*=spec;
    fragColor += spec*vec4(1,.8,.7,1);
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0/2.2));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cc = fragCoord - iResolution.xy / vec2(2);
    float l = length(cc); //max(abs(cc.x), abs(cc.y));
    float scale = log(l) / l * 10.0 * (1.5 + sin(iTime));
    vec2 c = cc * scale;
    fragColor = any(equal(ivec2(c) % ivec2(10), ivec2(0))) ? vec4(1) : vec4(0);
    if (length(c) < 4.0) fragColor = vec4(0, 1, 0, 1);
}

//	FXAA (autor: Mudlord).
void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    vec2  p         = fragCoord.xy/iResolution.xy;
    vec2  pp        = 1.0 / iResolution.xy;
    vec4  color     = texture(iChannel0, vec2(fragCoord.xy * pp));
    vec3  luma      = vec3(0.299, 0.587, 0.114);
    float lumaNW    = dot(texture(iChannel0, (fragCoord.xy + vec2(-1.,-1.)) * pp).xyz, luma);
    float lumaNE    = dot(texture(iChannel0, (fragCoord.xy + vec2( 1.,-1.)) * pp).xyz, luma);
    float lumaSW    = dot(texture(iChannel0, (fragCoord.xy + vec2(-1., 1.)) * pp).xyz, luma);
    float lumaSE    = dot(texture(iChannel0, (fragCoord.xy + vec2( 1., 1.)) * pp).xyz, luma);
    float lumaM     = dot(color.xyz, luma);
    float lumaMin   = min(lumaM, min(min(lumaNW, lumaNE), min(lumaSW, lumaSE)));
    float lumaMax   = max(lumaM, max(max(lumaNW, lumaNE), max(lumaSW, lumaSE)));

    vec2  dir       = vec2(-((lumaNW + lumaNE) - (lumaSW + lumaSE)), ((lumaNW + lumaSW) - (lumaNE + lumaSE)));
    float dirReduce = max((lumaNW + lumaNE + lumaSW + lumaSE) * (0.25 * (1.0/8.0)), (1.0/128.0));
    float rcpDirMin = 2.5 / (min(abs(dir.x), abs(dir.y)) + dirReduce);
    dir = min(vec2(8.0, 8.0), max(vec2(-8.0, -8.0), dir * rcpDirMin)) * pp;

    vec3 rgbA = 0.5 * 
    (
        texture(iChannel0, fragCoord.xy * pp + dir * (1. / 3. -.5)).xyz +
        texture(iChannel0, fragCoord.xy * pp + dir * (2. / 3. -.5)).xyz
    );
    vec3 rgbB = rgbA * 0.5 + 0.25 * 
    (
        texture(iChannel0, fragCoord.xy * pp + dir *-.5).xyz +
        texture(iChannel0, fragCoord.xy * pp + dir * .5).xyz
    );

    float lumaB = dot(rgbB, luma);
    fragColor = vec4((lumaB < lumaMin) || (lumaB > lumaMax) ? rgbA : rgbB, color.w);

    fragColor*= (1. / pow(1. + pow(length(p*2.-1.) * .3,  3.), 75.));  // Vignetting.
    fragColor = pow((((fragColor * (.15 * fragColor + .05) + .004) / (fragColor * (.15 * fragColor + .50) + .06)) - .0667) / .6 * 3.1, vec4(.45)); // Tonemapping & Gamma correction.
}

