float rand(vec2 uv) { return fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453); }

// Palette function by iq, see more at https://www.shadertoy.com/view/ll2GD3.
vec3  pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) { return a + b*cos(6.28318*(c*t + d)); }
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{   

    float tTime = floor(iTime/.02)*.02; // Drops down the perceived frame rate.
    vec2  uv  = fragCoord / iResolution.xy;
	vec4  col = vec4(0); 
    ivec2 wx  = ivec2(fragCoord.x, fragCoord.y); // Local fragment coordinate.
	int   chi = int(floor(tTime * 20.));// Static text character index.

    // Writes out the starfield state.
	vec4 sfx = vec4(0);
    if ((wx.y == VAR_Y_POS) && (wx.x < NUM_STARS))
	{
    	if (0 == iFrame)
	    {//	Initializes starfield data.
			col = vec4(rand(uv), rand(uv + fragCoord.xx), float(wx.x) / float(NUM_STARS), 0);
    	}
	    else
    	{// Updates the starfield data.
            vec4 displacement = vec4(sin(tTime * 1.0/0.9) * .01, sin(tTime * 1.0/1.32) * .02, sin(iTime * .25) * .01 ,0.);
			col = texture(iChannel0, uv);
			col+= displacement * clamp(col.z, .3, 1.);
			col = mod(col,1.);            
	    }
	}

    // Oldskool way of "starfielding". Really dirty but didn't want an actual 3D approach.
    if ((wx.y < (SCR_HEIGHT - CHR_HEIGHT)) && (wx.x < SCR_WIDTH))
    {
        for (int i = 0; i < NUM_STARS; ++i)
		{
            vec2 ruv = vec2(float(i) / iResolution.x, float(VAR_Y_POS + 1) / iResolution.y);
			vec4 rstate = texture(iChannel0, ruv);

            //	Simulates depth.
            rstate.xy = rstate.xy * 2.  - 1. ;
			rstate.xy*= clamp(rstate.z,.7,2.);
			rstate.xy = rstate.xy * .5  + .5 ;
			if ((int(rstate.x * float(SCR_WIDTH)) == wx.x) && (int(rstate.y*float(SCR_HEIGHT)) == wx.y))
			{
			    sfx = mix(vec4(.1), vec4(1), pow(rstate.z, 2.5));
			    break;
			}            
		}
    }

    // Top Copper-like scanline.
	if ((wx.x < SCR_WIDTH) && (wx.y == 1))
	    col = vec4(pal(uv.x + tTime*.75, vec3(.5), vec3(5), vec3(1), vec3(.0,.33,.67)), 1);

    // Static text.
    wx.y +=-8;    
	if ((wx.y >= 0) && (wx.y < (CHR_HEIGHT * kStaticText.length() / TXT_WIDTH)))
    {
		int x = wx.x / CHR_WIDTH ;
		int y = wx.y / CHR_HEIGHT;
        int i = y * TXT_WIDTH + x;

        if ((x < TXT_WIDTH) && (y < TXT_HEIGHT) && (chi > i))
        {
            vec4 mul = mix(vec4(1), vec4(
			    kStaticTextScanColors[wx.y*3    ],
			    kStaticTextScanColors[wx.y*3 + 1],
			    kStaticTextScanColors[wx.y*3 + 2],
			256)/ 256., min(1.,float(chi - i) * (tTime * 10.) - float(chi)));
			col = vec4(charPixel(wx, kStaticText[i])) * mul;
        }
    }

    // Scrolling text buffer.
    if ((wx.y >= SCR_HEIGHT) && (wx.y < SCR_HEIGHT+CHR_HEIGHT))
    {
	    ivec2 zx = wx;
        zx.x  = zx.x + int(tTime * float(XCR_SPEED));
		int x = zx.x / CHR_WIDTH ;
		int y = (zx.y) / CHR_HEIGHT - CHR_HEIGHT * TXT_HEIGHT;
        if (((int(fragCoord.x) / CHR_WIDTH) < TXT_WIDTH) && (y < TXT_HEIGHT))
        	col = vec4(charPixel(ivec2(mod(vec2(zx), vec2(CHR_WIDTH, 0))), kScrollingText[x % kScrollingText.length()]));
    }    

    // Scrolling remap to the viewport area.
    if ((wx.x < SCR_WIDTH) && (wx.y >= CHR_HEIGHT*XCR_MIN_Y) && (wx.y <= CHR_HEIGHT*XCR_MAX_Y+CHR_HEIGHT))
    {
		float vv  = ((fragCoord.y) - float(CHR_HEIGHT*XCR_MIN_Y-CHR_HEIGHT)) / float((XCR_MAX_Y - XCR_MIN_Y) * CHR_HEIGHT);
		vec4  mul = pow(vec4(.4, 0, 0, 1) * (     sin(vv * kPi * 2.)), vec4(1.0))
	              + pow(vec4(.6, 0, 0, 1) * (1. - sin(vv * kPi * 2.)), vec4(0.7))
            	  + pow(vec4(.6,.4,.2, 1) * (1. - sin(vv * kPi * 2.)), vec4(1.5));

        vec2 fc   = fragCoord;
		fc.x*=.5;
		fc.y = float(SCR_HEIGHT + CHR_HEIGHT) 
		     + ((fragCoord.y) - float(CHR_HEIGHT*XCR_MIN_Y+CHR_HEIGHT))*.5 
             + (sin(tTime + tTime + tTime + tTime - fragCoord.x / float(SCR_WIDTH) * kPi * 1.5) * .25 - .25) * float((XCR_MAX_Y - XCR_MIN_Y - 1) * CHR_HEIGHT);
		if (fc.y >= float(SCR_HEIGHT+ CHR_HEIGHT) && fc.y <= float(SCR_HEIGHT + CHR_HEIGHT+ CHR_HEIGHT))
        {        
			fc.y  = fc.y;
			col   = texture(iChannel0, fc / iResolution.xy) * mul;
        }
    }

    // Bottom Copper-like scanline.
    wx.y += 8;
