	if ((wx.y >= 0) && (wx.y < 5) && (wx.x >= 0) && (wx.x < kCreditsSprite.length()/5))
    	col = vec4(kCreditsSprite[wx.y*kCreditsSprite.length()/5 + wx.x]);

