const float i_kEpsilon   =.005 ; // Epsilon value.
const float i_kFov       = 2.5 ; // FOV.
const int   i_kSteps     = 64 ; // Raymarching steps.
const int   i_kStepsSec  = 32 ; // Raymarching steps for secondary rays.
const int   i_kStepsTri  = 32 ; // Raymarching steps for tertiary rays.
const float i_kNear      = 0.01  ; // Distance to the near plane.
const float i_kFar       = 100.; // Distance to the far  plane.
const float i_kPi        = 3.1415927;
