// This cracktro is my favourite of all times (the music by 4mat is probably the actual reason!!!).
// With this little tribute I wanted to make a homage to all Amiga cracktros that introduced me into the demoscene. 

// Image: composes the image. Uses Buffer B for mipmapping, Buffer C for pixel-perfect sampling. Composes the image
// by mixing different mipmap levels onto the original image, then applies some post-processing.

// Uncomment SHOW_ORIGINAL_RASTERPORT to watch the rasterport without any postprocessing.
// #define SHOW_ORIGINAL_RASTERPORT

// The CRT tube distortion is driven by this parameter.
#define CRT_FACTOR 1.1

// Taken from: http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// Taken from: http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImageSkidRow(out vec4 fragColor, in vec2 uv)
{
    vec4 col = vec4(0);
    float v  = 1.;

    // Fakes a CRT surface.
#	ifndef SHOW_ORIGINAL_RASTERPORT    
    uv = uv * 2. - 1.;
    v  = length(uv);
    uv = normalize(uv) * pow(v, CRT_FACTOR);
    uv = uv * .5 + .5;
#   endif//SHOW_ORIGINAL_RASTERPORT    

    // Fake brightness using mipmaps.
#	ifndef SHOW_ORIGINAL_RASTERPORT    
    col+= clamp(pow(textureLod(iChannel2, uv, 4.), vec4(.1)), 0., 1.) * .01;
    col+= clamp(pow(textureLod(iChannel2, uv, 3.), vec4(.1)), 0., 1.) * .01;
    col+= clamp(pow(textureLod(iChannel2, uv, 2.), vec4(.1)), 0., 1.) * .01;
	col+= clamp(textureLod(iChannel2, uv, 1.), 0., 1.) * .16;
#   endif//SHOW_ORIGINAL_RASTERPORT    
	col+= texture(iChannel3, uv);	

    vec2 coord = uv * vec2(SCR_WIDTH, SCR_HEIGHT);
    vec2 coord2= coord;
#   ifndef SHOW_ORIGINAL_RASTERPORT
    coord+= vec2(iTime * 25.);
#   endif//SHOW_ORIGINAL_RASTERPORT

	// Subpixel pattern.
#	ifndef SHOW_ORIGINAL_RASTERPORT    
    col*= vec4(mix(1.2, 1. - mod(coord.x     + coord.y, 3.), .12),
	           mix(1.2, 1. - mod(coord.x + 1.+ coord.y, 3.), .12),
    	       mix(1.2, 1. - mod(coord.x + 2.+ coord.y, 3.), .12), 1.);
#   endif//SHOW_ORIGINAL_RASTERPORT    

    // Some HSV correction.
#	ifndef SHOW_ORIGINAL_RASTERPORT    
    col = vec4(hsv2rgb(rgb2hsv(col.xyz) * vec3(.95,.95,.75)), 1);
#   endif//SHOW_ORIGINAL_RASTERPORT    

    // Scanlines & vignetting.
#	ifndef SHOW_ORIGINAL_RASTERPORT    
	col*= vec4(mix(1.2, 1. - mod(coord.y, 2.), .12));
    col*= max (.9, 1. - pow(v,5.));
#   endif//SHOW_ORIGINAL_RASTERPORT    

    fragColor = col;
}

//  Gets an N-dimensional random number using given argument as seed for the calculation.
//  @in     n       Random seed.
//  @return Resulting value.
float hashi(ivec2 n) { return float((0x3504f333 * n.x * n.x + n.y) * (0xf1bbcdcb * n.y * n.y + n.x)) * (2. / 8589934592.) +.5; }
float hashf(float n) { return hashi(ivec2(n)); }
vec3  hash3(vec2  p) { return fract(sin(vec3(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)), dot(p, vec2(419.2, 371.9)))) * 43758.5453); }

float nrand( vec2 n )
{
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}
float n4rand_ss( vec2 n )
{
	float nrnd0 = nrand( n + 0.07*fract( 123.456 ) );
	float nrnd1 = nrand( n + 0.11*fract( 123.456 + 0.573953 ) );	
	return 0.23*sqrt(-log(nrnd0+0.00001))*cos(2.0*3.141592*nrnd1)+0.5;
}

float blugausnoise2(vec2 c1) {
    float nrand1 = n4rand_ss(c1);
    float nrand0 = n4rand_ss(vec2(c1.x- 1.,c1.y));
    float nrand2 = n4rand_ss(vec2(c1.x+ 1.,c1.y));
    return 2.0* nrand1- 0.5* (nrand0+ nrand2);
}

vec4 tex_triplanar(sampler2D texID, vec3 pos, vec3 nor, vec2 bias, float k)
{
    vec3 m = pow(abs(nor), vec3(k));
    m   /= m.x + m.y + m.z;
    // Triplanar first component shall be inferred from pos.yz. Changed to pos.zy to make it work with our textures.
    return  (texture(texID, pos.zy + bias.yx) * m.x + texture(texID, pos.zx + bias.xx) * m.y + texture(texID, pos.xy + bias.xy) * m.z);
}

//  Gets material information for the given ID and world-space data.
