//  Gets light information for the given ID and world-space data.
//  @in     litID   Light ID.
//  @in     pos     World-space position.
//  @return The light material for the given input data.
//  Lights are set up using a mat4 instance. Output light structure is as follows:
//      [0] Light position or direction  (XYZ); light type (W), either spot (0) or directional (1).
//      [1] Light color (XYZ), attenuation (W).
//      [2] -
//      [3] (auto) Light direction (XYZ).
mat4 getLightInfo(int litID, vec3 pos)
{
	mat4 lit = mat4(0);
    if (0 == litID)
    {
		lit[0] = vec4(3,5, 3,0);
        lit[1] = vec4(0);
    }
    lit[3].xyz = mix(normalize(lit[0].xyz - pos), lit[0].xyz, lit[0].w);

	return  lit;
}
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}
float sdSphere(vec3 p, float s) { return length(p) - s; }
float sdBox   (vec3 p, vec3  b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

vec2 pMod2(inout vec2 p, vec2 size) {
	vec2 c = floor((p + size*0.5)/size);
	p = mod(p + size*0.5,size) - size*0.5;
	return c;
}

float sdChair(vec3 p)
{
    float a = sdBox(p,vec3( .25,.01, .25));
    a = min(a,sdBox(p+vec3(0, -.37, -.25),vec3(.25,.15,.02)));
    a = min(a,sdBox(p+vec3( .25,.00,-.25),vec3(.01,.50,.01)));
    a = min(a,sdBox(p+vec3(-.25,.00,-.25),vec3(.01,.50,.01)));
    a = min(a,sdBox(p+vec3(-.25,.42, .25),vec3(.01,.42,.01)));
    a = min(a,sdBox(p+vec3( .25,.42, .25),vec3(.01,.42,.01)));
    return a;
}

#define MIN(dst, src)   dst = mix(src, dst, step(dst.x, src.x))
vec2 mapScene(vec3 pos)
{
    vec2 a = vec2(i_kFar), b = vec2(i_kFar);

    float q = floor(mod(iTime*.20, 4.));

    b = vec2(max(-sdBox(pos-vec3(0,4,0), vec3(5)), sdBox(pos, vec3(100))), 8); MIN(a, b);
    b = vec2(sdBox(pos+vec3(0,.99,0), vec3(100,.01,100)), 0); MIN(a, b);
    b = vec2(sdBox(pos+vec3(0,0,4.99), vec3(5,5,.01)), 2); MIN(a, b);

    pos.y+= .5;
    vec2 c = pMod2(pos.xz, vec2(1, 3));
    pR(pos.xz, hashi(ivec2(c))*.25);
    b = vec2(sdChair(pos), 0); MIN(a, b);

    return	a;
}

//  Gets a rotation matrix within given direction.
//  @in     dir     Normalized direction in scene center, ground domain (up = [0,1,0]).
//  @in     upvec   Bias to apply to the up vector to avoid gimbal lock.
//  @return The resulting rotation matrix.
mat3 rotationMatrix(vec3 dir, vec3 upvec)
{
    vec3 cu = normalize(cross(dir , upvec));
    return  mat3(cu, normalize(cross(cu, dir)), dir);
}

//  Calculates the ray direction to draw pixel at the given viewport UV coordinates from the given world-space position.
//  @in     ro      World-space position within the ray starts from.
//  @in     rd      World-space position within the ray ends.
//  @in     uv      Viewport UV coordinates.
//  @return The resulting ray direction.
vec3 getCameraDir(vec3 ro, vec3 rd, vec2 uv)
{
    return rotationMatrix(normalize(rd - ro), vec3(i_kEpsilon, 1., i_kEpsilon)) 
        * normalize(vec3 (uv * vec2(iResolution.x / iResolution.y, 1.), i_kFov));
}

//  Gets square of "x".
//  @in     x       Value to get square of.       
//  @return Square of "x".
float sq(float x) { return x * x; }

//  Gets normal vector within a given hit.
//  @in     pos     World position where hit was found.
//  @return Normal vector within the hit.
#define ZERO min(iFrame,0)
vec3  computeNormal(const vec3 pos)
{
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*mapScene(pos+0.0005*e).x;
    }
    return normalize(n);
}

float ao(vec3 ro, vec3 rd, float kk)
{
    float k = 1.0;
    float d = 0.0;
    float occ = 0.0;
    for(int i = 0; i < 32; i++){
        d = mapScene(ro + 0.1 * k * rd).x;
        occ += 1.0 / pow(2.0, k) * (k * 0.1 - d);
        k += 1.0;
    }
    return 1.0 - clamp(kk * occ, 0.0, 1.0);
}

//  Casts a ray along the given direction until a hit is found. Returns a vec4() with distance (X), material ID (Y). 
//  Uses "over-relaxation raymarching" and "screen-space aware intersection point selection" algorithms depicted at
//  the  "Enhanced Sphere Tracing" paper by Benjamin Kelinert et al.
//  @in     ro      Ray origin.
//  @in     rd      Ray direction.
//  @in     steps   Maximum ray iterations.
//  @return Raycast result. A vec4() with total distance (X), last step distance (Y), material ID (Z), steps (W).
vec4 castRay(vec3 ro, vec3 rd, int steps)
{
    float t   = i_kNear, stepLength = 0., prevRad = i_kFar, prevErr = i_kFar, err, k = 1.2;
    vec2  res = vec2(1);
    for (; (steps >= 0) && (t < i_kFar); --steps)
    {
        res = mapScene(ro + rd * t);
        bool  sor = (k > 1.) && ((prevRad + res.x) < stepLength);
        if (sor)
        {
            stepLength-= k * stepLength;
            k = 1.;            
        }        
        else
            stepLength = res.x * k;

        prevRad = res.x;
        err = res.x / t;
        if (!sor)
        {
            if (err < prevErr     ) { res.x = t; prevErr = err; }
            if (err < gTexelRadius) break;
        }
        t  += stepLength;
    }
    return vec4(t, res.xy, steps);
}

//  Lits given point.
//  @in     pos     Surface position to lit.
//  @in     nor     Normal  vector to the surface position to lit.
//  @in     rd      Ray direction.
//  @in     mat     The material descriptor to use.
//  @return Surface color at that point.
vec4 shadeFragment(vec3 pos, vec3 nor, vec3 rd, mat4 mat)
{
    vec3  col    = vec3(1), diffuse;
    vec4  amb    = vec4(0), res;
    float weight = 0.;

    mat4 lit = getLightInfo(0, pos);
    diffuse  = mix(mat[0].xyz * clamp(dot(mat[3].xyz, lit[3].xyz), 0., 1.) * (1. - mat[1].z), mat[0].xyz, mat[0].w);

    //  Calculates the Cook-Torrance BRDF terms for a particular point in the 3D space within the given material setup  
    //  and  light direction.
    //  BRDF function terms used are:
    //          * D term (isotropic  ) - GGX.
    //          * F term (reflectance) - Schlick.
    //          * G term (geometric  ) - Schlick approximation of Smith solved with GGX.
 	vec3  h         = normalize   (-rd + lit[3].xyz);
   	float rsq       = max  (.001, sq(mat[1].x));
    vec3  terms     = vec3
    (
        /* D Term    */ (rsq * rsq / (i_kPi * sq((sq(rsq) - 1.) * sq(clamp(dot(mat[3].xyz, h), 0., 1.)) + 1. ))),   // Isotropic GGX NDF. 
        /* F Term    */ (mat[1].y + (1. - mat[1].y  ) * (pow(1. -    clamp(dot(mat[3].xyz, lit[3].xyz), 0., 1.),  5.))),
        /* G Term    */ sq(1. / (dot(h , lit[3].xyz) * (1. - rsq * .5) + rsq * .5))
    );
	col = diffuse * col * terms.y + mix(lit[1].xyz, vec3(1), mat[0].w) * diffuse;

    //  Integrates result with ambient light.
    col    += mat[0].xyz * i_kAmbientColor * i_kAmbientLevel;
    return  vec4(col, terms.y);
}

//  Gets a rotation matrix within given direction.
//  @in     dir     Normalized direction in scene center, ground domain (up = [0,1,0]).
//  @in     upvec   The up-vector.
//  @return The resulting rotation matrix.
mat3 rotation_matrix(vec3 dir, vec3 upvec)
{
    vec3 cu = normalize(cross(dir , upvec));
    return  mat3(cu, normalize(cross(cu, dir)), dir);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
