		mat         = getMaterialInfo(int(res.z), pos, nor);
		col+= att*dif*mix(mat[0].xyz * clamp(dot(mat[3].xyz, d2), 0., 1.) * (1. - mat[1].z), mat[0].xyz, mat[0].w).xyzz*((3.-float(i))/3.);
		att*=att2;
    }
    fragColor = mix(fragColor, col, .5);    

	pos=p2;nor=n2;ro=r2;rd=d2;
	col = vec4(0);
    att=1.;

    for (int i = 0; srcMat != 2 && i < 2; ++i)
    {
	    ro = pos + nor * .001;
	    rd = reflect(rd, nor);
		rd*= mix(vec3(1),abs(hash3(vec2(float(iTime*.01) / 512., hash3(uv + iTime*.01)))),aa)*1.-.5;
	    rd = normalize(rd);
	    res = castRay(ro, rd, i_kStepsTri);
