	    col+= att*mix(col, (mix(vec4(1),dif2,metallic))*sf*pdf,.5)*((3.-float(i))/3.);
	    pdf = sf.a;
		aa = mat[1].x * mat[1].x;
		att*=att2;
    }
    float m=((2==srcMat)?.7:.05);
    fragColor = mix(fragColor, col, .5)*m+texture(iChannel0,fragCoord.xy/iResolution.xy)*(1.-m);    
}

