void mainImage(out vec4 C, in vec2 O)
{
    vec2 f = vec2(
        10., // seconds
        12. * 10. // half-tones
    ) / iResolution.xy;
    float t = min(iTime, t_disk) + t_offset;
    vec2 d = vec2(
        t - 1.,
        0
    );

    C = vec4(0);
    if (abs(O * f - 1.).x < .6 * f.x)
        C = vec4(1);

    vec2 o = O * f + d;
    for (int i=min(0,iFrame) ; i<n_notes ; ++i)
    {
        vec4 note = notes(i);
        if (note.w == -1.)
            continue;
        if (note.x < o.x && o.x < note.x + note.z && int(o.y) == int(note.y))
        {
            if (note.x + note.z < t)
                C = vec4(0,0,1,1);
            else if (note.x < t)
                C = vec4(0,1,0,1);
            else
                C = vec4(1,0,0,1);
        }
    }

    if (iTime > t_disk)
    {
        C = mix(C, vec4(.5,.5,.5,1), .5);
        f = vec2(1. / iResolution.y);
        vec2 uv = (O - iResolution.xy / 2.) * f;
        uv = abs(uv);
        uv.x = abs(uv.x - .2);
        C = mix(C, vec4(1), float(uv.x < 0.1 && uv.y < .3));
    }
}

