float pitch(float i)
{
    return 440. * pow(2., (i - 60.) / 12.);
}

vec2 wave(float f, float a, float time)
{
    float w = 2. * pi * f * time;
    //float v = pow(abs(sin(w / 2.)), 8.) - .5;
    float v = pow(max(0., (sin(w) + 1.) / 2.), 8.) - .5;
    return vec2(a * v);
}

vec2 mainSound(int samp, float time)
{
    vec2 sound = vec2(0);

    float t = time + t_offset;
    for (int i=min(0, int(iSampleRate)) ; i<n_notes ; ++i)
    {
        vec4 note = notes(i);
        if (note.w == -1.)
            continue;
        if (note.x < t && t < note.x + note.z)
        {
            float f = pitch(note.y);
            float a = smoothstep(0., .05, t - note.x) * smoothstep(.0, -.05, t - (note.x + note.z));
            sound += wave(f, .05 * a * clamp(800. / f, 1., 10.), time);
        }
    }

    return sound;
}

