// Closest point on a capsule. For closest points on other primitives, check
//
//    https://www.shadertoy.com/playlist/wXsSzB



vec3 closestPointToTorus( vec3 p, float ra, float rb )
{
    float l = length(p.xz);
    vec3  q = vec3(ra*p.x/l,0.0,ra*p.z/l);
    return q + rb*normalize(p-q);
}

//------------------------------------------------------------

// https://iquilezles.org/articles/distfunctions
float sdTorus( vec3 p, float ra, float rb )
{
    return length( vec2(length(p.xz)-ra,p.y) )-rb;
}

// https://iquilezles.org/articles/distfunctions
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

// https://iquilezles.org/articles/distfunctions
float sdSphere( vec3 p, vec3 cen, float rad )
{
    return length(p-cen)-rad;
}

//------------------------------------------------------------
const float ra = 0.9;
const float rb = 0.25;
vec2 map( in vec3 pos, in bool showSurface, in vec3 samplePoint )
{


    // compute closest point to gPoint on the surace of the capsule
    vec3 closestPoint = closestPointToTorus(samplePoint, ra, rb );

    // point
    vec2 res = vec2( sdSphere( pos, samplePoint, 0.06 ), 1.0 );

    // closest point
    {
    float d = sdSphere( pos, closestPoint, 0.06 );
    if( d<res.x ) res = vec2( d, 4.0 );
    }

    // shape
    if( showSurface )
    {
    float d = sdTorus( pos, ra, rb );
    if( d<res.x ) res =  vec2( d, 5.0 );
    }

    // segment
    {
    float d = sdCapsule( pos, samplePoint, closestPoint, 0.015 );
    if( d<res.x ) res =  vec2( d, 4.0 );
    }

    return res;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos, in bool showSurface, in vec3 samplePoint )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps, showSurface, samplePoint ).x + 
					  e.yyx*map( pos + e.yyx*eps, showSurface, samplePoint ).x + 
					  e.yxy*map( pos + e.yxy*eps, showSurface, samplePoint ).x + 
					  e.xxx*map( pos + e.xxx*eps, showSurface, samplePoint ).x );
}

// https://iquilezles.org/articles/rmshadows
float calcSoftShadow( vec3 ro, vec3 rd, in bool showSurface, in vec3 samplePoint )
{
    float res = 1.0;
    const float tmax = 2.0;
    float t = 0.001;
    for( int i=0; i<64; i++ )
    {
     	float h = map(ro + t*rd, showSurface, samplePoint).x;
        res = min( res, 64.0*h/t );
    	t += clamp(h, 0.01,0.5);
        if( res<-1.0 || t>tmax ) break;

    }
    res = max(res,-1.0);
    return 0.25*(1.0+res)*(1.0+res)*(2.0-res); // smoothstep, in [-1,1]
}

