      //bool showSurface = ((samp.x+samp.y)&1)==0;     // 50% opaque
        bool showSurface = ((samp.x&1)+(samp.y&1))!=0; // 75% opaque

        // animate camera
        float an = 0.25*time + 6.283185*iMouse.x/iResolution.x;
        vec3 ro = vec3( 2.4*cos(an), 0.7, 2.4*sin(an) );
        vec3 ta = vec3( 0.0, 0.0, 0.0 );

        // camera matrix
        vec3 ww = normalize( ta - ro );
        vec3 uu = normalize( cross(ww,vec3(0.2,1.0,0.0) ) );
        vec3 vv = normalize( cross(uu,ww));

        // animate point
        vec3 samplePoint = sin(-time*0.8*vec3(1.0,1.1,1.1)+vec3(2.0,1.0,0.0));

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raycast
        const float tmax = 5.0;
        float t = 0.0;
        float m = -1.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            vec2 hm = map(pos,showSurface,samplePoint);
            m = hm.y;
            if( hm.x<0.0001 || t>tmax ) break;
            t += hm.x;
        }

        // shade background
        vec3 col = vec3(0.05)*(1.0-0.2*length(p));

        // shade objects
        if( t<tmax )
        {
            // geometry
            vec3  pos = ro + t*rd;
            vec3  nor = calcNormal(pos,showSurface,samplePoint);

            // color
            vec3  mate = 0.5 + 0.45*cos( m + vec3(0.0,1.0,1.5) );
