#define sat(a) clamp(a,0.,1.)
mat2 r2d(float a){float ca = cos(a),sa=sin(a);return mat2(ca,-sa,sa,ca);}
#define PI 3.14159265

float _sqr(vec2 p, vec2 s)
{
    vec2 l = abs(p)-s;
    return max(l.x, l.y);
}
float _cir(vec2 p, float r)
{
return length(p)-r;
}

float _bbox(vec3 p, vec3 s)
{
  vec3 l = abs(p)-s;
//  l = abs(l)-s*.9;
  return max(l.x,max(l.y,l.z));
}

vec2 _min(vec2 a, vec2 b)
{
  if(a.x<b.x)
  return a;
  return b;
}

float _sph(vec3 p, float r)
{
  return length(p)-r;
}

vec4 ovnip[3];

float ovni(vec3 p)
{
  float acc = 1000.;
  acc = min(acc, max(length(p)-2.,p.y+1.5));

  acc= min(acc,max(length(p.xz)-1.3,abs(p.y+1.1)-.4));

  vec3 pc = p-vec3(0.,2.9,0.);
  acc = min(acc, max(length(pc)-4.,pc.y+3.5));

 // float a = atan(p.x,p.z);

  float repa = 2.*PI/3.;

  for(int i = 0;i<3;++i)
  {
    float a = float(i)*repa+iTime;
    float r = 1.2;
    vec3 pos = p+vec3(sin(a)*r,0.55,cos(a)*r);
    acc = min(acc, _sph(pos,.25));
  }
  acc = min(acc, _sph(p-vec3(0.,-.6,0.),.5));
  return acc;
}

vec2 map(vec3 p)
{
  vec2 acc = vec2(1000.,-1.);

  for (int i = 0;i<3;++i)
  {
    float a = ovnip[i].w;
    vec3 pos = p-ovnip[i].xyz;
    vec3 opos = pos;
    pos.xy*=r2d(a);
    acc = _min(acc, vec2(ovni(pos),float(i)));

    vec3 dest = vec3(0.,-2.,0.);
    vec3 plaser = mix(opos, dest, mod(iTime*2.+float(i)*.33, 1.)/1.);
    plaser.yz *= r2d(-.5);
   plaser.xz *= r2d(-a*.5);
    acc= _min(acc, vec2(_sph(plaser*vec3(1.,1.,.1),.1), 12.));
  }

  acc = _min(acc, vec2(_sph(p-vec3(0.,19.,8.), 15.5), 5.));
  return acc;
}
vec3 gradient(float f)
{
    vec3 cols[3];

    cols[0] = vec3(0.169,0.086,0.816);
    cols[1] = vec3(0.835,0.216,0.843);
    cols[2] = vec3(1.,1.,1.);

    float cnt = 2.;
    float cur = f*cnt;
    float curIdx = floor(cur);
    return mix(cols[int(curIdx)], cols[int(min(curIdx+1., cnt))], sat(fract(cur)));
}
vec3 accCol;
vec3 trace(vec3 ro, vec3 rd, int steps)
{
  vec3 p = ro;
  for (int i = 0; i<steps;++i)
  {
    vec2 res = map(p);
    if (res.x<0.01)
      return vec3(res.x,distance(p,ro),res.y);
      if (res.y > 10.)
          accCol += vec3(0.2,1.,0.2)*pow(1.-sat(res.x/0.2),5.);
      else if (res.y != 5.)
          accCol += (1.-sat(p.z*.045))*.1*gradient(sat(p.z*.05))*pow(1.-sat(res.x/2.7),5.);
    p+= rd*res.x;
  }
  return vec3(-1.);
}

vec3 getCam(vec3 rd, vec2 uv)
{
  float fov = .75;
  vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
  vec3 u = normalize(cross(rd,r));
  return normalize(rd+fov*(r*uv.x+u*uv.y));
}

vec3 getNorm(vec3 p, float d)
{
  vec2 e = vec2(0.01,0.);
  return normalize(vec3(d)-vec3(map(p-e.xyy).x,map(p-e.yxy).x,map(p-e.yyx).x));
}

void setup()
{
  ovnip[0]= vec4(-2.+cos(iTime*2.)*.2,sin(iTime*2.)*.25-.5,2.,.3);
  ovnip[1]= vec4(3.,sin(iTime*2.+1.)*.25,10.,-.3);
  ovnip[2]= vec4(0.,sin(iTime*2.+2.)*.25,20.,0.);
}

float _person(vec2 uv, float off)
{
    float shape = _sqr(uv, vec2(.03,.07));
    shape = min(shape, _cir(uv-vec2(0.,.1), .03));
    shape = min(shape, _sqr(uv-vec2(0.02,-.05), vec2(.01,0.05+0.05*(sin(iTime*16.+off)*.5+.5))));
    shape = min(shape, _sqr(uv-vec2(-0.02,-.05), vec2(.01,0.05+0.05*(sin((iTime)*16.+3.14+off)*.5+.5))));
    float th = 0.01;
    uv -= vec2(0.,-.03);
    uv *= r2d(-sign(uv.x)*.5);
    shape = min(shape, max(abs(uv.y-.05-sat(abs(uv.x*2.))*sin(abs(uv.x*50.)-iTime*5.+off)*.02)-th, abs(uv.x)-.2));
    return shape;
}

vec3 drawPeople(vec3 col, vec2 uv)
{
    float shapea = _person((uv*r2d(.25)*.5-vec2(0.2,-.1))*r2d(sin(iTime*2.+1.)*.1),0.);
    float shapeb = _person((uv*r2d(-.25)*1.5-vec2(-0.3,-.6))*r2d(sin(iTime)*.2),1.);

    float shape = min(shapea, shapeb);
    col = mix(col, vec3(0.), 1.-sat(shapea*400.));
    col = mix(col, col*.25, 1.-sat(shapeb*400.));
    return col;
}

float letter(vec2 uv, int idx, int idy)
{
    uv *= .25;
    float width = 1./16.;
    float height = 1./16.;
    float x = float(idx);
    float y = float(idy);
    vec2 topl = vec2(x*width, y*height);
    vec2 coords = uv+vec2(width, height)*.5;//+topl;
    coords = clamp(coords, vec2(0.), vec2(width, height));
    coords -= topl;
    return texture(iChannel1, coords).x;
}

vec3 drawText(vec2 uv)
{
    vec3 col = vec3(0.);
    uv -= vec2(-.5,.3);
    uv *= r2d(-.5);
    uv.y *= 1.-sat(abs(uv.x))+sin(uv.x*7.+iTime*2.)*.1;
    uv.x += .4;
    vec2 off = vec2(0.12, 0.);
    col = mix(col, vec3(1.), letter(uv*r2d(.1),-9,5));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv*r2d(-.2),-14,5));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv*r2d(.1),10,6));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv*r2d(-.1),-1,5));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv*r2d(-.3),-4,5));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv,-5,5));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv*r2d(-.1),14,6));
    uv -= off;
    col = mix(col, vec3(1.), letter(uv*.75*r2d(.25),6,6));

    return col;
}

vec3 rdr(vec2 uv)
{
  vec3 col = vec3(0.);

  vec2 skyp = uv-vec2(0.,-1.75);
  float a = atan(skyp.y,skyp.x)+iTime*.25;
  vec3 sk = mix(vec3(0.863,0.486,0.196), vec3(0.875,0.125,0.424), sat(length(.5*uv-vec2(0.,-.5))));
  col = mix(vec3(0.286,0.467,0.553), sk, pow(sat(texture(iChannel0, vec2(length(skyp)*.5,a*.1)*.5).x),.5));
  col *= 1.-sat(length(uv)-.5);
  col = pow(col, vec3(.75));
col += vec3(0.863,0.486,0.196)*(1.-sat(length(uv-vec2(0.,-.5))));

float offh = 1.2;
  vec3 ro = vec3(0.,1.5+offh,-7.);
  vec3 ta = vec3(0.,-.15+offh,0.);
  vec3 rd = normalize(ta-ro);

  rd = getCam(rd,uv);
  setup();
  accCol = vec3(0.);
  vec3 res = trace(ro,rd,128);
  if (res.y>0.)
  {
    vec3 p = ro+rd*res.y;
    vec3 n = getNorm(p,res.x);
    col = n*.5+.5;
    vec3 lpos = vec3(0.,5.,25.);
    vec3 ldir = lpos-p;
    vec3 h = normalize(rd+ldir);
    if (res.z < 3.5)
    {
        vec3 pr = p-ovnip[int(res.z)].xyz;
        col = vec3(0.349,0.235,0.447)*.25+vec3(0.722,0.667,0.875)*pow(texture(iChannel0, 3.*vec2(atan(pr.x, pr.z)*.1, 2.*length(pr))).x,2.);
        float ndoth = sat(dot(n,h));
       col *= sat(.5+ndoth);
       col += vec3(0.863,0.486,0.196)*ndoth;

    }   
    if (res.z == 5.)
    {
        float rep = .1;
            col = mix(col, vec3(1.),1.-sat(max(abs(uv.x)-0.08*pow(sat(-uv.y),2.),sin(uv.y*20.-iTime*10.))*400.));
    }
  }
  col = drawPeople(col, uv-vec2(0.,.1));
  col += accCol;
  vec3 bucol = col;
  col *= 1.-sat(drawText(uv*.95));
  col += bucol.zxy*drawText(uv);
  return col;
}

vec3 mixcol(vec3 col, vec2 uv)
{
    vec3 rgb = col;

    rgb.xy *= r2d(sin(iTime));
    //rgb.yz *= r2d(-iTime*.5);
    rgb = sat(rgb);

    return mix(col, rgb, sat(.5+.5*sin(uv.y*5.-iTime*3.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy-vec2(.5)*iResolution.xy) / iResolution.xx;
  uv *= length(uv+vec2(sin(iTime), cos(iTime))*.2)+.8;
  uv *= mix(1.,1.5,sat(sin(iTime*.5)*.5+.5));
  vec3 col = rdr(uv*2.);
  col = mixcol(col, uv);
  fragColor = vec4(col, 1.0);
}

// https://iquilezles.org/articles/distfunctions2d
// https://drewcassidy.me/2020/06/26/sdf-antialiasing/

float SDFCircle(vec2 p, float r) {
    return length(p) - r;
}
float SDFRect(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}
float Antialias(float d, float size) {
    float texel = 1.0 / iResolution.y;
    return mix(0.0, 1.0, smoothstep(0.0, texel * size, d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float d  = 0.0;

    if (uv.x < 0.5) {
        d = SDFRect(p + vec2(1., 0.), vec2(0.3, 0.5));
    } else {
        d = SDFCircle(p - vec2(1.0, 0.), 0.2);
    }

    // Output to screen
    if (iMouse.z > 0.0) {
        fragColor = mix(vec4(1.0), vec4(0), Antialias(d, 0.0));
    } else {
        fragColor = mix(vec4(1.0), vec4(0), Antialias(d, 4.0));
    }
