float sdf_zicon(vec2 uv)
{
  float th = 0.02*2.;
  float sz = 0.1;
  float acc = 1000.;

  acc = abs(cir(uv-vec2(.3), sz))-th;
  acc = min(acc,abs(cir(uv-vec2(-.3), sz))-th);
  acc = min(acc,abs(cir(uv-vec2(-.3,.3), sz*.7))-th);
  acc = min(acc,abs(cir(uv-vec2(.3,-.35), sz*1.3))-th);

  acc = min(acc, sqr(uv-vec2(-0.015,.3), vec2(.21,th)));
  acc = min(acc, sqr((uv-vec2(-0.0,.0))*r2d(-PI/4.), vec2(.32,th)));
  acc = min(acc, sqr((uv-vec2(-0.015,-.3))*r2d(0.1), vec2(.19,th)));

    return acc;
}

vec3 doBloom(vec2 uv, float blur, float threshold)
{
    vec3 col;
    int cnt = 50;
    float fcnt = float(cnt);
    for (int i = 0;i <cnt;++i)
    {
        float fi = float(i);
        float coef = (fi/fcnt);
        float sz = 1.+pow(coef,2.)*blur;
        float samplePerTurn = 5.;
        float an = (fi/(fcnt/samplePerTurn))*PI;
        vec2 p = uv - vec2(sin(an), cos(an))*(fi/fcnt)*blur;
        vec3 smple = texture(iChannel0, p).xyz;
        if (length(smple) > threshold)
            col += smple;
    }

    return col/float(cnt);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;

    vec3 col = texture(iChannel0, uv).xyz;

    float bloomIntensity = 624./640.;  
    vec3 bloomSample = doBloom(uv, 30./360., 217./ 640.);
    bloomSample = pow(bloomSample, vec3(0.5));

    col = col + (bloomSample*bloomIntensity);

    col = mix(col, col.zyx, pow(sat(length(cuv*2.)),4.));

    col = mix(col, 1.-col.yzx, 1.-sat(sdf_zicon((cuv-vec2(.45, -.225))*15.)*40.));

    col = pow(col, vec3(1.75));

    col *= sat(iTime-1.);
    fragColor = vec4(col.yxz, 1.);
}

