vec3 rdrWaterDroplets(vec2 ouv, vec2 cuv)
{
    vec3 col = texture(iChannel0, ouv).xyz;
    float mapA = -.5+(texture(iChannel1, cuv*.3+vec2(0., iTime*.005)).x*texture(iChannel1, cuv*.2).x);
    mapA = pow(mapA, .5);
    col = mapA*vec3(1.);

    vec2 off = vec2(dFdx(mapA), dFdy(mapA))*5.;

    col = mix(texture(iChannel0, ouv).xyz, texture(iChannel0, ouv-off).yzx, sat((length(cuv)-.25)*10.)*(1.-sat(length(off*2.))));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    vec3 col = rdrWaterDroplets(uv, cuv);

    { // Not so cheap antialiasing SSAA x4
        //col = vec3(1.,0.,0.); // debug
        vec2 off = vec2(1., -1.)/(iResolution.x*1.);
        vec3 acc = col;
        // To avoid too regular pattern yielding aliasing artifacts
        mat2 rot = r2d(uv.y*5.); // a bit of value tweaking, appears to be working well
        acc += rdrWaterDroplets(uv, cuv-off.xx);
        acc += rdrWaterDroplets(uv, cuv-off.xy);
        acc += rdrWaterDroplets(uv, cuv-off.yy);
        acc += rdrWaterDroplets(uv, cuv-off.yx);
        col = acc/5.;
    }
    //col += texture(iChannel0, uv).xyz;

    fragColor = vec4(col,1.0);
}

