// C.x --> x-position
// C.y --> y-position
// C[2] --> (unused)
// C[3] --> y-velocity

void mainImage( out vec4 C, in vec2 fragCoord )
{
    C = vec4(0.0,0.0,0.0,0.0);

    if (int(fragCoord.x) == 0 && int(fragCoord.y) < n_particles) {   

        if (iFrame == 0) {           
            // initial position
            C.x = (fragCoord.y / float(n_particles)) - 0.5;
            if (int(fragCoord.y)>0 && int(fragCoord.y) < n_particles-1){
                C.y = 0.4*sin(8.*3.14*C.x)*(C.x*C.x);
            }

        // after first frame - move
        } else {
            // exclude endpoints
            C = texelFetch(iChannel0, ivec2(fragCoord.xy), 0);
            if (int(fragCoord.y)>0 && int(fragCoord.y) < n_particles-1){
                if ( length(C.x+0.5*iResolution.x / iResolution.y-iMouse.z/iResolution.y)<(2.0/float(n_particles))){//(hash12(vec2(hash12(fragCoord+iTime),0.1231*iTime))>0.999){
                    // Random kicks
                    C[3] -=kick_strength*(hash12(fragCoord+iTime+0.312349*fragCoord*iTime)+(C[2]-iMouse.y/iResolution.y)); 
                }
                // Get neighbours
                vec4 C_left = texelFetch(iChannel0, ivec2(fragCoord.x,fragCoord.y-1.0), 0); 
                vec4 C_right = texelFetch(iChannel0, ivec2(fragCoord.x,fragCoord.y+1.0), 0); 
                // Attraction to neighbours
                C[3] -= stiffness*( C.y - (C_left.y+C_right.y)*0.5); 
                // Damping
                C[3] *= 1.0-damping;
                C.y += C[3];
            }
        }
    }
}

