const float PI = 3.14159;

float opUnion( float d1, float d2 ) { return min(d1,d2); }
float opIntersection( float d1, float d2 ) { return max(d1,d2); }
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }
float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}
float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

float sdSphere( vec3 p, float r ) {
    return length(p) - r;
}
float sdCircle( vec2 p, float r ) {
    return length(p) - r;
}

mat3 rotate(float t) {
    mat3 R = mat3(
        vec3(cos(t), 0, sin(t)),
        vec3(0, 1, 0),
        vec3(-sin(t), 0, cos(t))
    );
    return R;
}
vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
vec3 hash32(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 random_in_unit_sphere(vec2 p)
{
    vec3 rand = hash32(p);
    float phi = 2.0 * PI * rand.x;
    float cosTheta = 2.0 * rand.y - 1.0;
    float u = rand.z;

    float theta = acos(cosTheta);
    float r = pow(u, 1.0 / 3.0);

    float x = r * sin(theta) * cos(phi);
    float y = r * sin(theta) * sin(phi);
    float z = r * cos(theta);

    return vec3(x, y, z);
}
vec3 random_unit_vector(vec2 p) {
    return normalize(random_in_unit_sphere(p));
}
bool near_zero(vec3 p) {
    float s = 1e-8;
    return p.x < s && p.y < s && p.z < s;
}

float sdApple(vec2 coord, float ra) {
    float leaf1 = sdCircle(coord - vec2(-0.0161, 0.9658) * ra, 0.4447 * ra);
    float leaf2 = sdCircle(coord - vec2(0.4628, 0.5473) * ra, 0.4567 * ra);

    float shoulderLeft = sdCircle(coord - vec2(-0.3340, 0.0644) * ra, 0.4608 * ra);
    float shoulderRight = sdCircle(coord - vec2(0.3964, 0.0382) * ra, 0.4909 * ra);

    float bodyLeft = sdCircle(coord - vec2(-0.1227, -0.1831) * ra, 1.006 * ra);
    float bodyCenter = sdCircle(coord - vec2(0.0141, -0.4889) * ra, 0.9256 * ra);
    float bodyRight = sdCircle(coord - vec2(0.3662, -0.0483) * ra, 1.2052 * ra);
    float bodyShoulderLeft = sdCircle(coord - vec2(-0.1791, -0.0624) * ra, 0.6640 * ra);

    float buttLeft = sdCircle(coord - vec2(-0.2998, -0.7505) * ra, 0.2495 * ra);
    float buttRight = sdCircle(coord - vec2(0.3803, -0.7505) * ra, 0.2495 * ra);

    float bite = sdCircle(coord - vec2(1.0080, -0.0543) * ra, 0.4427 * ra);
    float bottom = sdCircle(coord - vec2(0.0382, -1.4145) * ra, 0.4950 * ra);

    float delimiterLeft = sdCircle(coord - vec2(-0.6398390342052314, 0.0201) * ra, 0.3320 * ra);

    // assemble!
    float leaf = opIntersection(leaf1, leaf2);
    float tinyPieceLeft = opIntersection(delimiterLeft, bodyShoulderLeft) + 0.002 * ra; // random constant
    float lowerPart = opIntersection(bodyLeft, opIntersection(bodyCenter, bodyRight));
    float body;
    body = opUnion(shoulderLeft, shoulderRight);
    body = opSmoothUnion(body, lowerPart, 0.121 * ra); // random constant
    body = opUnion(body, tinyPieceLeft);
    body = opUnion(body, leaf);
    body = opSubtraction(bite, body);
    body = opSmoothSubtraction(bottom, body, 0.189 * ra); // random constant

    return body;
}

float map(vec3 p, vec2 iMouse, float iTime) {
    vec3 shift = (p - vec3(0., 0.06, 0.01)) * rotate(iTime);

    float s1 = sdApple(shift.xy, 1./3.);
    s1 = opExtrussion(shift, s1, 0.066);
    // if inside of apple, flip it
    s1 = abs(s1);

    float s2 = sdSphere(p - vec3(0., -1.7, 0.), 1.43);    

    return opUnion(s1, s2);
}

vec3 calcNormal( in vec3 p, vec2 iMouse, float iTime) // for function f(p)
{
    const float eps = 1e-7; // or some other value
    const vec2 h = vec2(eps, 0);
    return normalize( vec3(map(p+h.xyy, iMouse, iTime) - map(p-h.xyy, iMouse, iTime),
                           map(p+h.yxy, iMouse, iTime) - map(p-h.yxy, iMouse, iTime),
                           map(p+h.yyx, iMouse, iTime) - map(p-h.yyx, iMouse, iTime) ) );
}

float maxT = 12.;
bool rayMarch(vec3 ro, vec3 rd, vec2 iMouse, float iTime, out float t) {
    t = 1e-4;
    for(int i = 0; i < 18; i++) {
        vec3 p = ro + t*rd;
        float h = map(p, iMouse, iTime);
        if( abs(h) < 1e-5) {
            return true;
        }

        if (t > maxT) {
            return false;
        }
        t += h;
    }
    return true;
}

vec3 skyCol = vec3(0.6, 0.7, .7);
vec3 appleCol = vec3(0.5, 0.5, .8);

vec3 rayColor(vec3 ro, vec3 rd, vec2 iMouse, float iTime, vec2 seed) {
    vec3 color = vec3(1);

    vec3 ro_ = ro;
    vec3 rd_ = rd;
    for (int i = 0; i < 5; i++) {
        float t;
        if (rayMarch(ro_, rd_, iMouse, iTime, t)) {
            vec3 pos = ro_ + rd_ * t;
            vec3 normal = calcNormal(pos, iMouse, iTime);            

            vec3 scatter_direction = normal + random_unit_vector(seed + float(i));
            // catch degenerate scatter direction
            if (near_zero(scatter_direction)) {
                scatter_direction = normal;
            }
            ro_ = pos;
            rd_ = scatter_direction;

            color *= appleCol;
        } else {
            vec3 unit_direction = normalize(rd);
            float t = 0.5 * (unit_direction.y + 1.0);
            color *= mix(appleCol, skyCol, t);
            return color;
        }
    }

    return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (ivec2(fragCoord) == ivec2(0)) {
        fragColor = iResolution.xyxy;
    } else {
        vec3 ro = vec3(0., 0., 0.28);

        vec2 rand = hash22(fragCoord + iTime * 999.);
        vec2 coord = fragCoord + rand;
        coord = (2. * fragCoord - iResolution.xy) / iResolution.y;

        vec3 rd = normalize(vec3(coord, 0) - ro);
        vec3 col = rayColor(ro, rd, iMouse.xy, iTime, fragCoord + rand);

        if (texelFetch(iChannel0, ivec2(0), 0).xy == iResolution.xy) {        
            fragColor = vec4(col,1) * .1 + texelFetch(iChannel0, ivec2(fragCoord), 0) * .9;
        } else {        
            fragColor = vec4(col,1);
        }
    }
}

