void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 mouse = (2. * iMouse.xy - iResolution.xy) / iResolution.y;

    float body = sdApple(coord, .5);

    vec3 col = vec3(1.0) - sign(body) * vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-3.0 * abs(body));
    col *= 0.8 + 0.2 * cos(150.0 * body);
    col = mix(col, vec3(1.0), 1.0 - smoothstep(0.0,0.01, abs(body)));

    if(iMouse.z > 0.001) {
        vec2 m = mouse; vec2 p = coord;
        float d = sdApple(m, .5);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

    fragColor = vec4(col, 1.0);
}

/*
The circles of the logo are manually approximated in a Figma file:
https://www.figma.com/file/ywGtT2aMhG04cuvz2MKF0d/Apple-soft-3D-Community?node-id=0%3A1
Then converted from Figma's coordinate system, where:
- y increases downward
- sizes aren't normalized to 0 - 1
- origin of the circles are on the upper left
- circles are specified using width instead of radius

See convertCoords below
*/

float sdCircle( vec2 p, float r ) { return length(p) - r; }

float opUnion( float d1, float d2 ) { return min(d1,d2); }

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float opIntersection( float d1, float d2 ) { return max(d1,d2); }

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

float opExtrussion( in vec3 p, in float sdf, in float h ) {
    vec2 w = vec2( sdf, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float ratio = 3./3.;
float width = 419.;
float height = 497.;

float convertCoords(vec2 coord, vec3 xyr) {
    float r = xyr.z / 2.;
    vec2 absolute = vec2(xyr.x - width / 2., height / 2. - xyr.y) + vec2(r, -r);
    return sdCircle(coord - absolute * ratio / height * 2., r * ratio / height * 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float leaf1 = convertCoords(coord, vec3(95, -102, 221));
    float leaf2 = convertCoords(coord, vec3(211, -1, 227));

    float shoulderLeft = convertCoords(coord, vec3(12, 118, 229));
    float shoulderRight = convertCoords(coord, vec3(186, 117, 244));

    float bodyLeft = convertCoords(coord, vec3(-71, 44, 500));
    float bodyCenter = convertCoords(coord, vec3(-17, 140, 460));
    float bodyRight = convertCoords(coord, vec3(1, -39, 599));
    float bodyShoulderLeft = convertCoords(coord, vec3(0, 99, 330));

    float bite = convertCoords(coord, vec3(350, 152, 220));
    float bottom = convertCoords(coord, vec3(96, 477, 246));

    float delimiterLeft = convertCoords(coord, vec3(-32, 161, 165));


    float leaf = opIntersection(leaf1, leaf2);
    float tinyPieceLeft = opIntersection(delimiterLeft, bodyShoulderLeft) + 0.994 * ratio / height; // random constant
    float lowerPart = opIntersection(bodyLeft, opIntersection(bodyCenter, bodyRight));

    float body;
    body = opUnion(shoulderLeft, shoulderRight);
    body = opSmoothUnion(body, lowerPart, 60. * ratio / height); // random constant
    body = opUnion(body, tinyPieceLeft);
    body = opUnion(body, leaf);
    body = opSubtraction(bite, body);
    body = opSmoothSubtraction(bottom, body, 94. * ratio / height); // random constant


    vec3 color = vec3(1.0) - sign(body) * vec3(0.1,0.4,0.7);
    color *= 1.0 - exp(-3.0 * abs(body));
    color *= 0.8 + 0.2 * cos(150.0 * body);
    color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0,0.01, abs(body)));

    float line = 0.002;
    if (iMouse.z > 0.001 &&
        (abs(leaf1) < line
        || abs(leaf2) < line
        || abs(bite) < line
        || abs(shoulderLeft) < line
        || abs(shoulderRight) < line
        || abs(bottom) < line
        || abs(bodyRight) < line
        || abs(bodyLeft) < line
        || abs(bodyShoulderLeft) < line
        || abs(bodyCenter) < line
        || abs(delimiterLeft) < line)
    ) {
        color = vec3(1, 1, 0);
    }

    fragColor = vec4(color, 1.0);
}

