// rubix 2021-07-13
// based on https://twitter.com/emollick/status/1414436947733499905 (Takahashi's iPerception, 2017)

#define TAU 6.28318530718

float background(vec2 p) {
    float b = p.x-p.y;

    //if (b < -0.1) return 1.;
    //if (b < 0.6) return 0.5;
    //return 0.;

    float t = fwidth(b);
    return 0.5 * smoothstep(0.6+t, 0.6-t, b)
         + 0.5 * smoothstep(-0.1+t, -0.1-t, b);
}

float color(vec2 p) {
    float d = 250.*p.y + 1.*sin(100.*p.x);
    int z = int(d+1.) % 24;

    //if (z == 0 || z == 3)
    //    return sin(100.*p.x) < 0. ? 0.3 : 0.7;
    //if (z == 12 || z == 15)
    //    return sin(100.*p.x - TAU/4.) < 0. ? 0.3 : 0.7;
    //return background(p);

    float t = fwidth(p.x);
    float x = fwidth(2.*d);
    float wave = smoothstep((5.-x)/6., (5.+x)/6., abs(fract(d/3.)*3.-2.));
    float c = background(p);
    if (z < 6)
        c = mix(c, mix(0.3, 0.7, smoothstep(-t, t, sin(100.*p.x))), wave);
    if (z > 11 && z < 18)
        c = mix(c, mix(0.3, 0.7, smoothstep(-t, t, sin(100.*p.x - TAU/4.))), wave);;
    return c;
}

