/**/

// License CC0: Sommarhack 2021 as a distance field
//  Inspired by the Sommarhack 2021 I wanted to recreate the logo as a distance field
//  to enable some cool effects later
//  I took some liberties with the logo so it's not an exact replica
//  The distance field isn't perfect but doesn't look too awful

#define PI              3.141592654
#define TAU             (2.0*PI)
#define TIME            iTime
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))
#define DOT2(x)         dot(x, x)

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, vec3(1.0/2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

vec3 alphaBlend(vec3 back, vec4 front) {
  return mix(back, front.xyz, front.w);
}

// Lots of distance fields from: https://iquilezles.org/articles/distfunctions2d

float circle(vec2 p, float r) {
  return length(p) - r;
}

float vesica(vec2 p, vec2 sz) {
  if (sz.x < sz.y) {
    sz = sz.yx;
  } else {
    p  = p.yx; 
  }
  vec2 sz2 = sz*sz;
  float d  = (sz2.x-sz2.y)/(2.0*sz.y);
  float r  = sqrt(sz2.x+d*d);
  float b  = sz.x;
  p = abs(p);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

float moon(vec2 p, float d, float ra, float rb ) {
    p.y = abs(p.y);
    float a = (ra*ra - rb*rb + d*d)/(2.0*d);
    float b = sqrt(max(ra*ra-a*a,0.0));
    if( d*(p.x*b-p.y*a) > d*d*max(b-p.y,0.0) )
          return length(p-vec2(a,b));
    return max( (length(p          )-ra),
               -(length(p-vec2(d,0))-rb));
}

// Imprecise faster version
float fastMoon(vec2 p, float d, float ra, float rb) {
  float d0 = length(p) - ra;
  float d1 = length(p-vec2(d, 0.0)) - rb;
  return max(d0, -d1);
}

float roundedCross( in vec2 p, in float h )
{
    float k = 0.5*(h+1.0/h); // k should be const at modeling time
    p = abs(p);
    return ( p.x<1.0 && p.y<p.x*(k-h)+h ) ? 
             k-sqrt(DOT2(p-vec2(1,k)))  :
           sqrt(min(DOT2(p-vec2(0,h)),
                    DOT2(p-vec2(1,0))));
}

float box(vec2 p, vec2 b) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float plane(vec2 p, vec3 pp) {
  return dot(p, pp.xy) + pp.z;
}

float summerhack(vec2 p) {
  // As the moon shapes are mostly used for cut-outs I think there is less need for exact
  //  moon distance and therefore rely on fastMoon instead
  vec2 op = p;
  p.x = abs(p.x);
  vec2 p0 = p;
  p0 -= vec2(0.0, 0.385);
  float d0 = circle(p0, 0.605);
  vec2 p1 = p;
  p1 -= vec2(0.0, -0.375);  
  float d1 = vesica(p1, vec2(0.04, 0.569))-(mix(0.005, 0.035, smoothstep(0.1, 0.2, p1.y)));

  vec2 p2 = p;
  p2 -= vec2(0.0, 0.4);
  p2 = -p2.yx;
  float d2 = fastMoon(p2, 0.075, 0.33, 0.275);

  vec2 p3 = p;
  p3 -= vec2(0.0, 0.65);
  float d3 = circle(p3, 0.367);

  vec2 p4 = p;
  p4 -= vec2(0.0, 0.43);
  float d4 = circle(p4, 0.29);

  vec2 p5 = p;
  p5 -= vec2(-0.185, 0.12);
  float d5 = circle(p5, 0.30);

  vec2 p6 = p;
  p6 -= vec2(0.12, -0.19);
  p6 *= ROT(0.65);
  float d6 = vesica(p6, vec2(0.15, 0.024))-0.0175;

  vec2 p7 = p;
  p7 -= vec2(0.0, 0.735);
  p7 = -p7.yx;
  float d7 = fastMoon(p7, 0.13, 0.68, 0.595);

  vec2 p8 = p;
  p8 -= vec2(0.0, 0.7);
  p8 = -p8.yx;
  float d8 = fastMoon(p8, 0.1, 0.477, 0.4676);

  vec2 p9 = p;
  p9 -= vec2(0.25, 0.72);
  p9.x = -p9.x;
  float d9 = fastMoon(p9, 0.188, 0.73, 0.775);

  vec2 p10 = op;
  p10 -= vec2(0.0, 0.28);
  p10 = p10.yx;
  p10.x *= sign(op.x);
  p10.x += (-sign(op.x)+1.0)*-0.0775;
  float d10 = moon(p10, 0.045, 0.105, 0.095);

  vec2 p11 = p;
  p11 -= vec2(0.0, -0.78);
  p11 = p11.yx;
  float d11 = roundedCross(p11, 0.55);

  vec2 p12 = p;
  float d12 = plane(p12, vec3(normalize(vec2(-4.0, 1.0)), 0.315));

  vec2 p13 = p;
  p13 -= vec2(-0.05, -0.805);
  float d13 = circle(p13, 0.175);

  vec2 p14 = p;
  p14 -= vec2(0.0, -0.88);
  float d14 = p14.y;

  vec2 p15 = p;
  p15 -= vec2(0.45, -0.4);
  p15 = p15.yx;
  float d15 = fastMoon(p15, 0.14, 0.4, 0.4);

  vec2 p16 = op;
  p16 -= vec2(-0.095, 0.323);
  // Cheat to remove discontinuity in distance field due to hacking on d10
  float d16 = length(p16); 

  d11 = max(d11, -d12);
  d11 = max(d11, -d13);
  d11 = max(d11, -d14);
  d11 = max(d11, -d15);

  float dn = d3;
  dn = min(dn, d4);
  dn = min(dn, d2);
  dn = min(dn, d5);
  dn = min(dn, d6);
  dn = min(dn, d7);
  dn = min(dn, d8);
  dn = min(dn, d9);

  float d = d0;
  d = max(d, -dn);
  d = min(d, d1);
  d = min(d, d10);
  d = min(d, d11);
  d = min(d, d16);


  return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q    = fragCoord/RESOLUTION.xy;
  vec2 p    = -1. + 2. * q;
  p.x       *= RESOLUTION.x/RESOLUTION.y;
  float aa  = 2.0/RESOLUTION.y;

  float d = summerhack(p);  
  float ds = summerhack(p-0.033*vec2(1.0, -1.0));

  float od = abs(d-0.015)-0.0033;
  d = min(d, od);

  vec3 col  = vec3(0.1, 0.1, 0.2);
  vec4 scol = vec4(col*0.5, smoothstep(-aa, aa, -ds)); 
  vec4 dcol = vec4(mix(vec3(1.0, 0.5, 0.5), vec3(1.0), q.y*q.y), smoothstep(-aa, aa, -d)); 

  col = alphaBlend(col, scol);
  col = alphaBlend(col, dcol);
  /* View distance field
  if (p.x > 0.0)
    col += 0.5*sin(300.0*d);
  */
  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

//MIT License
//Copyright 2021 Mykhailo Moroz

vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb); 	
	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 C, in vec2 P )
{
    R = iResolution.xy; time = iTime; Mouse = iMouse;
    P = floor(P);

    if(iMouse.z < 0.)
    {
        //P = iMouse.xy + 100.*(P/R.x - 0.5); 
    }

    float m = 0.0;
    float rho = 0.0;
    vec2 v = vec2(0.0);
    range(i, -2, 2) range(j, -2, 2)
    {
        //load data
        vec2 di = vec2(i,j);
        vec4 data0 = A(P + di);
        vec4 data1 = B(P + di);

        //unpack data
        float m0 = data0.y;
        vec2 x0 = 2.0*decode(data0.x) - fract(P);
        vec2 v0 = decode(data1.x);
        mat2 B0 = mat2(decode(data1.y),decode(data1.z)); //velocity gradient
        //update particle position

        //find cell contribution
        vec3 o = overlap(2.0*x0 + di + v0*dt , vec2(0.1));
            x0 = x0 + di; 

        m += m0*o.z;

        float w = k1(1.0*x0);

        v += (v0 + 4.0*B0*x0)*w;
        rho += m0*w;
    }
   // rho = step(0.1, rho)*rho; //isosurface
    float arg = 0.5*(atan(v.y, v.x)/PI + 1.0);
    float d = 2.5*length(v);
    vec3 fluid = hsv2rgb(vec3(arg, 0.5, 0.3*d +0.*rho));

    C = vec4(mix( vec3(1.000,1.000,1.000),fluid, smoothstep(0.,0.5,sqrt(rho))), 1.0);
}

