vec3 tonemap(vec3 color)
{
    // Clamp Negatives
    color = max(color, 0.0);

    // Exposure Tonemap
    color = 1.0-exp(-color*exposure);

    // Return the Tonemapped Color
    return clamp(color, 0.0, 1.0);
