// ##### Rendering #####

// Distance Estimator
float DE(vec3 pos)
{
    // Transform the SDF (and dither the position to remove weird artifacts)
    pos = (vec3(-0.2, 0.2, 1.2)+nrand3(hitDist, pos)).xzy;

    // Soft Edges
    pos = nrand3(0.0003, pos);

    // Set-Up Variables
    vec3 z = pos;
    float dr = 1.0;
    float r = 0.0;

    // Iterate Mandelbulb
    for(uint i = 0U; i < 32U; i++)
    {
        r = length(z);

        // Bailout
        if(r > 4.0)
        {
            break;
        }

        // Iterate the Mandelbulb
        float theta = acos(z.z/r)*8.0;
        float phi = atan(z.y, z.x)*8.0;
        dr = r*r*r*r*r*r*r*8.0*dr+1.0;
        z = r*r*r*r*r*r*r*r*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
        z += pos;
    }

    // Return the Distance Estimate
    return 0.5*log(r)*r/dr;
}

// Tetrahedron Numerical SDF Normals
vec3 sampleNormal(vec3 pos)
{
    const vec2 k = vec2(-1.0, 1.0);
    return normalize(
    k.xxx*DE(pos+k.xxx*hitDist)+
    k.xyy*DE(pos+k.xyy*hitDist)+
    k.yxy*DE(pos+k.yxy*hitDist)+
    k.yyx*DE(pos+k.yyx*hitDist));
}

// "Light" Transport (or would it be Electron Transport?)
vec3 radiance(vec3 ro, vec3 rd)
{
    // Set-Up Variables
    float t = 0.0;

    // Ray-Marching
    for(uint i = 0U; i < maxSteps; i++)
    {
        // Check if the Ray reached the background
        if(t > maxDist)
        {
            return vec3(0.0);
        }

        // Compute the Distance Estimate
        float td = DE(ro+rd*t);

        // Check if the Ray hit the scene
        if(td < hitDist)
        {
            // Set-Up Variables
            //vec3 normal = sampleNormal(ro+rd*t);
            ro = ro+rd*t;
